content:
[[module CSS]]
:root {
  --new-side-bar-color: #000;
}
@media (max-width: 770px) {
 #header h1, #header h2
{
    margin-left: 100px;
  }
}
.collapsible-side .collapsible-block-unfolded-link a {
  color: white !important;
}
.collapsible-side .collapsible-block-folded:hover .collapsible-block-link {
  color: white !important;
}
.random-article-block .menu-item:last-child:hover {
  background: #000 !important;
}
.random-article-block .menu-item:first-child:hover {
  background: #000 !important;
}


body {
    color: #000;
    font-family: 'Montserrat Alternates', sans-serif;
}

h1, #page-title {
    color: #000;
}

#side-bar .side-block {
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.5);
}
#u-become-member {
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.5);
}

#header h1 a {
    color: #fff;
}
#header h1 a::before {
    color: #e9ebf0;
}
#header h2 span::before {
    color: #f2f2fd;
}
#header h2 span {
    text-shadow: 3px 1px 2px rgba(0,0,0,.8);
    color: #ffffff;
}

#login-status {
    color: #000;
}
#login-status a {
    color: #000;
}

a {
    color: #0c0043;
    text-decoration: none;
    background: transparent;
}
a.newpage {
    color: #051b73;
    text-decoration: none;
    background: transparent;
}
a:visited {
    color: #0c0043;
}



#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background-color: #e3e3e3;
    color: #505050;
}
#top-bar ul li ul {
    box-shadow: 2px 2px 6px rgba(0, 0, 0, 0.5);
}

#top-bar ul li ul a, #top-bar a:hover {
    color: #505050;
}

#search-top-box-input {
    color: #FFF;
    background-color: #000;
}
#search-top-box-form input[type=submit] {
    color: #fff;
    background-color: #000;
    background: linear-gradient(to bottom, #000,#000,#000);
}



.page-rate-widget-box .rate-points {
background-color: #616163 !important;
    border: solid 1px #616163;
}
.page-rate-widget-box .cancel {
    background-color: #616163;
    border: solid 1px #616163;
}
.page-rate-widget-box .cancel a {
    color: #fff;
}
.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
    background: #616163;
}
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
    background-color: #e8dcd4;
    border-top: solid 1px #616163;
    border-bottom: solid 1px #616163;
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
    color: #000;
}

.page-rate-widget-box .cancel a:hover {
    background: #464646;
}


#side-bar a:visited {
    color: #000;
}
#side-bar a{
    color: #000;
}
#side-bar div.menu-item img {
    filter: hue-rotate(0deg) brightness(0%);
}
.side-flag-icons img {
    filter: none !important;
}
#side-bar .side-block {
    border: 1px solid #000000;
    background: #fff !important;
}
.side-block{
background-color: #ffffff;
}
#side-bar .heading {
    color: #000;
}

#header h1 a {
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: 'Verdana', Arial, sans-serif;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0.9px;
}

#header h1 a::before {
    content: "DIRA";
    color: #000;
    letter-spacing: -0.02em;
}

#header h2 span {
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: '内部分析与研究部门';
    color: #000;
}

#header {
    background: url('https://files.scpfoundation.net/local--files/theme:dira-theme/DIRA_logo.png') 0px 40px no-repeat;
    background-size: 90px;
}
div#container-wrap {
    background: url('https://files.scpfoundation.net/local--files/theme:dira-theme/DIRA_back.png') top left repeat-x;
}

