/*
    Disrememberment Theme
    [2020 Wikidot Theme]
    Created by Rounderhouse.
    Based on Black Highlighter Theme created by Woedenaz and Croquembouche.
    Ennui Logo made by Ayers, CC BY SA 3.0.
*/
/* Fonts */

@import url('https://fonts.googleapis.com/css?family=PT+Mono|Poppins:600,800');

/* All Vars Used */

:root {
    /* S-CSS-P Integration */
    /* If you're making a new CSS theme, please include the following three variables at minimum. */
    --theme-base: "nuscp";
    /* must be either "nuscp" or "sigma9" */
    --theme-id: "disrememberment";
    /* set this to the URL of your theme's page - eg for "component:ar-theme", set it to "ar-theme" */
    --theme-name: "Disrememberment Theme";
    /* set this to your theme's full name */

    /* Header */
    --logo-image: url("https://scp-jp-storage.wdfiles.com/local--files/file%3A8260379-41-0h3y/o2.png");
    --header-title: "O5プロトコル: ENNUI";
    --header-subtitle: "善意の嘘と悪意ある心";

    /* Typefaces */
    --header-font: 'Poppins', sans-serif;
    --title-font: 'Poppins', sans-serif;
    --mono-font: "PT Mono", "Andale Mono", "Courier New", Courier, monospace;

    /* Standard Colors */
    --white-monochrome: 252, 252, 252;
    /* white */
    --pale-gray-monochrome: 244, 244, 244;
    /* v light gray for blockquotes and stuff */
    --light-gray-monochrome: 120, 120, 120;
    /* light accent gray for login status */
    --gray-monochrome: 66, 66, 72;
    /* gray */
    --dark-gray-monochrome: 48, 48, 52;
    /* dark accent gray for sidebar background */
    --black-monochrome: 12, 12, 12;
    /* black */
    --bright-accent: 90, 90, 90;
    /* blue */
    --medium-accent: 103, 0, 0;
    /* medium blue */
    --dark-accent: 103, 0, 0;
    /* dark blue */
    --alt-accent: 99, 190, 255;
    /* bright blue */

    --background-gradient-distance: 30rem;

    /* Header Gradients */

    --gradient-header: linear-gradient(to bottom, rgba(var(--black-monochrome), 0.97) 0%, rgb(var(--dark-gray-monochrome)) 100%);
    --gradient-topmenu: linear-gradient(to bottom,
     white 0%,
     white 7.5rem,
     rgba(var(--swatch-topmenu-border-color), 1) 7.5rem,
     rgba(var(--swatch-topmenu-bg-color), 1) 7.625rem,
     rgba(var(--swatch-topmenu-bg-color), 0.95) calc(100% - 0.125rem),
     rgba(var(--swatch-topmenu-bg-color), 1) calc(100% - 0.125rem),
     rgba(var(--swatch-topmenu-border-color), 1) 100%);
    --gradient-topmenu-mobile: linear-gradient(to bottom,
     rgba(var(--swatch-topmenu-border-color), 1) 0,
     rgba(var(--swatch-topmenu-bg-color), 1) 0.125rem,
     rgba(var(--swatch-topmenu-bg-color), 1) calc(3rem - 0.125rem),
     rgba(var(--swatch-topmenu-bg-color), 1) calc(3rem - 0.125rem),
     rgba(var(--swatch-topmenu-border-color), 1) 3rem);
    --gradient-sidemenu-header: linear-gradient(40deg,
     rgba(var(--medium-accent), 0.45) 0%,
     rgba(var(--medium-accent), 0.55) 100%);
    --gradient-background: linear-gradient(to bottom,
     rgba(var(--background-gradient-color), 1) calc(calc(0 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0.987) calc(calc(0.8 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0.951) calc(calc(2.9 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0.896) calc(calc(6.4 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0.825) calc(calc(11 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0.741) calc(calc(16.6 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0.648) calc(calc(23.1 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0.55) calc(calc(30.4 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0.45) calc(calc(38.3 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0.352) calc(calc(46.7 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0.259) calc(calc(55.4 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0.175) calc(calc(64.4 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0.104) calc(calc(73.5 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0.049) calc(calc(82.5 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0.013) calc(calc(91.4 / 100) * var(--background-gradient-distance)),
     rgba(var(--background-gradient-color), 0) calc(calc(100 / 100) * var(--background-gradient-distance)));
}

/*  ===============================
    ======= SPECIFIC COLORS =======
    ===============================
/* Colors for Buttons, Tabs, Tables, Footnotes Hover/Modal, Etc. */

:root {
    /* ===BACKGROUND GRADIENT=== */

    /* ===GENERAL BACKGROUND=== */
    /* Gradient Color used for the --gradient-background var */
    --background-gradient-color: var(--bright-accent);

    /* ===TOPBAR GRADIENT=== */
    /* Colors used in the --gradient-topmenu var to give the topbar its color */
    --swatch-topmenu-border-color: var(--swatch-menubg-medium-dark-color);

    /* ===LINK COLORS=== */
    --link-color: var(--swatch-primary-darkest);
    --visited-link-color: var(--swatch-primary-darkest);
    --hover-link-color: var(--swatch-primary-darker);
}

#header {
    /* Focus is when the search box has been clicked on */
    --search-icon-focus-bg-color: var(--swatch-primary-darker);
    --search-focus-outline-color: var(--swatch-primary-darker);
}

#top-bar {
    /* ===TOPBAR ELEMENTS=== */
    --topmenu-hover-border-color: var(--swatch-primary-darker);

    /* ===DROPDOWN MENU=== */
    --dropdown-border-color: var(--swatch-text-secondary-color), 0.5;
}

#main-content {
    /* ===MAIN CONTENT UI ELEMENTS */
    /* ===UI ICONS=== */
    /* ===PAGE OPTIONS ICONS=== */
    --ui-icon-bg: var(--gray-monochrome);
    --ui-icon-hover-bg: var(--dark-gray-monochrome);

    /* ===TABS=== */
    --tabs-bg: var(--swatch-alternate-color);
    --tabs-txt: var(--swatch-text-secondary-color);
    --tabs-hover-bg: var(--swatch-primary);
    --tabs-selected-bg: var(--swatch-primary-darker);
    --tabs-selected-outline: var(--swatch-primary-darker);

    --tabs-content-bg-color: var(--swatch-menubg-dark-color), 0.25;

    /* ===TABLES=== */
    --tables-header-bg: var(--swatch-menubg-dark-color);

    /* ===BLOCKQUOTES=== */
    --blockquote-bg-color: var(--swatch-menubg-dark-color), 0.25;

    /* ===FOOTNOTES/BIBLIOGRAPHY FOOTER=== */
    --footnotes-footer-bg-color: var(--swatch-menubg-light-color);
    --footnotes-footer-colorbar-color: var(--gray-monochrome);
    --footnotes-footer-title-bg-color: var(--gray-monochrome);

    /* ===PAGE TAGS=== */
    --pagetags-title-bg: var(--gray-monochrome);
    --pagetags-text-color: var(--swatch-primary-darker);
    --pagetags-text-hover-color: var(--swatch-primary-darker);
    --pagetags-text-hover-bg-color: var(--swatch-background);
}

/* ===GLOBAL ELEMENTS=== */
:root {
    /* ===UI BUTTONS=== */
    --ui-button-bg: var(--gray-monochrome);
    --ui-button-txt: var(--swatch-text-secondary-color);
    --ui-button-hover-txt: var(--swatch-text-secondary-color);
    --ui-button-hover-outline: var(--dark-gray-monochrome);

    /* ===MODALS=== */
    --modal-bg: var(--swatch-alternate-color);
    --modal-body-text: var(--swatch-text-secondary-color);

.creditRate.creditModule~[id*=u-credit] .credit-license a.cc {
    color: rgb(var(--swatch-text-light));
}
    /* h1/h2/h3/h4/h5/h6 */
    --modal-body-header-txt: var(--swatch-text-secondary-color);

    --modal-header-stripe: var(--swatch-primary), 0.25;

    /* ===FOOTNOTES HOVER BLOCK=== */
    --hoverblock-bg: var(--swatch-alternate-color);
    --hoverblock-txt: var(--swatch-text-secondary-color);
    --hoverblock-footer-bg: var(--swatch-secondary-color);
    --hoverblock-footer-txt: var(--swatch-text-secondary-color);
}

.scp-image-block {
    color: rgb(var(--white-monochrome));
}

#login-status {
    color: rgb(var(--light-gray-monochrome));
}

#side-bar .heading p,
#interwiki .heading p,
#side-bar .side-block>.collapsible-block .collapsible-block-link {
    padding: 0.2em;
    background-color: rgb(var(--light-gray-monochrome));
    color: rgb(var(--black-monochrome));
}

blockquote {
    background-color: rgba(rgb(var(--black-monochrome)), 0.05);
}

#page-content .collapsible-block-link {
    color: rgb(var(--black-monochrome));
}

#page-title::before,
.meta-title::before {
    content: "";
    flex-grow: 1;
    height: 0.0625rem;
    background: rgb(var(--swatch-primary));
}

#page-title::before,
.meta-title::before {
    margin: auto 1.25rem auto auto;
}

.code {
    border-style: dashed;
    background-color: rgba(var(--bright-accent), .75);
}

