  .open-menu-btn a {
    display: block;
    bottom: 0.5rem;
    left: 0.5rem;
    background-color: rgba(var(--dark-accent),.25) !important;
    border: 4px solid rgb(var(--bright-accent)) !important;
    color: rgb(var(--bright-accent)) !important;
    position: fixed;
    text-align: center;
    width: 30px;
    height: 25px;
    font-weight: bolder;
    font-size: 20px !important;
