:root:lang(ko) {
    --header-txt-color: 240, 240, 240;
    --logo-image: url('http://scpko.wdfiles.com/local--files/department-of-analytical-psychology/DoAP-blackline.png');
    --theme-color: 168, 42, 67;
    --theme-color-dark: 107, 16, 36;
    --bg-color: 252, 249, 250;
    --header-title: "분석심리학부";
    --header-subtitle: "사고, 감정, 감각, 직관";
    --title-outline:block;
    --clrd: 1;
    --suncross: url("data:image/svg+xml,%3C%3Fxml version='1.0' encoding='utf-8'%3F%3E%3Csvg xmlns='http://www.w3.org/2000/svg' fill='rgb(107,16,36)' viewBox='0 0 100 100'%3E%3Cpath d='M929.21,506a50,50,0,1,0,50,50A50,50,0,0,0,929.21,506Zm33,42h-25v-25A34.12,34.12,0,0,1,962.25,548Zm-41-25v25h-25A34.11,34.11,0,0,1,921.21,522.93Zm-25,41h25v25A34.08,34.08,0,0,1,896.17,564Zm41,25V564h25A34.11,34.11,0,0,1,937.21,589Z' transform='translate(-879.21 -505.97)'/%3E%3C/svg%3E");
}
div#header-extra-div-1 {
    opacity: 0.8;
}
div#header-extra-div-2 {
    background: linear-gradient(rgba(0,0,0,0.2),rgba(0,0,0,0.3)), repeating-linear-gradient(45deg, hsla(0,0%,100%,0), hsla(0,0%,100%,0) 0.5vh, rgba(88,88,88,.1) 0.7vh, rgba(88,88,88,.2) 1vh), url(http://scpko.wdfiles.com/local--files/theme%3Adoap-bhl/rdbk_black.png), linear-gradient(171deg, rgb(168, 42, 67) 0%, rgb(18, 0, 5) 100%), linear-gradient(45deg, black, transparent);
    box-shadow: 0 0 200px rgba(var(--theme-color),0.15);
}
div#header-extra-div-3 {
    background: rgb(50 50 50);
    outline: solid 1.5px rgb(20 20 20);
}
div#extra-div-4 {
    background: url(http://scpko.wdfiles.com/local--files/theme%3Adoap-bhl/rdbk.png);
    opacity: 0.2;
}
div#extra-div-5 {
    background: linear-gradient(transparent 0%,rgb(var(--bg-color)) 15%, rgb(var(--bg-color)) 85%, transparent 100%);
}
.img {
    --img: url(http://scpko.wdfiles.com/local--files/department-of-analytical-psychology/DoAP_Black.png);
}

#page-content>hr {
    border: none;
    background-image: var(--suncross);
    background-repeat: no-repeat;
    background-position: center;
    width: 100%;
    height: 25px;
    margin: 2em 0;
    display: flex;
}
#page-content>hr:before {
    content: "";
    background: linear-gradient(90deg, transparent, rgb(var(--theme-color)));
    display: block;
    height: 3px;
    width: 45%;
    margin: 11px auto 11px 1em;
    z-index: -1;
}
#page-content>hr:after {
    content:"";
    background: linear-gradient(90deg, rgb(var(--theme-color)), transparent);
    display: block;
    height: 3px;
    width: 45%;
    margin: 11px 1em 11px auto;
    z-index: -1;
}

.redbook {
    background: linear-gradient(rgba(255,255,255,.78), white 100px, white calc(100% - 100px), rgba(255,255,255,.78)), url(http://scpko.wdfiles.com/local--files/theme%3Adoap-bhl/rdbk.png) repeat 30% top;
  border: solid 2px #000; 
  padding: 5px 15px; 
  box-shadow: 2px 3px 5px rgb(0 0 0 / 30%);
  margin: 2em 1em;
}

/* Ꚛ Vocatus atque non vocatus deus aderit Ꚛ */
.suncross {
    line-height: 1.4;
    font-family: var(--title-font);
    font-size: 1.5375em;
    font-weight: 700;
    margin: 0 0 .5em;
    background: linear-gradient(transparent 60%, rgba(var(--theme-color),.1) 60%, rgba(var(--theme-color),.1) 90%, transparent 90%, transparent 100%);
    display: inline;
}
.suncross:before {
    content: "";
    background-color: rgb(var(--theme-color-dark));
    mask: var(--suncross);
    -webkit-mask: var(--suncross);
    mask-repeat: no-repeat;
    -webkit-mask-repeat: no-repeat;
    background-size: 1em 1em;
    width: 1em;
    height: 0.85em;
    display: inline-block;
}

.mandala-box {
  background: linear-gradient(white, #ffffffde 100px, #ffffffde calc(100% - 100px), white), url(http://scpko.wdfiles.com/local--files/theme%3Adoap-bhl/mandala.png) center no-repeat;
  background-size: 110%;
  border: solid 2px #000; 
  padding: 5px 15px; 
  box-shadow: 2px 3px 5px rgb(0 0 0 / 30%);
  margin: 2em 1em;
}
@media only screen and (max-width: 768px) {
.mandala-box {
  background-size: 300%;
}
}
