/* #region = BREADCRUMBS */
#breadcrumbs, .pseudocrumbs {
    display: flex;
    position: relative;
    grid-area: breadcrumbs;
    flex-wrap: wrap;
    align-items: center;
    justify-content: flex-start;
    width: -webkit-fit-content;
    width: -moz-fit-content;
    width: fit-content;
    margin: -1em 0 1em;
