    z-index: 2;
    position: relative;
    margin-right: .75em;
    padding: 0 .5em;
    transition: color .15s cubic-bezier(.4, 0, .2, 1);
}

#breadcrumbs a:active, #breadcrumbs a:hover, .pseudocrumbs a:active, .pseudocrumbs a:hover, #breadcrumbs a:focus-within, .pseudocrumbs a:focus-within {
