}

/* Patch pour hover imbriqué */
#top-bar ul li ul li ul {
    left: 159px;
}

/* MOBILE SIDE-BAR */
@media (max-width: 767px) {
    #side-bar {
        background-color: rgb(var(--pale-gray-monochrome));
        padding: 1em 1em 0 1em;
    }

    #side-bar:target {
        border: 1px solid #dedede;
    }

    #side-bar:target .close-menu {
        background: rgba(0, 0, 0, 0.3) 1px 1px repeat;
    }

    #top-bar div.open-menu a {
        border: 0.2em solid rgb(var(--bright-accent));
        background-color: rgb(var(--gray-monochrome));
        color: rgb(var(--bright-accent));
    }

}

/* #endregion == Sigma-9 侧栏 */

/* #region == Sigma-9 Toggle Sidebar */
#top-bar .open-menu a {
    position: fixed;
    top: 0.5em;
    left: 0.5em;
    z-index: 5;
    font-family: 'Nanum Gothic', sans-serif;
    font-size: 30px;
    font-weight: 700;
    width: 30px;
    height: 30px;
    line-height: 0.9em;
    text-align: center;
    border-radius: 3em;
    border: 0.2em solid rgb(var(--medium-accent));
    background-color: rgb(var(--dark-gray-monochrome));
    color: rgb(var(--bright-accent));
    text-decoration: none;
}

@media (min-width: 768px) {
    #top-bar .mobile-top-bar {
        display: block;
    }

    #top-bar .mobile-top-bar li {
        display: none;
    }

    #main-content {
        max-width: 708px;
        margin: 0 auto;
        padding: 0;
        transition: max-width 0.2s ease-in-out;
    }

    #side-bar {
        display: block;
        position: fixed;
        top: 0;
        left: -20em;
        width: 17.75em;
        height: 100%;
        margin: 0;
        overflow-y: auto;
        z-index: 10;
        padding: 1em 1em 0 1em;
        background-color: color-mix(in srgb, rgb(var(--pale-gray-monochrome)) 70%, black 30%);
        transition: left 0.4s ease-in-out;
        scrollbar-width: thin;
    }

    #side-bar:target {
        left: 0;
    }

    #side-bar:focus-within:not(:target) {
        left: 0;
    }

    #side-bar:target .close-menu {
        display: block;
        position: fixed;
        width: 100%;
        height: 100%;
        top: 0;
        left: 0;
        margin-left: 19.75em;
        opacity: 0;
        z-index: -1;
        visibility: visible;
    }

    #side-bar:not(:target) .close-menu {
        display: none;
    }

    #top-bar .open-menu a:hover {
        text-decoration: none;
    }

    /* FIREFOX-SPECIFIC COMPATIBILITY METHOD */
    @supports (-moz-appearance: none) {
        #top-bar .open-menu a {
            pointer-events: none;
        }

        #side-bar:not(:target) .close-menu {
            display: block;
            pointer-events: none;
            user-select: none;
        }

        /* This pseudo-element is meant to overlay the regular sidebar button
so the fixed positioning (top, left, right and/or bottom) has to match */
        #side-bar .close-menu::before {
            content: "";
            position: fixed;
            z-index: 5;
            display: block;
            top: 0.5em;
            left: 0.5em;
            border: 0.2em solid transparent;
            width: 30px;
            height: 30px;
            font-size: 30px;
            line-height: 0.9em;
            pointer-events: all;
            cursor: pointer;
        }

        #side-bar:focus-within {
            left: 0;
        }

        #side-bar:focus-within .close-menu::before {
            pointer-events: none;
        }
    }
}

/* #endregion == Sigma-9 Toggle Sidebar */

/* #region == Sigma-9 移动端侧栏启动按钮 */
@media screen and (max-width: 768px) {
    #top-bar .open-menu {
