    line-height: 1;
    white-space: nowrap
}

#footer,#footer .options {
    align-items: center;
    justify-content: center
}

#footer .options {
    display: grid!important;
    grid-template-columns: repeat(9,-webkit-min-content);
    grid-template-columns: repeat(9,min-content);
    padding: 0 1.5em 0 0;
