}

#footer .options a,#footer .options a:visited {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    height: 1em;
}


#footer a,#footer a:visited {
    display: inline-flex;
    z-index: 1;
    position: relative;
    align-items: center;
    justify-content: center;
    margin: .2em;
    padding: .2em;
