/*---- 手机端标题位置 ----*/

@media only screen and (max-width: 56.25rem) and (min-width: 768px) {
    #header h1 a {
        margin-left: 7.75rem;
        margin-top: calc(var(--header-height-on-mobile)/2 - 1.345rem);
    }
}

@media only screen and (max-width: 768px) {
    #header h1 a, #header h2 span {
        margin-left: 7.75rem;
        margin-top: calc(var(--header-height-on-mobile)/2 - 2.45rem);
    }
}

@media only screen and (max-width: 415px) {
    #header h1 a, #header h2 span {
        margin-left: 7.75rem;
        margin-top: calc(var(--header-height-on-mobile)/2 - 2.2rem);
        font-size: 150%;
    }
}

@media only screen and (max-width: 374px) {
    #header h1 a, #header h2 span {
        margin-left: 7.75rem;
        margin-top: calc(var(--header-height-on-mobile)/2 - 1.85rem);
        font-size: 110%;
    }
}

/*---- 页面元素位置调整 ----*/

#search-top-box-form {
    top: 0;
}

/*---- 页面内容 ----*/

:root {
    --hoverblock-header-txt: var(--white-monochrome);
    --pager-link: var(--medium-accent);
    --pager-link-hover: var(--white-monochrome);
    --pager-selected-link: var(--dark-gray-monochrome);
    --modal-header-txt: var(--white-monochrome);
    --ui-button-hover-txt: var(--dark-gray-monochrome);
}

#top-bar {
    --dropdown-links-color: var(--dark-gray-monochrome);
    --dropdown-links-hover-color: var(--dark-gray-monochrome);
}

#main-content {
    --tabs-selected-txt: var(--dark-gray-monochrome);
    --tabs-hover-txt: var(--dark-gray-monochrome);
    --tables-border: var(--gray-monochrome);
    --tables-header-bg: var(--light-gray-monochrome);
    --footnotes-footer-bg-color: #EEF6EE;
    --footnotes-footer-num-color: var(--medium-accent);
    --ui-icon-color: var(--dark-gray-monochrome);
    --ui-icon-hover-color: var(--dark-gray-monochrome);
    --ui-icon-bg: var(--bright-accent);
    --ui-button-hover-txt: var(--dark-gray-monochrome);
    --footnotes-footer-colorbar-color: var(--swatch-primary);
    --footnotes-footer-title-bg-color: var(--swatch-primary);
    --footnotes-footer-title-text-color: var(--dark-gray-monochrome);
    --pagetags-title-text: var(--white-monochrome);
}

#footer {
    --footer-bg-color: var(--dark-gray-monochrome);
}

#breadcrumbs a:active, #breadcrumbs a:hover, .pseudocrumbs a:active, .pseudocrumbs a:hover {
    color: rgb(var(--dark-gray-monochrome));
    text-decoration: none
}

#interwiki, #side-bar {
    --sidebar-links-hover-text-color: var(--dark-gray-monochrome);
}

#header {
    --login-myaccount-hover-color: var(--dark-gray-monochrome);
    --login-myaccount-underline-color: 0,0,0,0;
    --login-myaccount-hover-bg-color: var(--bright-accent);
    --login-arrow-color: var(--search-icon-color);
    --login-dropdown-text-hover-color: var(--dark-gray-monochrome);
    --search-icon-focus-bg-color: 0,0,0,0;
}

div.page-watch-options a {
    --swatch-text-secondary-color: var(--dark-gray-monochrome);
}

table.page-history tr:nth-of-type(2) td:not(:nth-of-type(7)):before {
    --swatch-text-secondary-color: var(--dark-gray-monochrome);
}

#page-content .rate-box-with-credit-button>.creditButton>p>a:after {
    background-color: rgba(var(--rating-module-button-cancel-color),1);
}

