[[iftags +theme]]
[[module css]]
@import url("https://nu-scptheme.github.io/Black-Highlighter/css/min/normalize.min.css");
@import url("https://nu-scptheme.github.io/Black-Highlighter/css/min/black-highlighter.min.css");

.scp-image-block.block-right {
    margin: 1em 0 1.1em 2em;
}

.scp-image-block img {
    width: 100%;
    border: 0.5rem solid rgba(var(--pale-gray-monochrome));
    box-sizing: border-box;
}
.colors_container {
    width: 90%;
    display: flex;
    flex-direction: column;
    margin: 0 auto;
    font-weight: 700;
    font-family: var(--header-font);
}

.colors_container >  .colors,
.colors_container >  .subcolors {
    width: 100%;
    flex-shrink: 0;
    display: flex;
    flex-wrap: wrap;
}

.colors > .color,
.subcolors > .color {
    display: flex;
    flex-direction: row;
    flex-grow: 2;
    justify-content: center;
    align-items: flex-end;
    padding: 0.5rem;
    margin: 0.5rem;
}

.colors > .color > .sub,
.subcolors > .color > .sub,
.colors > .color > .sub > .css-variable,
.subcolors > .color > .sub > .css-variable {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%;
}

.colors > .color {
    height: 7rem;
    flex-basis: calc((100%/2) - 2rem);
}

.colors > .color.one {
    background-color: rgba(var(--gray-monochrome),1);
}

.colors > .color.two {
    background-color: rgba(var(--bright-accent),1);
}

.subcolors > .color {
    height: 3rem;
    font-size: 75%;
    text-align: center;
    flex-basis: calc((100%/6) - 2rem);
}

.colors > .color.one,
.subcolors > .color.three,
.subcolors > .color.five {
    color: rgba(var(--swatch-text-light),1);
}

.subcolors > .color.one {
    background-color: rgba(var(--pale-gray-monochrome),1);
}

.subcolors > .color.two {
    background-color: rgba(var(--light-gray-monochrome),1);
}

.subcolors > .color.three {
    background-color: rgba(var(--dark-gray-monochrome),1);
}

.subcolors > .color.four {
    background-color: rgba(var(--medium-accent),1);
}

.subcolors > .color.five {
    background-color: rgba(var(--dark-accent),1);
}

.subcolors > .color.six {
    background-color: rgba(var(--newpage-color),1);
}

.color > .sub > .css-variable {
    font-size: 75%;
    letter-spacing: 0.1em;
    font-family: var(--body-font);
}

[[/module]]
[[include :scp-wiki:component:interwiki-style
| priority=1
| theme=https://nu-scptheme.github.io/Black-Highlighter/css/min/normalize.min.css
]]

[[include :scp-wiki:component:interwiki-style
| priority=2
| theme=https://nu-scptheme.github.io/Black-Highlighter/css/min/black-highlighter.min.css
]]
