[[iftags +theme]]
[[module css]]
@import url("https://cdn.scpwiki.com/theme/en/basalt/normalize-min.css");
@import url("https://cdn.scpwiki.com/theme/en/basalt/basalt-bedrock-min.css");
[[/module]]

[[module CSS]]
:root {
    --loop3: 1;
    --loop2: 1;
    --loop1: 1;
    --demoloop: infinite;
    --custom-color: 36, 31, 36;
}
.state_one, .state_two, .state_three, .state_four {
  float: left;
  width: 20%;
padding: 5px;
}

.containser:after {
  content: "";
  display: table;
  clear: both;
}

#toc .title { font-size: 1em; }

.tocy {
    float:right;
    padding-left: 10px;
}

@media all and (max-width: 800px) { 
    #page-content div:is(.tocy) {
        float: none; 
        display: table;
        margin: 0 auto;
        padding-left: 0;
    }

    :where(.table_wrap) table {
        table-layout:fixed;
        width: fit-content;
        overflow-x:auto;
    }
}

@media screen and (max-width: 600px) {
.state_one, .state_two, .state_three {
    width: 100%;
  }
}


[[/module]]

[[div class="pseudocrumbs"]]
//See also: [[[theme:scpedia|SCPedia Theme]]], [[[theme:eigenvector|Eigenvector Theme]]]//
[[/div]]

[[>]]
[[module Rate]]
[[/>]]
