    --basalt-undertone: 63,174,255;
    --basalt-overtone: 16,17,18;
    --basalt-bright-element-color: var(--basalt-undertone);
    --basalt-dark-element-color: 168,136,230;

    --top-bar-dropdown-background: var(--basalt-secondary-color);
