::-moz-selection {
	background: rgb(var(--swatch-primary-darkest));
}
::selection {
	background: rgb(var(--swatch-primary-darkest));
}
body {
	-webkit-font-smoothing: subpixel-antialiased;
	-moz-osx-font-smoothing: auto;
}
[[/code]]
