[[module css]]
@import url("https://nu-scptheme.github.io/Black-Highlighter/stable/styles/normalize.min.css");
@import url("https://nu-scptheme.github.io/Black-Highlighter/stable/styles/black-highlighter.min.css");
@import url('https://cdn.jsdelivr.net/gh/XCninety/xcninety.github.io/Black-Highlighter/NanumBarunCatsi.css');
:root {
    --header-title: "SCP 재단";
    --header-subtitle: "확보, 격리, 보호";

    --body-font: Raleway, 'NanumBarunCatsi', sans-serif;
    --header-font: "Poppins", 'NanumBarunCatsi', sans-serif;
    --title-font: "Poppins", 'NanumBarunCatsi', sans-serif;
    --mono-font: 'Nanum Gothic Coding', "Space Mono", "Andale Mono", "Courier New", Courier, monospace;
}
#side-bar div.menu-item a[href*="/scp-artwork"] {
    flex-shrink: unset;
    text-align: left;
}
.footnote-footer > a:nth-of-type(1) {
    margin-left: 0rem;
}

#page-title, #breadcrumbs {
    text-align: center !important;
}

#page-title {
    font-family: var(--header-font);
    font-weight: bold;
}

.scp-image-block.block-right {
    margin: 1em 0 1em 2em;
}

.scp-image-block > a:first-of-type {
    background: var(--gradient-header);
}

.scp-image-block .printuser img.small {
    width: initial;
}

.scp-image-block img {
    min-width: 100%;
    -webkit-box-sizing: border-box;
       -moz-box-sizing: border-box;
            box-sizing: border-box;
    padding: 0.5em;
}
[[/module]]
