[[module CSS show="true"]]
/* Info module fix  */
#page-content .rate-box-with-credit-button {
    background: linear-gradient(to bottom, rgba(var(--header-gradient-color-bottom),0.5) 0%, rgba(var(--header-gradient-color-middle),0.15) 90%, rgba(var(--header-gradient-color-top),0.15) 100%);
    border: 0.0625rem solid rgba(var(--swatch-menubg-medium-color), 0.25);
}

#page-content .rate-box-with-credit-button>.creditButton:hover {
    background-color: rgba(var(--rating-module-button-cancel-color),.8);
}

#page-content .rate-box-with-credit-button>.creditButton>p>a:hover:before {
    background-color: rgb(var(--rating-module-button-color));
}

.modalbox {
    background: rgb(var(--black-monochrome)) !important;
}

