@import url(https://fonts.googleapis.com/css2?family=Carrois+Gothic&display=swap);

/* Febris Famaceutica Theme by jyj_52
 * [2024 Wikidot Theme]
 * Based on Aelanna's Sigma-9 Theme
 * Based also on "Pataphysics Department's Theme" by Lt Flops
 * Logo Designed by Heck_Teck
**/

@import url('https://fonts.googleapis.com/css2?family=Nanum+Gothic:wght@400;700');
@import url('https://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/css/font-awesome.min.css');

 body{
/* ---- COLOUR VARIABLES ---- */
    /* ---- GREY --- */
    --very-dark-grey: #2E1A43; /* Sfondo scuro principale di Febris */
    --grey: #5E2D79; /* Grigio violaceo scuro di Febris */
    --light-grey: #7042c7; /* Colore principale dei bordi e delle ombreggiature */
    --lighter-grey: #e6cdfa; /* Colore dei titoli e dei link */
    --very-light-grey: #E3DFFF; /* Colore del testo principale */

    /* ---- GOLD ---- */
    --strong-orange: #FFC0CB; /* Colore hover link e decorazioni */
    --pure-yellow: #FFCFDB; /* Colore per bottoni o elementi di evidenziazione */

    /* ---- GREEN ---- */
    --vivid-lime-green: #000000; /* Colore Febris per sfondi di blocchi laterali o pulsanti speciali */

    /* ---- BLUE ---- */
    --dark-blue: #1f1e1e; /* Colore dei link principali */
    --light-grayish-lime-green: #BFA2DB; /* Colore di contorno per dettagli o sezioni informative */

    /* ---- VIOLET ---- */
    --very-dark-violet: #2E1A43; /* Sfondo principale Febris per uniformità */
    --very-dark-moderate-violet: #000000; /* Background di blocchi e sidebar */
    --dark-violet: #7042c7; /* Colore per link e bordi intensi */
    --very-dark-muted-violet: #5E2D79; /* Background leggero per blocchi citazione */
    --strong-violet: #7042c7; /* Colore principale di titoli e pulsanti */
    --dark-moderate-violet: #BFA2DB; /* Colore di transizione per hover o bordi */
    --mostly-muted-dark-violet: #D1C4E9; /* Colore per bordi e sfondi leggeri */

    /* ---- GRADIENT VIOLET ---- */
    --grad-very-dark-muted-violet: #5E2D79; /* Background gradient principale */
    --grad-dark-moderate-violet: #7042c7; /* Sfondo gradiente per elementi di blocco */

    /* ---- MAGENTA ---- */
    --dark-magenta: #929292; /* Colore dei testi decorativi e titoli di sezioni */
}

/* CONTENUTO */
 div#container-wrap{
     background: url(http://jyj.wikidot.com/local--files/ff-test/bg%20test%202.jpg) top left repeat-x !important;
}
 body{
     color: hsl(0, 0%, 05%);
     color: var(--very-dark-grey);
     background-color: hsl(0, 0%, 95%);
     background-color: var(--very-light-grey);

     word-break: unset;
}

/* LOGO */
 #header{
     background-image: none;
}

 #header #header-extra-div-1{
     width: 6.25rem;
     height: 6.25rem;
     display: flex;
     position: absolute;
     left: .25rem;
     top: 2rem;
}

 #header #header-extra-div-1::before{
     content: "";
     width: 6.25rem;
     height: 6.25rem;
     position: absolute;
     background-image: url("http://jyj.wikidot.com/local--files/ff-test/logo_ff");
     background-repeat: no-repeat;
     background-position: center center;
     background-size: contain;
     flex: 1;
}

/* ---- MOBILE ANIMATION ---- */
 @media (max-width: 767px){
     #header{
         background: none !important;
    }
     #header #header-extra-div-1{
         width: 5rem !important;
         height: 5rem !important;
         left: -1rem !important;
         top: 3rem !important;
    }
     #header #header-extra-div-1::before{
         width: 5rem !important;
         height: 5rem !important;
    }
}

/* ---- GENERAL ---- */
 span,
 a{
     word-break: unset;
}
 hr{
     height: 0;
     margin: 1em 0;

     background-color: transparent;
     border-top: 1px solid hsl(0, 0%, 62%);
}
 table.wiki-content-table{
     background-color: white;
}
 table.wiki-content-table th{
     background-color: hsl(0, 0%, 88%);
     background-color: var(--light-grey);
}

/* ---- LINKS ---- */
 a{
     color: hsl(268, 100%, 37%);
     color: var(--strong-violet);
}
 a:visited{
     color: hsl(253, 60%, 33%);
     color: var(--dark-blue);
}
 #side-bar a{
     color: hsl(268, 100%, 37%);
     color: var(--strong-violet);
}
 a.newpage{
     color: hsl(298, 86%, 47%);
     color: var(--dark-magenta);
}
 a.footnoteref,
 a.footnoteref:before{
     color: hsl(307, 100%, 37%);
     color: var(--dark-magenta);
}
 a.collapsible-block-link{
     font-weight: bold;
}
 .pata-notice a{ /* ---- PATA-NOTICE LINK COLOUR ---- */
     color: hsl(51, 100%, 50%);
     color: var(--pure-yellow);
}
 .pata-notice a:visited{
     color: hsl(36, 67%, 49%);
     color: var(--strong-orange);
}
 .pata-notice a.footnoteref, /* ---- PATA-NOTICE FOOTNOTE COLOUR ---- */
 .pata-notice a.footnoteref:before{
     color: hsl(51, 100%, 50%);
     color: var(--pure-yellow);
}

/* ---- HEADER ---- */
 h1, h2, h3, h4, h5, h6{
     word-break: unset;
}
 h1,
 #page-title,
 .meta-title{
     color: hsl(266, 100%, 25%);
     color: var(--dark-violet);
}
 #header h1 a,
 #header h1 a::before{
     position: relative;
     left: -1.5px;

     letter-spacing: -.02em;
}
 #header h1 a{
     color: transparent;

     font-family: "Carrois Gothic", "Arial", sans-serif;
     text-shadow: none;
}
 #header h1 a::before{
     content: "费布里斯制药";

     color: hsl(0, 0%, 91%);
     color: var(--lighter-grey);

     text-shadow: 1px 1px 1px hsl(0, 0%, 05%);
     text-shadow: 1px 1px 1px var(--very-dark-grey);
}
 #header span{
     color: hsl(0, 0%, 91%);
     color: var(--lighter-grey);
}
 #header h2 span{
     color: transparent;

     text-shadow: none;
}
 #header h1 a span{
     content: "";

     visibility: hidden;
}
 #header h2 span::before{
     content: "吾之灾厄，吾之救恩";

     color: hsl(0, 0%, 91%);
     color: var(--lighter-grey);

     text-shadow: 1px 1px 1px hsl(0, 0%, 05%);
     text-shadow: 1px 1px 1px var(--very-dark-grey);
     text-shadow: 1px 1px 1px hsla(0, 0%, 5%, .8);
}
 @media only screen and (max-width: 425px){
     #header h1 a::before{
         content: "Febris F.";
    }
}

/* ---- TOP MENU ---- */
 #top-bar ul li ul{
     border: solid 1px hsl(268, 40%, 25%);
     border: solid 1px var(--very-dark-moderate-violet);
}
 #top-bar a{
     color: hsl(0, 0%, 91%);
     color: var(--lighter-grey);
}
 #login-status ul a{
     color: hsl(268, 100%, 37%);
     color: var(--strong-violet);
}
 #top-bar ul .sfhover a,
 #top-bar ul li:hover a{
     color: hsl(268, 40%, 25%);
     color: var(--very-dark-moderate-violet);
     background: hsl(0, 0%, 95%);
     background: var(--very-light-grey);
}
 #my-account,
 #account-topbutton{
     color: hsl(0, 0%, 95%);
     color: var(--very-light-grey);
}

/* ---- SEARCH BAR ---- */
 #search-top-box-input{
     background-color: hsl(268, 40%, 25%);
     background-color: var(--very-dark-moderate-violet);
     border: solid 1px hsl(268, 33%, 30%);
     border: solid 1px var(--very-dark-muted-violet);
     border-radius: 0;
}
 #search-top-box-input:hover,
 #search-top-box-input:focus{
     background: hsl(268, 25%, 50%);
     background: var(--mostly-muted-dark-violet);
     border: solid 1px hsl(0, 0%, 95%);
     border: solid 1px var(--very-light-grey);
     border-radius: 0;
}
 #search-top-box-form input[type="submit"]{
     color: hsl(270, 75%, 76%);
     background: hsl(268, 33%, 30%);
     background: var(--very-dark-muted-violet);
     border: solid 1px hsl(268, 40%, 45%);
     border: solid 1px var(--dark-moderate-violet);
     border-radius: 0;
     box-shadow: none;

     font-size: unset;
}
 #search-top-box-form input[type="submit"]:hover,
 #search-top-box-form input[type="submit"]:focus{
     color: hsl(0, 0%, 95%);
     color: var(--very-light-grey);
     background: hsl(268, 25%, 50%);
     background: var(--mostly-muted-dark-violet);
     border: solid 1px hsl(0, 0%, 95%);
     border: solid 1px var(--very-light-grey);
     border-radius: 0;
     box-shadow: none;
}

/* ---- SIDE MENU ---- */
 #side-bar{
     background: none;
}
 #side-bar .side-block{
     background: hsl(0, 0%, 91%);
     background: var(--lighter-grey);
     border: 3px solid hsl(268, 40%, 25%);
     border: 3px solid var(--very-dark-moderate-violet);
     border-radius: 0;
     box-shadow: none;
}
 #side-bar .heading{
     color: hsl(268, 40%, 25%);
     color: var(--very-dark-moderate-violet);
     border-bottom: 1px solid hsl(268, 40%, 45%);
     border-bottom: 1px solid var(--dark-moderate-violet);
}
 .side-block .menu-item > .image{
     filter: hue-rotate(268deg) saturate(90%) brightness(100%);
     -webkit-filter: hue-rotate(268deg) saturate(90%) brightness(100%);
}
 #side-bar div.menu-item.inactive a{
     color: var(--very-dark-moderate-violet);
}
 #side-bar div.menu-item .sub-text{
     color: var(--very-dark-moderate-violet);
}
 #side-bar .collapsible-block-folded{
     background: none;
}
 #side-bar .collapsible-block-unfolded-link{
     border-bottom: solid 1px var(--very-dark-moderate-violet);
}
 #side-bar .collapsible-block-unfolded-link .collapsible-block-link{
     color: var(--very-dark-moderate-violet);

     font-weight: bold;
}

/* ---- INFO BAR ---- */
 body{
     --linkColour: hsl(0, 0%, 95%);
     --linkColour: var(--very-light-grey);
     --barColour: hsl(268, 39%, 10%);
     --barColour: var(--very-dark-violet);
}

/* ---- INFO PANE ---- */
 .rate-box-with-credit-button{
     background-color: hsl(268, 40%, 25%);
     background-color: var(--very-dark-moderate-violet);
     border-color: hsl(268, 40%, 25%);
     border-color: var(--very-dark-moderate-violet);
     border-radius: 0 !important;
     box-shadow: none !important;
}
 .rate-box-with-credit-button .fa-info{
     color: hsl(0, 0%, 95%);
     color: var(--very-light-grey);
     border-color: hsl(268, 40%, 45%);
     border-color: var(--dark-moderate-violet);
}
 .rate-box-with-credit-button .fa-info:hover{
     color: hsl(112, 77%, 48%);
     color: var(--vivid-lime-green);
}
 .rate-box-with-credit-button .cancel{
     border: 0;
}
 .close-credits,
 .credit-back{
     filter: hue-rotate(268deg);
}
 .modalbox{
     box-shadow: 0 1px 10px hsla(282, 73%, 51%, .3);
}


.creditButtonStandalone {
     background-color: hsl(268, 40%, 25%) !important;
     background-color: var(--very-dark-moderate-violet) !important;
     border-color: hsl(268, 40%, 25%) !important;
     border-color: var(--very-dark-moderate-violet) !important;
     border-radius: 0 !important;
     box-shadow: none !important;
}

.creditButtonStandalone .fa-info {
     background-color: hsl(268, 40%, 25%);
     background-color: var(--very-dark-moderate-violet);
     color: hsl(0, 0%, 95%);
     color: var(--very-light-grey);
     border-color: hsl(268, 40%, 25%) !important;
     border-color: var(--very-dark-moderate-violet) !important;
     border-radius: 0 !important;
}

.creditButtonStandalone .fa-info:hover {
     color: hsl(112, 77%, 48%);
     color: var(--vivid-lime-green);
}

/* ---- PAGE RATING ---- */
 .page-rate-widget-box{
     border: none;
     border-radius: 0;
     box-shadow: none;
}
 div.page-rate-widget-box .rate-points{
     background-color: hsl(268, 40%, 25%) !important;
     background-color: var(--very-dark-moderate-violet) !important;
     border: solid 1px hsl(268, 33%, 30%) !important;
     border: solid 1px var(--very-dark-muted-violet) !important;
     border-radius: 0;
}
 .page-rate-widget-box .rateup,
 .page-rate-widget-box .ratedown{
     background-color: hsl(0, 0%, 95%);
     background-color: var(--very-light-grey);
     border-top: solid 1px hsl(268, 33%, 30%);
     border-top: solid 1px var(--very-dark-muted-violet);
     border-bottom: solid 1px hsl(268, 33%, 30%);
     border-bottom: solid 1px var(--very-dark-muted-violet);
}
 .page-rate-widget-box .rateup a,
 .page-rate-widget-box .ratedown a{
     color: hsl(268, 40%, 25%);
     color: var--very-dark-moderate-violet);
}
 .page-rate-widget-box .rateup a:hover,
 .page-rate-widget-box .ratedown a:hover{
     color: hsl(0, 0%, 95%);
     color: var(--very-light-grey);
     background: hsl(268, 40%, 25%);
     background: var(--very-dark-moderate-violet);
}
 .page-rate-widget-box .cancel{
     background-color: hsl(268, 40%, 25%);
     background-color: var(--very-dark-moderate-violet);
     border: solid 1px hsl(268, 33%, 30%);
     border: solid 1px var(--very-dark-muted-violet);
     border-left: 0;
     border-radius: 0;
}
 .page-rate-widget-box .cancel a{
     color: hsl(268, 40%, 45%);
     color: var(--dark-moderate-violet);
}
 .page-rate-widget-box .cancel a:hover{
     color: hsl(0, 0%, 95%);
     color: var(--very-light-grey);
     background: hsl(268, 40%, 25%);
     background: var(--very-dark-moderate-violet);
     border-radius: 0;
}

/* ---- DIV BLOCKS ---- */
 blockquote,
 div.blockquote{
     background-color: var(--lighter-grey);
     border: 1px solid hsl(0, 0%, 60%);
}
 .pata-notice{
     margin: 1em 0;
     padding: 0 1em;

     color: hsl(0, 0%, 95%);
     color: var(--very-light-grey);
     background-color: hsl(268, 40%, 25%);
     background-color: var(--very-dark-moderate-violet);
     border: 1px solid hsl(268, 33%, 30%);
     border: 1px solid var(--very-dark-muted-violet);

     text-align: center;
}
 .code{
     background-color: hsl(0, 0%, 92%);
     border-color: hsl(0, 0%, 82%);
}

/* ---- CUSTOM PAGE CONTENT CLASSES ---- */
 .content-panel .panel-body{
     background: hsl(0, 0%, 95%);
     background: var(--very-light-grey);
}
 .content-panel .panel-footer{
     color: hsl(0, 0%, 95%);
     color: var(--very-light-grey);
}
 .content-panel .panel-footer a{
     color: hsl(0, 0%, 95%);
     color: var(--very-light-grey);
}

/* ---- YUI TAB CUSTOMIZATION ---- */
 .yui-navset .yui-content{
    /* ---- Content Background ---- */
     background-color: white;
    /* -------- */
}
 .yui-navset .yui-nav a, /* ---- Link Modifier ---- */
 .yui-navset .yui-navset-top .yui-navset-left .yui-navset-right .yui-nav a{
     color: hsl(0, 0%, 05%);
     color: var(--very-dark-grey);
    /* ---- Tab Background Colour | [UNSELECTED] ---- */
     background-color: white;
    /* -------- */
     background-image: none;
     border: solid 1px #808080;
     border-bottom: none;

     transition: .125s;
}
 .yui-navset .yui-nav .selected a, /* ---- Selection Modifier ---- */
 .yui-navset .yui-nav .selected a:focus,
 .yui-navset .yui-nav .selected a:hover{
     color: hsl(0, 0%, 95%);
     color: var(--very-light-grey);
    /* ---- Tab Background Colour | [SELECTED] ---- */
     background: hsl(268, 40%, 25%);
     background: var(--very-dark-moderate-violet);
    /* -------- */
}
 .yui-navset .yui-nav a:hover,
 .yui-navset .yui-nav a:focus{
    /* ---- Tab Background Colour | [HOVER] ---- */
     background: hsl(0, 0%, 75%);
     background: var(--grey);
    /* -------- */
}
 .yui-navset .yui-nav, /* ---- Between Tab And Content ---- */
 .yui-navset .yui-navset-top .yui-nav{
     border-color: hsl(268, 40%, 25%);
     border-color: var(--very-dark-moderate-violet);
}

/* ---- STANDARD IMAGE BLOCK ---- */
 .scp-image-block{
     box-shadow: none;
}
 .scp-image-block .scp-image-caption{
     background-color: hsl(0, 0%, 88%);
     background-color: var(--light-grey);
     border-top: solid 1px hsl(268, 40%, 25%);
     border-top: solid 1px var(--very-dark-moderate-violet);
}

/* ---- INTERWIKI ---- */
 #interwiki body{
     overflow: hidden;
}
 #interwiki .side-block{
     background: var(--light-grayish-lime-green);
     border: 3px solid var(--very-dark-moderate-violet);
     border-radius: 0;
     box-shadow: none;
}
 #interwiki .heading{
     color: var(--very-dark-moderate-violet);
     border-color: var(--dark-moderate-violet);
}

/* ---- ADJUST MOBILE IMAGE SIZE ---- */
 @media (max-width: 767px){
     #top-bar .open-menu a{
         color: hsl(268, 40%, 25%) ;
         color: var(--very-dark-moderate-violet);
         background-color: white;
         border: 2px solid hsl(268, 40%, 25%);
         border: 2px solid var(--very-dark-moderate-violet);
    }
     #side-bar{
         background-color: hsl(0, 0%, 75%);
         background-color: var(--grey);
    }
     #side-bar:target + #main-content{
         left: 0;
    }
}
 @media (min-width: 768px){
     #header h1 a{
         font-size: 150%;
    }
}

/* ---- REDUCED MOTION ACCESSIBILITY ----
 * By SMLT
**/
 @media (prefers-reduced-motion: reduce){
     *, *::before, *::after{
         animation-duration: .001s !important;
         animation-iteration-count: 1 !important;
         transition-duration: .001s !important;
    }
}

