[[code type="css"]]
@charset "utf-8";

/*
    기동항공대 CSS 테마
    [2020 Wikidot Theme]
    Created for SCP FMAF by thd-glasses
    The FMAF Logos were created by thd-glasses and is licensed under CC-BY-SA 3.0.
*/

@import url('https://fonts.googleapis.com/css2?family=Chakra+Petch:wght@700&display=swap');
@font-face {
    font-family: 'EliceDigitalBaeum_Bold';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_220508@1.0/EliceDigitalBaeum_Bold.woff2') format('woff2');
    font-weight: 700;
    font-style: normal;
}

 #header {
     background-image: url(https://scpko.wdfiles.com/local--files/theme%3Afmaf-theme/FMAF_logo.png);
}

 #header h1 a {
     color: transparent;
     font-size: 150%;
     text-shadow: none;
}

 #header h1 a::before {
    content: "MOBILE AIR FORCE";
    font-family: "Chakra Petch", "EliceDigitalBaeum_Bold", sans-serif;
    font-weight: 700;
    color: #eee;
    text-shadow: 3px 3px 5px #000000;
}

 #header h2 span {
     color: transparent;
     font-size: 120%;
     text-shadow: none;
}
 #header h2 span::before {
    content: 'ALPHA-17 OPERATIONS COMMAND';
    font-family: "Chakra Petch", "EliceDigitalBaeum_Bold", sans-serif;
    color: #f0f0c0;
    text-shadow: 1px 1px 1px #000000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

 #side-bar .side-block {
     border-radius: 0px;
}

.yui-nav {
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: flex-end;
}

.yui-nav li {
  display: flex;
  flex-grow: 2;
  margin: 0;
}

.yui-nav li a {
  width: 100%;
  text-align: center;
}

.yui-navset .yui-content {
  border: none;
  border-bottom: 2px solid #641527;
  background-color: #F9F9F9;
  padding: 15px;
}

.yui-navset .yui-nav a {
  background:#FFF;
  background-image: none;
  border-top: solid #641527 2px;
  border-left: solid #EDD 1px;
  border-right: solid #EDD 1px;
  border-bottom: none;
  color:#000;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
  background: linear-gradient(to bottom, #782637,#641527);
  color: #FFF;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
  background: #DDD;
  text-decoration: none;
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
  border-color: #641527;
}

@media (min-width: 768px) {
  #header { background-position: 10px 30px; }
}

@media (max-width: 767px) {
   #header h1 a::before {
      content: "FMAF";
  }
   #header h2 span::before {
      content: 'ALPHA-17';
  }
}
[[/code]]

