blockquote {

    background-image: radial-gradient(#FFE4B5 60%, #D2B48C);
    box-shadow: 0 0 6px 1px  rgb(63, 63, 63)
}



:root {
  --logo-image: url("https://scpsandboxcn.wikidot.com/local--files/fishalt-2/%E6%A0%87%E8%AE%B0%E9%9B%86");
/* Typefaces */
   --body-font:  'IBM Plex Mono', '仿宋',  monospace;
   --header-font:  'ZCOOL XiaoWei', serif;
   --title-font:  'IBM Plex Mono', '仿宋',  monospace;
   --mono-font:  'IBM Plex Mono', '仿宋',  monospace;
   --UI-font: 'ZCOOL XiaoWei', serif;


   --black: 0, 0, 0;
   --PeachPuff: 255, 218, 185;  
   --Burlywood2: 238, 197, 145;  
   --Goldenrod4: 139, 105, 20;
   --Wheat4: 145, 120, 7, 0.5;  
   --LightGoldenrod3: 205, 190, 112;
   --neon-blue: 0, 229, 255;

--swatch-primary: var(--PeachPuff);
     --swatch-primary-darker: var(--Wheat4);
     --swatch-primary-darkest: var(--Burlywood2);
        --swatch-background: var(--Burlywood2);
     --swatch-border-color: var(--PeachPuff);

        --swatch-text-light: var(--PeachPuff);
        --swatch-text-general: var(--Goldenrod4);

        --swatch-menubg-color: var(--Goldenrod4);
        --swatch-menubg-extra-dark-color: var(--Burlywood2);
        --swatch-menutxt-general-color: var(--PeachPuff);

        --swatch-secondary-color: var(--Burlywood2);
        --swatch-tertiary-color: var(--PeachPuff);
        --swatch-alternate-color: var(--Goldenrod4);

        --swatch-text-secondary-color: var(--Wheat4);
        --swatch-text-tertiary-color: var(--Wheat4);

        --swatch-topmenu-bg-color: var(--PeachPuff);
}

:root:lang(cn) {
    --header-title: "异常美食部";
    --header-subtitle: "民以食为天";
}

#header {
--login-line-divider-color: var(--PeachPuff);
    --login-username-color: var(--LightGoldenrod3);
    --login-myaccount-color: var(--LightGoldenrod3);
    --login-dropdown-text-color: var(--PeachPuff);
    --login-dropdown-text-hover-color: var(--Goldenrod4);
    }

#main-content {
    --footnotes-footer-title-text-color: var(--PeachPuff);
    --hoverblock-header-txt: var(--PeachPuff);

    --tabs-hover-bg: var(--PeachPuff);
    --tabs-selected-bg: var(--Burlywood2);
    --tabs-selected-txt: var(--Goldenrod4);
    --tabs-selected-outline: var(--Goldenrod4);
    }

#top-bar {
        --dropdown-bg-color: var(--Goldenrod4);
     --mobile-topmenu-sidebar-button-color: var(--Goldenrod4);

     --topmenu-category-color: var(--Burlywood2);
     --topmenu-category-hover-color: var(--Goldenrod4) ;
     --dropdown-links-color: var(--Burlywood2);
     --dropdown-links-hover-color:  var(--Goldenrod4);
    }



body {
background: rgb(255, 242, 204);
}

a,a:visited {
    color: rgb(210, 117, 20);
}    

h1,h2,h3,h4,h5,h6 {
    color: rgb(120, 63, 4);
    font-family: 'ZCOOL XiaoWei', serif;
}

div#container-wrap {
    background-image: url('https://scpsandboxcn.wikidot.com/local--files/fishalt-2/%E6%B6%88%E6%9D%A1%E5%AD%90');
}



