/* Alternate Rating Module */

.page-rate-widget-box,
div.page-rate-widget-box .rate-points {

color: var(--dark-accent);
border-radius: 0;
font-family: var(--title-font);
box-shadow: none;
background-color: var(--ghost-accent);
border-color: var(--dark-accent);
border-width: 2px;
padding-top: 1px;
padding-bottom: 1px;

}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {

background-color: var(--ghost-accent);
padding-top: 1px;
padding-bottom: 1px;
border-color: var(--dark-accent);
border-width: 2px;

}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {

color: var(--dark-accent);

}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {

background: var(--ghost-accent);
color: var(--bright-accent);
text-decoration: none;

}

.page-rate-widget-box .cancel a {

color: var(--bright-accent);
border-color: var(--dark-accent);
border-width: 2px;

}

.page-rate-widget-box .cancel a:hover {

color: var(--bright-accent);
padding-top: 1px;
padding-bottom: 1px;
background-color: var(--ghost-accent);
border-color: var(--dark-accent);
border-width: 2px;

}

.page-rate-widget-box {

margin-right: 0;

}

.page-rate-widget-box .cancel {

border-radius: 0 !important;
padding-top: 1px;
padding-bottom: 1px;
background-color: var(--ghost-accent);
border-color: var(--dark-accent);
border-width: 2px;

}

#page-content .rate-box-with-credit-button {

background-color: var(--ghost-accent);
border: solid 2px var(--dark-accent);
border-radius: 0;
box-shadow: none;

}

#page-content .rate-box-with-credit-button .page-rate-widget-box .rate-points,
#page-content .rate-box-with-credit-button .page-rate-widget-box .rateup,
#page-content .rate-box-with-credit-button .page-rate-widget-box .ratedown,
#page-content .rate-box-with-credit-button .page-rate-widget-box .cancel {

border: none;

}

