/* Logo Animation */

div#extra-div-1 {

animation: intro, spin cubic-bezier(1,1,1,1);
animation-duration: 1s, .5s;
animation-delay: 0s, 1s;

}

@keyframes intro {

from { scale: .1; }
to { scale: 1; }

}

@keyframes spin {

100% { transform: rotate(360deg); }

}

