@font-face { font-family: "NanumSquareRound"; src: url("https://fastly.jsdelivr.net/gh/projectnoonnu/noonfonts_two@1.0/NanumSquareRound.woff") format("woff"); font-style: normal; font-weight: normal; }

:root {
  --main: var(--main-1, 34, 34, 34);
  --bg: var(--bg-1, 255, 255, 255);
  --issue: "특별호";
  --feature: "가락을 만나다 심층 인터뷰 대특집";
  --index: {$index};
  --title: var(--title-0, 200%);
  --padding-top: var(--title-0, 1em);
  --body-bg: url('https://scpko.wdfiles.com/local--files/theme:garak-the-magazine/body_bg.png');
}

html {
  overflow-x: hidden;
  scrollbar-color: rgba(var(--main),0.2) rgba(var(--main),0.15);
}
::-webkit-scrollbar {
    width: 15px;
}
::-webkit-scrollbar-thumb {
  background: rgba(var(--main),0.2);
}
::-webkit-scrollbar-track {
  background: rgba(var(--main),0.15);
}

html div {
  scrollbar-width: thin;
  scrollbar-color: rgba(var(--main),0.3) transparent;
}
html div::-webkit-scrollbar {
  width: 8px;
}
html div::-webkit-scrollbar-thumb {
  background: rgba(var(--main),0.3);
}
html div::-webkit-scrollbar-track {
  background: transparent;
}

body {
  font-family: 'NanumSquareRound', san-serif;
  font-size: 1em;
}

div#container-wrap {
  background: rgba(var(--main), 0.5) var(--body-bg) center/cover repeat;
}

#main-content {
  padding-top: 5em;
  display: flex;
  flex-wrap: wrap;
}

#main-content > div {
  box-sizing: border-box;
  width: 100%;
  max-width: 100%;
  order: 2;
}

#page-options-container {
  display: grid;
}

#header {
  background: url('https://scpko.wdfiles.com/local--files/theme:garak-the-magazine/garak-the-magazine.png') center/contain no-repeat;
  height: 170px;
  margin-bottom: 66px;
  padding-bottom: 25px;
}

div#header-extra-div-1 {
  position: absolute;
  inset: 0;
  -webkit-mask: url('https://scpko.wdfiles.com/local--files/theme:garak-the-magazine/garak-the-magazine.png') center/contain no-repeat;
  mask: url('https://scpko.wdfiles.com/local--files/theme:garak-the-magazine/garak-the-magazine.png') center/contain no-repeat;
  pointer-events: none;
}

div#header-extra-div-1 span {
  position: absolute;
  inset: 0;
  background: rgb(var(--main));
}

#header h1 a {
  display: block;
  position: absolute;
  color: transparent;
  text-shadow: none;
  top: 25px;
  left: 50%;
  transform: translateX(-50%);
  width: min(510px, 80%);
  height: 120px;
  max-height: 150px;
  padding: 0;
}

#header h2 {
  display: block;
  position: absolute;
  top: max(min(180px, 27vw), 170px);
  left: 50%;
  transform: translateX(-50%);
  margin-left: 0;
  width: 95%;
  text-align: center;
}

#header h2 span {
  display: flex;
  color: unset;
  font-size: 0px;
  text-shadow: none;
  line-height: 1.3;
  width: 100%;
  justify-content: center;
  align-items: center;
  word-break: keep-all;
}

#header h2 span::before {
  display: block;
  content: "【" var(--issue-1, '특별호') "】";
  font-size: 20px;
}

#header h2 span::after{
  display: block;
  content: var(--feature-1, '가락을 만나다 심층 인터뷰 대특집');
  font-size: 25px;
}

#page-title, .pseudo-title, h1, h2, h3, h4, h5, h6 {
  font-family: "Paperlogy", san-serif;
  color: rgb(var(--main));
}

#page-content p em {
  font-family: "Paperlogy", san-serif;
  margin-right: 0.2em;
}

#page-title,
#page-content .pseudo-title {
  position: relative;
  padding: 1.25em 1em 0.5em;
  font-size: 200%;
  text-align: center;
  word-break: keep-all;
  margin-bottom: 1em;
}

#page-title {
  font-size: var(--title);
}

#main-content > #page-title {
  order: 1;
  margin-top: -0.12rem;
  margin-bottom: 0;
  border: 2px solid rgb(var(--main));
  border-bottom: none;
  background: rgb(var(--bg));
}

#page-content .pseudo-title {
  left: -0.8rem;
  width: calc(100% + 1.6rem);
  max-width: 100vw;
  box-sizing: border-box;
}

#page-title::before,
.pseudo-title:not(.empty)::before {
  content: "";
  position: absolute;
  inset: 0 1em;
  border-bottom: 1px solid rgba(var(--main), 0.5);
  pointer-events: none;
}

#page-content .pseudo-title.empty {
  margin-bottom: 0;
  padding-top: 0.5em;
}

#page-title::after,
.index {
  display: block;
  position: absolute;
  top: -2px;
  right: -1em;
  font-size: 1.1rem;
  background: rgb(var(--main));
  color: rgb(var(--bg));
  padding: 5px 1.5em;
  clip-path: polygon(0 0, 100% 0, calc(100% - 0.75em) 100%, 0.75em 100%);
}

#page-title::after {
  content: var(--index);
  right: 0;
}

#main-content > #breadcrumbs {
  font-size: 0;
  order: 0;
  overflow: hidden;
  border-left: 2px solid rgb(var(--main));
}

#breadcrumbs a {
  display: inline-block;
  position: relative;
  font-size: 15px;
  padding: 5px 20px 7px 15px;
  margin-bottom: 0.12rem;
  transform: translateX(-4px);
  background: rgb(var(--main));
  color: rgb(var(--bg));
  text-decoration: none;
  border: 2px solid transparent;
  border-bottom: none;
  clip-path: polygon(0 0, calc(100% - 16px) 0, calc(100% - 1px) 15px, calc(100% - 1px) 100%, 0 100%);
}

#breadcrumbs a::before {
  content: "";
  position: absolute;
  background-color: rgb(var(--main));
  inset: 0;
  left: -2px;
  z-index: -1;
  clip-path: polygon(0 0, calc(100% - 15px) 0, calc(100% - 1px) 14px, calc(100% - 1px) 100%, 0 100%);
}

#breadcrumbs a:not(:first-child) {
  padding-left: 25px;
  margin-left: -11px;
  clip-path: polygon(0 0, calc(100% - 15px) 0, 100% 15px, 100% 100%, 13px 100%, 13px 14px);
}

#breadcrumbs a:not(:first-child)::before {
  clip-path: polygon(0 0, calc(100% - 15px) 0, 100% 15px, 100% 100%, 0 100%);
}

#breadcrumbs a:hover::before {
  background-color: rgb(var(--bg));
  opacity: 0.25;
}

#page-content {
  padding: 1em 2em;
  border: 2px solid rgb(var(--main));
  border-top: none;
  background: rgb(var(--bg));
  text-align: justify;
  min-height: 1250px;
}

#page-content {
  padding-top: var(--padding-top);
}

#action-area {
  padding: 1em;
  border: 2px solid rgb(var(--main));
  background: rgb(var(--bg));
  box-sizing: border-box;
}

#action-area textarea {
  width: 99% !important;
}

table.form td {
  max-width: 80vw;
}

#page-content .gap {
  position: relative;
  left: calc(-2em - 2px);
  width: calc(100% + 4em + 4px);
  max-width: 100vw;
  height: auto;
  padding: 2em 0;
  margin-top: 2em;
  border: solid rgb(var(--main));
  border-width: 2px 0;
  background: rgb(var(--bg)) var(--body-bg) center/300% repeat;
}

#page-content .gap::before {
  content: "";
  position: absolute;
  inset: 0;
  background: rgba(var(--main), 0.5) var(--body-bg) center/300% repeat;
}

#page-content > .cover:not(.gap) {
  position: relative;
  margin: -2px calc(-2em);
  max-width: unset;
  width: calc(100% + 4em);
  box-sizing: border-box;
}
#page-content > .cover img {
  width: 100%;
}

.cover p {
  display: inline-block;
  position: absolute;
  inset: 0; bottom: unset;
  text-align: right;
  padding-right: 15px;
  color: rgb(var(--main));
  font-size: min(3vw, 20px);
  word-break: keep-all;
  text-shadow: 2px 2px 2px var(--shadow), 2px -2px 2px var(--shadow), -2px 2px 2px var(--shadow), -2px -2px 2px var(--shadow), 2px 0px 2px var(--shadow), 0px 2px 2px var(--shadow), -2px 0px 2px var(--shadow), 0px -2px 2px var(--shadow);
  --shadow: rgb(var(--bg));
}

#page-content .gap.cover {
  margin-top: 0;
}

.gap.cover::after,
.gap > .cover {
  position: absolute;
  bottom: 4.5rem;
  left: 0;
  width: 100%;
  height: 300px;
  box-sizing: border-box;
  padding: 10px;
  content: var(--issue-1, '특별호') "】 " var(--feature-1, '가락을 만나다 심층 인터뷰 대특집') "】";
  writing-mode: vertical-lr;
  text-orientation: sideways;
  direction: rtl;
  white-space: nowrap;
  text-align: right;
  color: rgb(var(--main));
  font-size: min(50px, 5vw);
  font-family: "Paperlogy", san-serif;
  font-weight: 800;
  --shadow: rgb(var(--bg));
}

.gap.cover::after {
  background: url('https://scpko.wdfiles.com/local--files/theme:garak-the-magazine/garak-the-magazine.png') bottom right/90% auto no-repeat;
  filter: drop-shadow(0 3px 1px var(--shadow)) drop-shadow(0 -3px 1px var(--shadow)) drop-shadow(3px 0 1px var(--shadow)) drop-shadow(-3px 0 1px var(--shadow));
}

.gap > div.cover {
  -webkit-mask: url('https://scpko.wdfiles.com/local--files/theme:garak-the-magazine/garak-the-magazine.png') bottom right/90% auto no-repeat;
  mask: url('https://scpko.wdfiles.com/local--files/theme:garak-the-magazine/garak-the-magazine.png') bottom right/90% auto no-repeat;
  z-index: 2;
}

.gap > .cover::before {
  content: "";
  position: absolute;
  inset: 0;
  background: rgb(var(--main));
}

.cover.dark p,
.gap.cover.dark::after {
  color: rgb(var(--bg));
  --shadow: rgb(var(--main));
}
.gap.dark > .cover::before {
  background: rgb(var(--bg));
}

strong {
  font-family: "Paperlogy", san-serif;
  color: rgb(var(--main));
}

a, a:visited {
  font-family: "Paperlogy", san-serif;
  color: rgb(var(--main));
  text-decoration: underline;
}

a:hover {
  background: rgb(var(--main), 0.15);
}

#login-status a,
#login-status,
#side-bar a,
#u-side a {
  color: rgb(var(--main));
  text-decoration: none;
}

a.newpage {
  color: rgb(var(--main), 0.5);
}

#top-bar {
  background: rgb(var(--main));
  top: 230px;
  right: 0;
  box-shadow: 50vw 0 0 rgb(var(--main)), -50vw 0 0 rgb(var(--main)), 25vw 0 0 rgb(var(--main)), -25vw 0 0 rgb(var(--main));
  height: 31px;
  font-size: 0.8em;
}

#header #top-bar > div > ul > li:not(:hover) > a {
  color: rgb(var(--bg));
}

#top-bar ul li ul li ul {
    z-index: 1;
}

#header #top-bar a,
.c_topbar_arrow {
  color: rgb(var(--main));
}

#top-bar > div > ul {
  display: flex;
  justify-content: center;
  width: 100%;
}

#top-bar ul li a {
  padding-top: 15px;
  padding-bottom: 15px;
}

#top-bar .open-menu a {
  border-color: rgb(var(--main));
  color: rgb(var(--main));
  text-decoration: none;
}

#login-status {
  right: unset;
  left: 10px;
}

#search-top-box {
  top: 10px;
  right: 10px;
}

#search-top-box-form input[type="submit"] {
  background: rgb(var(--main));
  border-color: rgb(var(--bg));
  color: rgb(var(--bg));
}

#search-top-box-form input[type="submit"]:hover,
#search-top-box-form input[type="submit"]:focus {
  background: rgb(var(--main));
}

#side-bar .side-block,
#u-side .side-block {
  border: none;
  box-shadow: none;
}

#side-bar .side-block, #side-bar .red, #side-bar .green, #side-bar .blue,
#u-side .side-block, #u-side .red, #u-side .green, #u-side .blue {
  background: none;
}

#side-bar .side-block, #side-bar .collapsible-block-folded, #side-bar .collapsible-block-unfolded-link, #side-bar .collapsible-block-unfolded, #side-bar .heading,
#u-side .side-block, #u-side .collapsible-block-folded, #u-side .collapsible-block-unfolded-link, #u-side .collapsible-block-unfolded, #u-side .heading {
  color: rgba(var(--main), 0.8);
  border-color: rgba(var(--main), 0.8);
}

#side-bar .heading,
#u-side .heading {
  font-size: 1em;
}

blockquote,
div.blockquote {
  background: rgba(var(--main), 0.05);
  border: solid rgb(var(--main));
  border-width: 2px 0;
  padding: 0.5em 1em;
  margin: 1em 2em;
}

.writer {
  margin: -1.5em 1em 0;
  font-family: "Paperlogy", san-serif;
  font-size: 1.1em;
  text-align: right;
  color: rgb(var(--main));
}
.writer strong {
  font-size: 1.1em;
}

.gasa {
  text-align: center;
  font-family: "Paperlogy", san-serif;
  margin: 3em auto;
  padding: 0.5em 0;
  width: min(95%, 500px);
  background: rgba(var(--main), 0.1);
  box-shadow: 0.5em 0.5em 1em rgba(var(--main),0.05), 0.5em -0.5em 1em rgba(var(--main), 0.05), -0.5em 0.5em 1em rgba(var(--main), 0.05), -0.5em -0.5em 1em rgba(var(--main), 0.05);
  border-radius: 1em;
}

.gasa > p {
  font-style: italic;
}

.gasa > p:first-of-type {
  margin-top: 2em;
}

.gasa p, gasa span {
  word-break: keep-all;
}

.image-container.floatright {
  width: 300px;
  padding-left: 2em;
}

.yui-navset {
  margin: 2em 0;
}

.gap .yui-navset + script + script + .yui-navset {
  margin-top: 4em;
}

.yui-navset .yui-nav {
  border-bottom: none;
  border-left: 2px solid rgb(var(--main));
  overflow: hidden;
}

.yui-navset .yui-nav li,
.yui-navset .yui-nav li.selected {
  margin: -0.1em 0.16em 0.1em 0;
}

#page-content .yui-navset .yui-nav a {
  position: relative;
  border: 2px solid transparent;
  border-bottom: none;
  background-color: rgb(var(--main));
  background-image: none;
}

.yui-navset .yui-nav a {
  color: rgb(var(--bg));
  padding: 5px 20px 5px 15px;
  clip-path: polygon(0 0, calc(100% - 15px) 0, 100% 15px, 100% 100%, 0 100%);
}

.yui-navset .yui-nav a::before {
  content: "";
  position: absolute;
  background-color: rgb(var(--main));
  inset: 0;
  left: -2px;
  z-index: 0;
