+ CSS 版式代码
@@@@
[[code type="css"]]
/*
    Powered on 2021/05/08 12:36:31

    [2021 Wikidot Theme]

    Created by c_bonefish
    Header Code adopted from The Buck Stops Here CSS Theme by thd-glasses
    Info Bar and Page Rating Code adopted from Technoblast Theme by EstrellaYoshte

    Background Image1: https://pixabay.com/vectors/ornate-decoration-banner-greeting-3068591/
    Background Image2: https://pixabay.com/illustrations/chandelier-ornament-decor-golden-3006697/

    CC BY-SA 3.0
*/

@import url('https://fonts.googleapis.com/css?family=Poiret+One');

:root {
    --title: "安布罗斯餐厅";
    --short_title: "安布罗斯";
}

/* Header */

div#container-wrap {
    background: url(https://scp-wiki.wdfiles.com/local--files/theme:golden-feast/background.png) center top repeat-x;
    background-size: 224px 400px;
}

#header h1 {
    margin-left: 0;
    padding: 0px;
    clear: both;
    float: none;
}

#header h1 a {
    color: transparent;
    font-size: 0px;
    font-family: 'Poiret One', '楷体', cursive;
    letter-spacing: 0.5px;
}

#header h1 a::before {
    display: inline-block;
    width: 100%;
    text-align: center;
    content: var(--title);
    color: #FFF;
    font-size: 50px;
    text-shadow: 1px 1px 3px black;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    top: 0;
    padding-top: 75px;
}

#header h2 {
    font-size: 0px;
}

#header #header-extra-div-1 {
    z-index: -1;
    width: 100%;
    height: 100%;
    display: flex;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    top: 0;
    margin-top: 0px;
}

#header #header-extra-div-1::before{
    z-index: -1;
    content: "";
    width: 100px;
    height: 100px;
    position: absolute;
    background-repeat: no-repeat;
    background-position: center center;
    background-size : 280px
    flex: 1;
}

#header #header-extra-div-1 {
    background: url(https://scp-wiki.wikidot.com/local--files/theme:golden-feast/top-image.png) center top no-repeat;
    background-size : 280px
}

#header {
    background: none;
}

#login-status {
  position: absolute;
  top: 30px;
  z-index: 2;
}

#login-status {
    color: #ffffff;
    font-size: 90%;
}

#login-status ul a {
    color: #A38800;
}

#search-top-box {
  position: absolute;
  top: 105px;
  right: 0px;
  width: 250px;
  text-align: right;
  z-index: 1;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #978536;
    border-radius: 5px;
    color: #978536;
    background-color: #18002B;
    box-shadow: inset 1px 1px 3px rgba(0,0,0,.5);
}

#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #978536;
    border-radius: 5px;
    padding: 2px 5px;
    font-size: 90%;
    font-weight: bold;
    color: #978536;
    background-color: #0000FF;
    background: #18002B;
    box-shadow: 0 1px 3px rgba(0,0,0,.5);
}

#top-bar {
  width: 100%;
  top: 160px;
  z-index: 20;

  display: flex;
  justify-content: center;
}

.top-bar {
  position: relative;
  margin: 0 auto;
}

.mobile-top-bar{
  position: relative;
  margin: 0 auto;
  margin-top: -1em;
}

#page-title {
    font-size: 2.1em;
    color: #4D4D00;
    font-weight: bold;
    font-family: 'Poiret One', '楷体', cursive;
}

#content-wrap {
    margin: 30px auto 0;
}

h1,h2,h3,h4,h5,h6 {
    color: A38800;
    font-weight: bolder;
    font-family: 'Poiret One', '楷体', cursive;
}

a:link {
    color: #797B20;
}

a:visited {
    color: #A6A600;
}

/* Side bar */

#side-bar {
    background: rgba(255, 255, 255, 0);
}

#side-bar .side-block,
#side-bar .side-block.media,
#side-bar .side-block.resources,
#interwiki .side-block {
    background: #18002B !important;
    border: 5px solid #A38800;
    border-radius: 10px 0px;
}

#side-bar .heading,
#interwiki .heading {
    color: #C4B73B;
    border-bottom: solid 3px #C4B73B;
}

div.open-menu a {
    color: #18002B!important;
    border-color: #18002B!important;
}

#side-bar div.menu-item.inactive a {
    color: #ffffff;
}

#side-bar div.menu-item .sub-text {
    color: #ffffff;
}

#side-bar a:link,
#interwiki a:link {
    color: #C4B73B;
}

#side-bar a:visited,
#interwiki a:visited {
    color: #FFF7D8;
}

/* side-bar collapsible stuff */
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #000000;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #000000;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #000000;
}

/* Rate Module */

.page-rate-widget-box {
    box-shadow: none;
    margin: unset;
}

div.page-rate-widget-box .rate-points {
    background-color: #18002B !important;
    color: #ffffff !important;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background: #ffffff;
    color: #18002B;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: #ffffff;
    color: #18002B;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #18002B;
    color: #ffffff;
}

.page-rate-widget-box .cancel {
    background: transparent;
    background-color: #18002B;
}

.page-rate-widget-box .cancel a {
    color: #ffffff;
}

.page-rate-widget-box .cancel a:hover {
    background: #ffffff;
    color: #18002B;
}

 .info-container .collapsible-block-content .wiki-content-table{
     width: 100%;
}

#page-content .creditRate{
     margin: unset;
}
#page-content .creditRate .creditButtonStandalone,
#page-content .rate-box-with-credit-button {
    background-color: #18002B;
    border: solid 1px #18002B;
    box-shadow: none;
}

#page-content .creditRate .creditButtonStandalone .fa-info,
#page-content .rate-box-with-credit-button .fa-info {
    border: none;
    color: #ffffff;
}

#page-content .creditRate .creditButtonStandalone .fa-info:hover,
#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #A38800;
    color: #18002B;
}

#page-content .creditRate .creditButtonStandalone p a {
    background: #18002B;
}

/* Tab */

div.yui-navset div.yui-content {
    background: #ffffff;
}

div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    background: #fefefe;
    color: #000000;
}

div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: rgb(232, 244, 255);
}

div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: rgb(32, 18, 77);
    color: #ffffff;
}

/* blockquote */

blockquote,
blockquote p,
div.blockquote,
div.blockquote p {
    background: #FFFEF7;
}

.scp-image-block {
    border: solid 1px #18002B;
    box-shadow: none;
}

.scp-image-block .scp-image-caption {
    background: #18002B;
    border-top: solid 1px #18002B;
    color: #FAF4C0;
}

@media (max-width: 499px) {
  #header h1 a::before {
    content: var(--short_title);
    font-size: 50px;
    padding-top: 80px;
  }
}

@media (max-width: 767px) {
  #content-wrap {
    margin: 43px auto 0;
  }
  #login-status {
    top: 25px;
  }
}

[[/code]]

[[/iftags]]
