@import url(https://scp-wiki.wikidot.com/theme:golden-feast/code/1);
@import url('https://fonts.googleapis.com/css?family=Poiret+One');
@import url('https://scpko.wdfiles.com/local--files/unfont/UnJamoNovel.css');

:root {
    --title: "Ambrose Restaurants";
    --short_title: "Ambrose";
}

/* Header */

#header h1 a {
    font-family: 'Poiret One', 'UnJamoNovel';
}

#page-title {
    font-family: 'Poiret One', 'UnJamoNovel';
}

h1,h2,h3,h4,h5,h6 {
    font-family: 'Poiret One', 'UnJamoNovel';
}
