[[code type="css"]]
/*
    Powered on 2021/05/08 12:36:31

    [2021 Wikidot Theme]

    Created by c_bonefish and Denevola
    Header Code adopted from The Buck Stops Here CSS Theme by thd-glasses
    Info Bar and Page Rating Code adopted from Technoblast Theme by EstrellaYoshte
    Logo by NAVLA

    CC BY-SA 3.0
*/

@import url('https://fonts.googleapis.com/css2?family=Do+Hyeon&display=swap');

/* Header */
div#container-wrap {
    background: url('https://scpko.wikidot.com/local--files/theme:golden-samdaecheon/background.png') center top repeat-x;
    background-size: 600px;
}

#header {
    height: 350px;
    background: none;
}

#header h1 {
    position: relative;
    float: none;
    clear: both;
    margin-left: 0;
    padding: 0;
    z-index: 1;
}

#header h1 a {
    width: 280px;
    margin: 0 auto;
    padding-top: 260px;
    color: transparent;
    font-size: 0;
}

#header h1 a::before {
    display: inline-block;
    position: absolute;
    top: 0;
    left: 50%;
    width: 100%;
    padding-top: 60px;
    color: #000000;
    font-family: 'Do Hyeon', sans-serif;
    font-size: 50px;
    letter-spacing: 60px;
    text-align: center;
    text-indent: 60px;
    text-shadow: 1px 0 #EDD200, -1px 0 #EDD200, 0 1px #EDD200, 0 -1px #EDD200, 1px 1px #EDD200, -1px 1px #EDD200, 1px -1px #EDD200, -1px -1px #EDD200; !important;
    transform: translateX(-50%);
    content: "삼대천";
}

#header h2 span {
    padding: 4px;
    font-size: 0;
}

#header h2::after {
    display: inline-block;
    position: absolute;
    top: 0;
    left: 50%;
    width: 100%;
    padding-top: 250px;
    color: #000000;
    font-family: 'Do Hyeon', sans-serif;
    font-size: 20px;
    font-weight: bolder;
    text-align: center;
    text-indent: 10px;
    text-shadow: 1px 0 #EDD200, -1px 0 #EDD200, 0 1px #EDD200, 0 -1px #EDD200, 1px 1px #EDD200, -1px 1px #EDD200, 1px -1px #EDD200, -1px -1px #EDD200; !important;
    letter-spacing: 10px;
    transform: translateX(-50%);
    content: "셋이 모여 천하를 든다";
}

#header-extra-div-1 {
    display: flex;
    position: absolute;
    top: 105px;
    left: 50%;
    width: 280px;
    height: 128px;
    background: url('https://scpko.wikidot.com/local--files/theme:golden-samdaecheon/logo.png') center top no-repeat;
    background-size: contain;
    transform: translateX(-50%);
    z-index: -1;
}

/* Login & Search & Top Bar */
#login-status {
    position: absolute;
    top: 40px;
    z-index: 2;
}

#login-status {
    top: 3px;
    color: #000000 !important;
    text-shadow: -1px 0 #EDD200, 0 1px #EDD200, 1px 0 #EDD200, 0 -1px #EDD200 !important;
}

#login-status a {
    color: #000000;
    font-weight: bolder;
    text-shadow: -1px 0 #EDD200, 0 1px #EDD200, 1px 0 #EDD200, 0 -1px #EDD200 !important;
}

#login-status ul a {
    color: #000000 !important;
}

#search-top-box {
    position: absolute;
    top: 31px;
    right: 0;
    width: 250px;
    text-align: right;
    z-index: 1;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: #3E6200;
    border: solid 1px #FFFFD3;
    border-radius: 5px;
    color: #FFFFD3;
}

#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    padding: 2px 5px;
    background: #3E6200;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
    border: solid 1px #FFFFD3;
    border-radius: 5px;
    color: #FFFFD3;
    font-size: 90%;
}

#top-bar {
    display: flex;
    justify-content: center;
    top: 324px;
    width: 100%;
    z-index: 20;
}

#top-bar a {
    color: #ffffff;
}

.top-bar {
    position: relative;
    margin: 0 auto;
}

/* Page Elements */
#page-title {
    color: #FFE131;
    font-family: 'Do Hyeon', sans-serif;
    font-size: 2.1em;
}

#content-wrap {
    margin: 30px auto 0;
}

h1, h2, h3, h4, h5, h6 {
    color: #FFE131;
}

body {
    background-color: #000000;
    color: #ffffff;
}

a:link {
    color: #FFE131;
}

a:visited {
    color: #F2FF9D;
}

.hovertip {
    background-color: #000000 !important;
    border: 2px solid #ffffff !important;
    color: #ffffff !important;
}

.scp-image-block {
    background: #000000;
    border: solid 1px #FFFFE5;
    box-shadow: none;
}

.scp-image-block .scp-image-caption {
    background: #FFFFC1;
    border-top: solid 1px #ffffff;
    color: #000000;
}

/* ---- SCROLLBAR ---- */
::-webkit-scrollbar {
    width: unset;
    height: unset;
}

::-webkit-scrollbar-track {
    background: #000000;
}

::-webkit-scrollbar-thumb {
    background: #ffffff;
    border: none;
}

::-webkit-scrollbar-thumb:hover {
    background: #FFFFC1;
}

textarea::-webkit-scrollbar-track {
    background: #000000;
}

/* Side Bar */
#side-bar {
    background: none;
}

#side-bar .side-block {
    background-color: #000000 !important;
    border: 2px solid #FFFFA1 !important;
    border-radius: 10px 0 !important;
}

#side-bar .heading,
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #ffffff;
    color: #ffffff;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link:hover,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #FAED7D;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #ffffff;
}

#side-bar .menu-item .image,
#side-bar .collapsible-block-folded,
#side-bar .collapsible-block-unfolded-link .collapsible-block-content .image {
    filter: hue-rotate(300deg);
}

#side-bar .collapsible-block-folded a:link {
    filter: hue-rotate(-300deg);
}

#side-bar a:link {
    color: #E8DB6B;
}

#side-bar a:visited {
    color: #FFFFB3;
}

#side-bar a:link:hover,
#side-bar a:visited:hover {
    color: #D6C959;
}

.scpnet-interwiki-frame {
    filter: invert(1) hue-rotate(245deg) saturate(.8);
}

div.open-menu a {
    border-color: #000000 !important;
    color: #000000 !important;
}

/* Tab */
div.yui-navset div.yui-content {
    background: #000000;
    color: #ffffff;
}

div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    background: #FFFFD3;
    color: #000000;
}

div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: #8CB37F;
}

div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: #2C5000;
    color: #ffffff;
}

/* Rate Module */
.page-rate-widget-box {
    margin: unset;
    box-shadow: none;
}

.page-rate-widget-box .rate-points {
    background-color: #EDD200 !important;
    color: #000000!important;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background: #ffffff;
    color: #000000;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: #ffffff;
    color: #000000;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #EDD200;
    color: #000000;
}

.page-rate-widget-box .cancel {
    background-color: #EDD200;
}

.page-rate-widget-box .cancel a {
    color: #000000;
}

.page-rate-widget-box .cancel a:hover {
    background: #000000;
    color: #EDD200;
}

.info-container .collapsible-block-content .wiki-content-table {
    width: 100%;
}

#page-content .creditRate {
    margin: unset;
}

#page-content .rate-box-with-credit-button {
    background-color: #EDD200;
    border: solid 1px #EDD200;
    box-shadow: none;
}

#page-content .rate-box-with-credit-button .fa-info {
    border: none;
    color: #000000;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #B2AC78;
    color: #EDD200;
}

/* ---- EDITING WINDOWS ---- */
.owindow {
    background-color: #000000;
    border-color: #FFFFD3;
}

.owindow .modal-header {
    background-color: #000000;
}

.owindow .modal-body img {
    background-color: #000000 !important;
}

.owindow .title {
    background-color: #000000;
}

.owindow a,
.owindow a:visited {
    color: #ffffff;
}

.owindow .active {
    color: #ffffff;
}

.owindow .content.modal-body img {
    background-color: #1A3E00 !important;
}

.owindow .button-bar a {
    background-color: #000000;
    color: #ffffff;
}

.owindow .button-bar a:hover,
.owindow .button-bar a:focus {
    background-color: #000000 !important;
    border-color: #FFFFF7;
    color: #ffffff;
}

table.page-files .highlight {
    background-color: #FFFFF7;
}

input.text,
input.text:focus,
input.text:hover {
    background-color: #000000 !important;
    border-color: #F2FF9D;
    color: #ffffff;
}

.wd-editor-toolbar-panel div {
    filter: hue-rotate(62deg) invert(83%);
}

textarea {
    background-color: #FFFFF7;
    color: #000000;
}

.change-textarea-size a {
    background-color: #000000;
    color: #ffffff;
}

#lock-info {
    background-color: #000000;
}

/* Blockquote */
blockquote,
blockquote p,
div.blockquote,
div.blockquote p {
    background-color: #000000;
    color: #ffffff;
}

/* etc. */
@media (max-width: 767px) {
    #header h1 a::before {
        font-size: 50px;
    }
}

@media (max-width: 479px) {
    #header h1 a::before {
        letter-spacing: 40px;
        text-indent: 40px;
    }

    #header h2::after {
        letter-spacing: 5px;
        text-indent: 5px;
    }
}
[[/code]]

[[/iftags]]
