[[module css]]
@media only screen and (max-width: 1040px) {
:root: {
    --header-title: "";
    --header-subtitle: "";
}
}

:root {
    --header-title: "快速、安全、放心。";
    --header-subtitle: "Gp快递物流有限公司";
    --header-logo: url("https://scpsandboxcn.wikidot.com/local--files/jr-reboot/New_gp_light.png");

    --basalt-primary-color: 254,254,254;
    --basalt-secondary-color: 247,248,250;
    --basalt-tertiary-color: 218,219,222;
    --basalt-main-text-color: 20,20,20;
    --basalt-overtone: 35,0,0;
    --basalt-undertone: 22,98,133;

    --basalt-bright-element-color: 0,117,169;
    --basalt-dark-element-color: 0,54,99;
    --basalt-alternate-color: 244,96,54;
    --basalt-positive-color: 76,159,112;
    --basalt-negative-color: 255,34,12;
    --basalt-cancel-color: var(--basalt-negative-color);
    --basalt-sub-text-color: 155,154,162;
    --basalt-light-text-color: var(--basalt-primary-color);

    --basalt-UI-dark-palette: var(--basalt-overtone);
    --basalt-focus-color: var(--basalt-undertone);
    --basalt-background-color: var(--basalt-primary-color);

    --header-title-color: var(--basalt-secondary-color);
    --header-background-color: var(--basalt-bright-element-color);
    --header-subtitle-color: var(--header-title-color);
    --top-bar-link-color: var(--header-title-color);
    --top-bar-dropdown-link-color: var(--basalt-main-text-color);
    --search-icon-color: var(--basalt-primary-color);
    --side-bar-button-color: var(--top-bar-link-color);
    --side-bar-button-color-hover: var(--top-bar-link-color-hover);


    --bottom-area-text-color: var(--basalt-secondary-color);
    --bottom-area-background-color: var(--basalt-dark-element-color);
    --footer-divider-color: var(--basalt-tertiary-color);
    --footer-link-color: 52,228,234;
    --license-area-logo:  var(--light-logo);
    --light-logo: url(https://scpsandboxcn.wikidot.com/local--files/jr-reboot/header_light.png);
    --license-area-divider-color: var(--basalt-tertiary-color);

    --bg-img: url(https://scpsandboxcn.wikidot.com/local--files/jr-reboot/world.png);
    --prologue: "快速、安全、放心。";
    --prologue-setting: visible;
    --prologue-author: "——Gp快递";
    --prologue-author-setting: visible;
    --display-mode: 86vh;

    --background-logo: var(--trans-logo);
    --trans-logo: url(https://scpsandboxcn.wikidot.com/local--files/jr-reboot/GpLogo-small-trans.png);
}

/* {$scp-takeover}/
:root {
    --header-title: "顺通畅达 包裹至上";
    --header-subtitle: "顺畅包裹物流有限公司";
    --header-logo: url("https://scpsandboxcn.wikidot.com/local--files/jr-reboot/ShunChang%20Package%20Banner%20Reverse.png");

    --trans-logo: url(https://scpsandboxcn.wikidot.com/local--files/jr-reboot/ShunChang%20Package%20Banner%20Logo%20Trans.png);
    --light-logo: url(https://scpsandboxcn.wikidot.com/local--files/jr-reboot/ShunChang%20Package%20Banner%20Logo%20Reverse.png);
    --bg-img: url(https://scpsandboxcn.wikidot.com/local--files/jr-reboot/world.png);

    --footer-link-color: 244, 44, 4;
    --basalt-bright-element-color: 232,75,72;
    --basalt-dark-element-color: 61,8,20;
    --basalt-undertone: 175,62,60;
}
/* */

body:not(#interwiki body)::before {
    content: " ";
    position: fixed;
    display: block;
    z-index: -2;

    top: 0;
    width: 100%;
    height: 100vh;
    background: var(--bg-img);
    background-size: cover;
    background-position: center;
}

#header h1 {
    margin: 0;
    padding: 0;
}

#header h1 a::before {
    width: 7vw !important;
    max-width: unset !important;
    background-position: left top;
    margin: 10;
    padding: 10;
}

#extrac-div-1::before {
    line-height: 1.5em;
    font-size: 1.5em;
    font-family: var(--mono-font-primary);
    font-weight: 400;

    content: var(--prologue);
    color: rgb(var(--secondary-color));
    visibility: var(--prologue-setting);
    display: block;

    text-align: center;
    transition: font-size 0.3s ease, background-size 0.3s ease;
}

#extrac-div-1:lang(cn)::before {
    font-family: var(--mono-font-fallback);
}

#extrac-div-1 {
    position: absolute;
    top: calc(var(--display-mode) / 2);
    left: 50%;
    transform: translateX(-50%);
    padding-left: 1em;
    padding-right: 1em;
    z-index: 2;
    pointer-events: var(--introduction-setting);
    width: 50%;

    display: flex;
    flex-direction: column;
}

#extrac-div-1::after {
    line-height: 1.5em;
    font-size: 1.25em;
    font-family: var(--mono-font-primary);

    content: var(--prologue-author);
    color: rgb(var(--secondary-color));
    visibility: var(--prologue-author-setting);
    display: block;

    text-align: right;
    transition: font-size 0.3s ease, background-size 0.3s ease;
}

#extrac-div-1:lang(cn)::after {
    font-family: var(--mono-font-fallback);
}

#content-wrap {
    position: relative;

    max-width: none;
    margin: 0;

    overflow: hidden; 
    top: var(--display-mode);

    background: linear-gradient(to bottom, transparent, rgb(var(--primary-color), 0.75) 15vh, rgb(var(--primary-color), 0.75));
}

@media only screen and (max-width: 1000px) {
:root {
    --display-mode: 43vh;
}
#header h1 a::before {
    width: 150vw !important;
}
}

@media (min-width: 1px) and (max-width: 1000px) {
  #extrac-div-1{
    width: 80%;
 }

 #page-title {
     font-size: 2.5em;
 }

 #top-bar {
   top: 1rem !important;
 }

 #top-bar li {
  margin-left: -0.6rem;
 }

#top-bar li a {
  padding: 1px 0.9em;
 }
}

#license-area, #footer {
  top: var(--display-mode);
  position: relative;
}

/* {$half-banner}/
:root {
  --display-mode: 43vh;
}
/* */

/* {$remove-banner}/
:root {
  --display-mode: 3em;
  --prologue-setting: hidden;
  --prologue-author-setting: hidden;
}

div#extrac-div-1 {
  pointer-events: none;
}

#footer {
  margin-top: 15px;
}

#license-area {
  top: 0;
}
/* */

:is(span.specialtext, span.st) {
    text-decoration: underline 2px;
    text-underline-offset: 3px;
}

.tablescp {
    --basalt-th-background-color: 150, 24, 43;
}
.tablegp {
    --basalt-th-background-color: var(--basalt-bright-element-color);
}

div.gp_memo {
    --memo-heading: "Gp快递提醒您";
    --memo-logo: url(https://scpsandboxcn.wikidot.com/local--files/jr-reboot/header_trans.png);
    --memo-color: 200, 240, 241;
}

div.blockquote {
    background-image: var(--background-logo);
    background-blend-mode: multiply;
    background-size: contain;
    background-position: center center;
    background-repeat: no-repeat;
}
div.notation {
    background-image: var(--background-logo);
    background-blend-mode: multiply;
    background-size: contain;
    background-position: center center;
    background-repeat: no-repeat;
}
div.jotting {
    background-image: var(--background-logo);
    background-blend-mode: multiply;
    background-size: contain;
    background-position: center center;
    background-repeat: no-repeat;
}
div.papernote {
    background-image: var(--background-logo);
    background-blend-mode: multiply;
    background-size: contain;
    background-position: center center;
    background-repeat: no-repeat;
}

div.scp_memo {
    --memo-heading: "顺畅包裹提醒您";
    --memo-logo: url(https://scpsandboxcn.wikidot.com/local--files/jr-reboot/ShunChang%20Package%20Banner%20Logo%20Trans.png);
    --memo-color: 236, 209, 209;
}
