@import url("https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css");

/* --基本样式↓-- */

a {
    color: #DFDFCF;
    text-decoration: none;
}
a:hover {
    color: #A1A191;
    text-decoration: none;
}
a:visited {
    color: #A1A191;
    text-decoration: none;
}
#main-content a {
    transition: .3s;
