h1 {
    color: #CAD1D1;
    font-family: 'FlyFlowerSong', serif;
}
body {
    color: #CAD1D1;
    background: #161615F5;
    font-weight: 100;
}
html {
    scroll-behavior: smooth;
}
#skrollr-body {
    width: 100%;
    overflow: hidden;/*解决移动端右侧溢出问题*/
}
div#container-wrap {
    background: none;
}
div#container-wrap::before {
    content: "";
    display: block;
    width: 100%;
    height: 100%;
    background: url("http://scpsandboxcn.wikidot.com/local--files/collab:mayca/灰日_背景") no-repeat 50% 50%/cover;
    -webkit-filter: blur(15px) opacity(0.8) brightness(0.8);
    filter: blur(15px) opacity(0.8) brightness(0.8);
    position: fixed;
    top: 0;
    z-index: -1;
}

/* --基本样式↑-- */


/* --版头样式↓-- */

#header {
    height: 150px;
    background: none;
}
#header::after {
    -webkit-filter: blur(5px) brightness(0);
    filter: blur(5px) brightness(0);
    z-index: -1;
}
#header::after, #header::before {
    content: "";
    display: block;
    width: 100px;
    height: 100px;
    background-image: var(--header-img);
    background-repeat: no-repeat;
    background-position: 50% 50%;
    background-size: 100% 100%;
    opacity: .9;
    position: absolute;
    top: 40px;
    left: 10px;
