/* --版头动画↓-- */

div#extra-div-1 {
    width: 100%;
    height: 150px;
    background: url("http://scpsandboxcn.wikidot.com/local--files/collab:mayca/灰日_中下") no-repeat 0 50%/100% 150px;
    overflow: hidden;
    position: absolute;
    top: 0;
}
div#extra-div-1::before, div#extra-div-1::after {
    content: "";
    display: block;
    width: 200%;
    height: 150px;
    position: absolute;
    top: 0;
}
div#extra-div-1::before {
    background: url("http://scpsandboxcn.wikidot.com/local--files/collab:mayca/灰日_上") repeat-x 0 50%/50% 150px;
    animation: moveBgAnim 120s linear infinite;
    z-index: -1;
}
div#extra-div-1::after {
    background: url("http://scpsandboxcn.wikidot.com/local--files/collab:mayca/灰日_沙尘") repeat-x 0 50%/50% 150px;
    animation: windBgAnim 8s linear infinite;
    z-index: 1;
}
@keyframes moveBgAnim {
    0% { transform: translateX(-0%) } 100% { transform: translateX(-50%) }
}
@keyframes windBgAnim {
