[[code type="CSS"]]
:root {
    --header-title: "GSK Project";
    --header-subtitle: "Supervise, Cooperation, Protect";     
    /* 这里的和上一行只有英文阅读者才能看到 */

    --dark-accent: 26, 26, 26;
    --pale-accent: 94, 219, 148;
    --bright-accent: 94, 219, 148;
    --light-gray-monochrome: 152, 154, 162;
    --dark-gray-monochrome: 26, 26, 26;
    --black-monochrome: 26, 26, 26;
    --medium-accent: 228, 228, 228;
    --tab-border-color: 94, 219, 148;
    --rating-module-button-color: 94, 219, 148;
    --rating-module-button-credit-color: 94, 219, 148;
    --swatch-background: var(--dark-accent);
    --swatch-menutxt-general-color: 94, 219, 148;
    --swatch-menubg-black-color: var(--dark-gray-monochrome);
    --barColour: var(--dark-accent);
    /* 主题颜色设置 */

     --header-height-on-desktop: 12.4rem;
     --header-height-on-mobile: 12rem;

    /* 这里是LOGO */
    --logo-image: url('https://scpsandboxcn.wikidot.com/local--files/eastcountry-center/site-logo.png');
    --logo-image-size: 200px auto;
    --lgurl: url('https://scpsandboxcn.wikidot.com/local--files/eastcountry-center/site-logo.png');
    --accentColor: 30,181,232;
    --ui-wght: 800;
    --ui-hvr-wght: 500;

    /* 背景和标题颜色 */
    --background-color: var(--dark-accent);

    /* 主要文本颜色 */
    --swatch-text-light: 228, 228, 228;
    --swatch-text-general: 228, 228, 228;

    /* 主菜单颜色 */
    --swatch-menubg-color: var(--dark-accent);

    /* 二级和三级项目的颜色，如 Blockquote 和 UI Tabs */
    --swatch-secondary-color: var(--swatch-menubg-dark-color);
    --swatch-tertiary-color: var(--swatch-menubg-medium-dark-color);
    --swatch-alternate-color: var(--swatch-menubg-medium-color);
    --sidebar-links-hover-text-color: var(--dark-accent);

    /* 辅助和第三项的文本颜色，例如侧边栏标题和顶栏菜单文本 */
    --swatch-text-tertiary-color: var(--swatch-menutxt-light-color);

    /* 主要标题颜色 */
    --swatch-topmenu-border-color: 94, 219, 148;
    --swatch-topmenu-bg-color: var(--swatch-menubg-extra-dark-color);

    --rating-module-text-color: 94, 219, 148;
    --rating-module-button-cancel-color: 94, 219, 148;
    ;

    --gradient-header: linear-gradient(to bottom,
            rgb(94, 219, 148) 0%,
            rgb(94, 219, 148) 90%,
            rgb(94, 219, 148) 100%);

    --diagonal-stripes: repeating-linear-gradient(45deg,
            hsla(0, 0%, 100%, 0),
            hsla(0, 0%, 100%, 0) 0.25vh,
            rgba(var(--swatch-menubg-extra-dark-color), 0.1) 0.35vh,
            rgba(var(--swatch-menubg-extra-dark-color), 0.2) 0.5vh);

  /* 侧边栏按钮 */
  --toggle-button-bg: ;
  --toggle-border-width: 0;
  --toggle-icon-color: rgb(94, 219, 148);
  --toggle-roundness: 0;
}



