    --body-font: 'Times New Roman', '黑体';
    --header-font: 'Times New Roman', '黑体';
    --title-font: 'Times New Roman', '黑体';
    --mono-font: 'Times New Roman', '黑体';
    --UI-font: 'Times New Roman', '黑体';

    --swatch-text-light: 228, 228, 228;
    --swatch-text-general: 228, 228, 228;
}
    /* ↑这里是页面标题（中文）↑ */


#extra-div-1 {
    position: absolute;
    z-index: -1;
    top: 0;
    left: 0;
    width: 100%;
    height: var(--header-height-on-desktop);
    pointer-events: none;
    background-image: url('https://scpsandboxcn.wikidot.com/local--files/eastcountry-center/hubble.png');
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
}
    /* ↑这里是替换banner图片↑ */

body {
    color: #e4e4e4;
}
blockquote {
    color: #e4e4e4
}
/* ↑正文颜色↑ */

.yui-navset .yui-nav .selected a {
    border-color: #e4e4e4;
    color: rgb(var(--dark-accent)) !important;
}
.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    background: rgb(var(--dark-accent));
    color: #e4e4e4;
}
.yui-navset-bottom .yui-nav li a,
.yui-navset-top .yui-nav li a,
.yui-navset .yui-nav li a {
    background: rgb(var(--dark-gray-monochrome));
    color: #e4e4e4;
}
/* ↑tab栏颜色↑ */

    a.footnoteref {
        padding: 0.05em;
        color: rgb(var(--swatch-primary));
        -webkit-box-decoration-break: clone;
        box-decoration-break: clone;
        box-shadow: inset 0 0 0 0rem rgba(var(--swatch-primary), 1);
        transition:
            box-shadow 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
            color 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
            padding 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
            margin 300ms cubic-bezier(0.4, 0.0, 0.2, 1);
    }
     a.footnoteref:hover {
        box-shadow: inset 0 -1.25em 0 0 rgba(var(--swatch-primary), 1);
        text-decoration: none;
        color: rgb(var(--black-monochrome))
    }
     .footnotes-footer a[href*="javascript"]::before,
    .bibitems .bibitem::after,
    .footnotes-footer {
        background-color: rgb(var(--black-monochrome));
    }
/* ↑脚注颜色↑ */

#page-content div.b-tables table.wiki-content-table td, #page-content div.b-tables table.wiki-content-table th, #page-content table.wiki-content-table td, #page-content table.wiki-content-table th {
    border: .0625rem solid rgb(80, 58, 246) !important;
}

#page-content div.b-tables table.wiki-content-table th, #page-content table.wiki-content-table th {
    background: rgb(80, 58, 246);
}
/* ↑表格颜色↑ */

    #header::before {
    background-image: var(--lgurl);
    background-size: 15rem;
    background-position-y: -2.5rem;
    opacity: 0.20;
    }
    #header h1,
    #header h1 a {
    },
        #header h1 a::before {
        opacity: 1;
    }
    #header h1 a::after {
        opacity: 1;
    }
    #header h2,
    #header h2 span{
    },
    #header h2 apan::before {
       opacity: 1;
    }
    #header h2 apan::after {
       opacity: 1;
    }
    /* ↑标题偏移↑ */

