[[code type="css"]]
@import url('https://scpko.wikidot.com/component:theme/code/1');

@font-face {
    font-family: '열사체';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/naverfont_07@1.0/Korea_hero.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

@font-face {
    font-family: '정선아리랑뿌리체';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2001@1.1/JSArirangPPURI-RegularA1.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

div#container-wrap {
     background: url('https://scpko.wdfiles.com/local--files/theme%3Ahalloween-koreana/body_1031.png') top left repeat-x;
}

/* HEADER */
 #header {
     height: 140px;
     position: relative;
     z-index: 10;
     padding-bottom: 22px;
    /* FOR MENU */
     background: url('https://scpko.wdfiles.com/local--files/theme%3Ahalloween-koreana/logo_1031.png') 10px 40px no-repeat;
}

#header h1 a {
    color: #b10000;
    font-family: '열사체', sans-serif;
}

#header h2 {
    color: #e5750a;
    font-family: '정선아리랑뿌리체', sans-serif;
}
[[/code]]

