/*
    Horn Theme
    [2021 Wikidot Theme]
    By hoah2333
    Based on:
      Anderson Robotics Theme by Croquembouche
      Paperstack Theme by EstrellaYoshte
*/
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@500&amp;amp;display=swap');

/* 页眉修改 */
#header {
    background: url(https://scpsandboxcn.wikidot.com/local--files/nmscxj/logo%20400x400-%E6%81%A2%E5%A4%8D%E7%9A%844.png) 15px 5px no-repeat;
    background-size: 455px 149px;
}
#header h1 a,
#header h2 span {
    color: transparent;
    text-shadow: none;
    font-family: Simhei, Arial, sans-serif;
}
#header h2 span {
    font-size: 110%;
}
@media (max-width: 707px) {
    #header {
        background-position: 10px 45px;
        background-size: 300px auto;
    }
    #header h1 a,
    #header h2 span {
        padding-left: 50px;
    }
}
div#container-wrap {
    background: url(https://scpsandboxcn.wikidot.com/local--files/nmscxj/%E7%89%88%E5%A4%B41.jpg) top left no-repeat, url(https://scpsandboxcn.wdfiles.com/local--files/nmscxj/%E7%89%88%E5%A4%B4%E8%83%8C%E6%99%AF-1.png) top left repeat-x;
    background-size: 100% 140px, auto auto;
}
#account-options {
    background-color: #2e5380;
    text-align: center;
    width: 4em;
}
#search-top-box {
    top: 35px;
    right: 8px;
}
#search-top-box-input {
    display: none;
}
#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background: #06162E;
    box-shadow: none;
    color: #EEFFFF;
    border-radius: 0;
}
#search-top-box-form input[type=submit],
#account-topbutton {
    border: 1px solid #EEFFFF;
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: 1px solid #FFFFFF;
}
#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    color: #CCFFFF;
    border-left: solid 1px transparent;
    border-right: solid 1px transparent;
    background-color: transparent;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background-color: black;
}
#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    color: #EEFFFF;
    background-color: #2F333C;
    border-top: solid 1px transparent;
}

/* 颜色替换 */
#account-options li a,
#account-options li a:hover,
#login-status,
#login-status a,
#side-bar a,
#side-bar a:visited,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link,
#side-bar .heading p,
#side-bar .menu-item,
#side-bar .side-block span {
    color: #EEFFFF;
}
#page-title,
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #06162E;
}
a,
a.newpage,
a:visited {
    color: #3296A0;
}

/* 侧边栏修改 */
#side-bar .heading,
#side-bar .collapsible-block-unfolded-link{
    border-bottom: solid 1px #EEFFFF;
}
#side-bar .side-block {
    border: #EEFFFF;
    border-radius: 0;
    box-shadow: 0 2px 6px #06162E;
    background: #2f333c !important;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #CCFFFF;
}
#side-bar img {
    opacity: 0;
}
#side-bar .media .menu-item img,
#side-bar .side-block a img {
    opacity: 1;
}
#side-bar .collapsible-block-folded {
    background: none;
}

/* 分页修改 */
.yui-navset .yui-content {
    border: none;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav,
.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
    border: none;
}
.yui-navset .yui-nav li a,
.yui-navset-top .yui-nav li a,
.yui-navset-bottom .yui-nav li a {
    border: 1px solid #2F333C;
    border-width: 1px 1px 0 1px;
    background: #EEFFFF;
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus,
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background: #2F333C;
    color: #EEFFFF;
    border: none;
}
.yui-navset .yui-content {
    background-color: #EEFFFF;
    color: #2F333C;
    border: 1px solid #2f333c;
}

/* 分页选择 by EstrellaYoshte */
.yui-navset .yui-nav,
 .yui-navset .yui-navset-top .yui-nav{
     display: flex;
     flex-wrap: wrap;
     width: 100%;
}
 .yui-navset .yui-nav li,
 .yui-navset .yui-navset-top .yui-nav li{
     position: relative;
     display: flex;
     flex-grow: 2;
     max-width: 100%;
     margin: 0;
     padding: 0;
}
 .yui-navset .yui-nav li a,
 .yui-navset-top .yui-nav li a,
 .yui-navset-bottom .yui-nav li a{
     display: flex;
     align-items: center;
     justify-content: center;
     width: 100%;
}
 .yui-navset .yui-nav a em,
 .yui-navset .yui-navset-top .yui-nav a em{
     padding: .35em .75em;
     text-overflow: ellipsis;
     overflow: hidden;
     white-space: nowrap;
}
 .yui-navset .yui-nav .selected,
 .yui-navset .yui-navset-top .yui-nav .selected{
     flex-grow: 2;
     margin: 0;
     padding: 0;
}

/* 分页动画 by Croquembouche */
.yui-navset .yui-content {
    display: flex;
    flex-direction: column;
    position: relative;
    transform-origin: 0 0;
}
.yui-navset .yui-content > div {
    display: block;
    top: 0;
    overflow: hidden;
    transform-origin: 0 0;
}
#page-content .yui-navset .yui-content > div > * {
    transform-origin: 0 0;
}
#page-content .yui-navset .yui-content > div[style*="none"] {
    display: block !important;
    flex: 0;
    max-height: 0;
    padding: 0 0.5em;
    border-width: 0;
    transition: padding 0s linear 0.5s,
        border-width 0s linear 0.5s,
        flex 0.5s cubic-bezier(.0,1.27,.0,.89) 0s;
    animation: tab-disappear 0.5s ease-in-out 0s 1 both;
}
#page-content .yui-navset .yui-content > div[style*="block"] {
    display: block !important;
    flex: 1;
    max-height: 9999rem;
    transition: padding 0s linear 0.5s,
        border-width 0s linear 0.5s,
        flex 0.5s cubic-bezier(.99,.0,.99,.0) 0.5s;
    animation: tab-appear 0.5s ease-in-out 0.5s 1 both;
}
@keyframes tab-disappear {
    0% { max-height: 9999rem; }
    1% { max-height: 100vh; }
    100% { max-height: 0; }
}
@keyframes tab-appear {
    0% { max-height: 0; }
    99% { max-height: 100vh; }
    100% { max-height: 9999rem; }
}

/* 表格修改 */
table.wiki-content-table {
    border-collapse: separate;
    border: 2px solid #21252E;
}
table.wiki-content-table th {
    border: 1px solid #EEFFFF;
    background-color: #21252E;
    color: #EEFFFF;
}
table.wiki-content-table td {
    border: 1px solid #EEFFFF;
    background-color: #5D6371;
    color: #EEFFFF;
}

/* 评分栏修改 */
.page-rate-widget-box .rate-points,
.page-rate-widget-box .cancel,
#page-content .rate-box-with-credit-button {
    background-color: #2F333C !important;
    border-radius: 0;
    border: solid 1px #2F333C;
    color: #EEFFFF;
    box-shadow: none;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #2F333C;
    border-top: solid 1px #2F333C;
    border-bottom: solid 1px #2F333C;
}
.page-rate-widget-box .rateup a, 
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
    color: #EEFFFF;
}
.page-rate-widget-box {
    border-radius: 0;
}
.creditRate .creditButton p a {
    color: #EEFFFF;
    border-left: solid 1px #EEFFFF
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover,
.page-rate-widget-box .cancel a:hover{
    color: #3498DB;
    background: #2F333C;
}

/* 杂项 */
.scp-image-block .scp-image-caption {
    background-color: #2F333C;
    color: #EEFFFF;
}
blockquote,
div.blockquote {
    background-color: #2F333C;
    color: #EEFFFF;
    border: 2px solid #2f333c;
    box-shadow: 2px 2px 0 #CCFFFF inset, -2px -2px 0 #CCFFFF inset;
}
blockquote hr,
div.blockquote hr {
    background-color: #CCFFFF;
}
.hovertip {
    border-color: #EEFFFF !important;
}
.hovertip .content {
    background-color: #2F333C;
    color: #EEFFFF;
}
[[/code]]
[[/collapsible]]
[[/iftags]]
[[div [[iftags -版式]]style="display: none"[[/iftags]]]]
