[[code type="css"]]
@charset "utf-8";
/*
    Tema de la Instalación-57
    [Tema Sigma-9 de 2019]
    [Hecho en la Rama Hispana (Ñ)]
    Diseñado y retocado a lo largo del tiempo por Dc_Yerko
*/

/* CUERPO */

body {
    background-color: #f0f0f0;
    color: #000000;
}

/* ENLACES */

a {
    color: #004fa0;
}

a:visited {
    color: #00356c;
}

a:new-page {
    color: #8791d9;
}

a.newpage {
    color: #005fc1;
}

/* FONDO DE CABECERA */

div#container-wrap {
    background: url(http://lafundacionscp.wdfiles.com/local--files/theme:i-57/opera.png) top left repeat-x;
}

/* LOGO DE CABECERA */

#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 30px; /* FOR MENU */
    background: url(http://lafundacionscp.wdfiles.com/local--files/theme:i-57/SCP-LOGO-RENDER-HD.png) 10px 35px no-repeat;
}

/* BARRA Y BOTÓN DE BÚSQUEDA */

#search-top-box-form input[type=submit] {
    border: 1px solid #D6D6D6;
    border-radius: 5px;
    padding: 2px 5px;
    font-size: 90%;
    font-weight: bold;
    color: #FFFFFF;
    background: linear-gradient(to bottom, #353535,#212121,#000000);
    box-shadow: 0 1px 3px rgba(0,0,0,.5);
    cursor: pointer;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #D6D6D6;
    color: #f3cf08;
    text-shadow: 0 0 1px rgba(255,255,255,.25);
    background: linear-gradient(to bottom, #353535,#212121,#000000);
    box-shadow: 0px 0px 5px 6px rgba(243,207,8,.2);
}

/* INICIO DE SESIÓN Y MI CUENTA */

#login-status {
    color: #dddddd;
    font-size: 90%;
    z-index: 30;
}

#login-status a {
    background: transparent;
    color: #FFF;
}

#login-status ul a {
    color: #000;
    background: transparent;
}

#account-topbutton {
    background: transparent;
    color: #f3cf08;
}

/* TÍTULO DE CABECERA */

#header h1 {
    margin-left: 120px;
    padding: 0;
    float: left;
    max-height: 95px;
}
#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}

#header h1 a {
    display: block;
    content: "57号设施";
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: 'Play', sans-serif; 'Open Sans', sans-serif;
    font-size: 180%;
    text-decoration: none;
    text-shadow: none;

}

#header h1 a::before {
    content: "57号设施";
    color: #DDBC05;
    text-shadow: 3px 3px 5px #242424;
}

@media (max-width: 767px) {
    #header h1 a::before {
    margin-left: -57px;
    font-size: 65%;
    text-shadow:
    -2px 0px 0px #111419,
    2px 0px 0px #111419,
    0px -2px 0px #111419,
    0px 2px 0px #111419;
    }
}

/* SUBTÍTULO DE CABECERA */

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: '在拉丁美洲地区的控制与收容行动';
    color: #708090;
    text-shadow: 1px 1px 1px #000000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

@media (max-width: 767px) {
#header h2 span::before {
    content: '在拉美地区的控制收容行动';
    margin-left: -57px;
    text-shadow:
    -2px 0px 0px #111419,
    2px 0px 0px #111419,
    0px -2px 0px #111419,
    0px 2px 0px #111419;
    }
}

/* BARRA SUPERIOR */

#top-bar a {
    color: #C0C0C0;
    background: transparent;
}

#top-bar ul li {
    border: 0;
    position: relative;
}

#top-bar ul li ul {
    border: solid 1px #2F4F4F;
    box-shadow: 0 2px 6px rgba(0,0,0,.5);
    border-top: 0;
}

#top-bar ul li a {
    border-left: solid 1px rgba(64,64,64,.1);
    border-right: solid 1px rgba(64,64,64,.1);
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #d6d6d6;
    color: #2F4F4F;
    border-left: solid 1px rgba(64,64,64,1);
    border-right: solid 1px rgba(64,64,64,1);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-width: 0;
    width: 150px;
    border-top: 1px solid #ddd;
    line-height: 160%;
    height: auto;
    max-height: none;
    padding-top: 0;
    padding-bottom: 0;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #C0C0C0;
    text-decoration: none;
}
#top-bar ul li ul {
    border-width: 0 1px 1px 1px;
    width: auto;
}
#top-bar ul li ul li, #top-bar ul li ul li.sfhover,
#top-bar ul li ul li, #top-bar ul li ul li:hover {
    border-width: 0;
}
#top-bar ul li ul li a {
    border-width: 0;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: #a01;
}

/* ASCENDENTES */

#breadcrumbs {
    margin-top: -1.5em;
    margin-right: 0;
    margin-bottom: 1em;
    font-weight: 85%;
    padding: 5px;
    font-size: 10px;
    border-top: 1px solid #000000;
}

#breadcrumbs a {
    color: #F3CF08;
    background-color: black;
    padding: 5px;
}

#breadcrumbs a:visited {
    color: #dabb0b;
    background-color: black;
    padding: 5px;
}

/* TÍTULOS */

h1, #page-title {
    color: #000000;
}

/* BARRA LATERAL */

#side-bar a {
    color: #F3CF08;
}

#side-bar a:visited {
    color: #F3CF08;
}

#side-bar a.newpage {
    color: #F3CF08;
}

#side-bar a.collapsible-block-link {
    color: #F3CF08;
}

#side-bar .side-block {
    border: 1px solid #D6D6D6;
    box-shadow: 0 2px 6px rgba(32,32,32,1);
    background: #000000;
}

#side-bar .side-block img {
    filter: hue-rotate(210deg) brightness(150%);
    -webkit-filter: hue-rotate(210deg) brightness(150%);
}

#side-bar .heading {
    color: #D6D6D6;
    border-bottom: solid 1px #D6D6D6;
}

#side-bar div.menu-item.inactive a {
    color: #D6D6D6;
}

#side-bar div.menu-item .sub-text {
    color: #D6D6D6;
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #D6D6D6;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    color: #D6D6D6;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #ddbc05;
    text-decoration: none;
}

#side-bar .side-block.media {
    border: 1px solid #D6D6D6;
    box-shadow: 0 2px 6px rgba(32,32,32,1);
    background: #000000;
}

#side-bar .side-block.media img {
    filter: none;
}

#side-bar .side-block.nuevos {
    border: 1px solid #D6D6D6;
    box-shadow: 0 2px 6px rgba(32,32,32,1);
    background: #000000;
}

#side-bar .side-block.guias {
    border: 1px solid #D6D6D6;
    box-shadow: 0 2px 6px rgba(32,32,32,1);
    background: #000000;
}

#side-bar .side-block.borradores {
    border: 1px solid #D6D6D6;
    box-shadow: 0 2px 6px rgba(32,32,32,1);
    background: #000000;
}

#side-bar .side-block.cc {
    border: 1px solid #D6D6D6;
    box-shadow: 0 2px 6px rgba(32,32,32,1);
    background: #000000;
}

#side-bar .side-block.cc img {
    filter: none;
}

#side-bar .side-block.recurso {
    border: 1px solid #D6D6D6;
    box-shadow: 0 2px 6px rgba(32,32,32,1);
    background: #000000;
}

/* FONDO DE LA BARRA LATERAL EN VISTA MÓVIL */

@media (max-width: 767px) {
    #side-bar {
    background-color: rgba(255,255,255,.1);
    }
}

/* BOTÓN DE LA BARRA LATERAL */

.open-menu a {
    border: 0.2em solid #000 !important;
    background-color: #F5F5F5 !important;
    color: #000 !important;
}

.open-menu a:hover {
    text-decoration: none !important;
    -webkit-box-shadow: 0px 0px 20px 3px rgba(243,207,8,1);
    -moz-box-shadow: 0px 0px 20px 3px rgba(243,207,8,1);
    -ms-box-shadow: 0px 0px 20px 3px rgba(243,207,8,1);
    -o-box-shadow: 0px 0px 20px 3px rgba(243,207,8,1);
    box-shadow: 0px 0px 20px 3px rgba(243,207,8,1);
}

/* INTERWIKI */

#interwiki .side-block {
    padding: 10px;
    border: 1px solid #D6D6D6;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(32,32,32,1);
    background: #000000;
    margin-bottom: 15px;
}

#interwiki .heading {
    color: #D6D6D6;
    border-bottom: solid 1px #D6D6D6;
    padding-left: 15px;
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}

#interwiki a,
#interwiki a:visited,
#interwiki a.newpage {
    color: #F3CF08;
}

#interwiki .menu-item img {
    filter: hue-rotate(210deg) brightness(150%);
    -webkit-filter: hue-rotate(210deg) brightness(150%);
}

/* PUNTUACIÓN */

.page-rate-widget-box {
    display: inline-block;
    border-radius: 5px;
    box-shadow: 1px 1px 3px rgba(0,0,0,.5);
    margin-bottom: 10px;
    margin-right: 2em;
}
div.page-rate-widget-box .rate-points {
    background-color: #000000;
    border: solid 1px #000000;
    border-right: 0;
    border-radius: 5px 0 0 5px;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: ffffff;
    border-top: solid 1px #000000;
    border-bottom: solid 1px #000000;
    font-weight: bold;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: 000000;
    color: #000000;
    padding: 0 4px;
    margin: 0 1px;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #000000;
    color: #ffffff;
    text-decoration: none;
}
.page-rate-widget-box .cancel {
    background-color: #000000;
    border: solid 1px #000000;
    border-left: 0;
    border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: #ffffff;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0 3px 3px 0;
    background: #000000;
    color: #ffffff;
    text-decoration: none;
}

/* PUNTUACIÓN CON INFO */

#page-content .rate-box-with-credit-button {
    background-color: #000;
    border: 1px solid #000;
    border-radius: 5px ;
    box-shadow: 1px 1px 3px rgba(0,0,0,.5);
}
#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: #ffffff;
}
.rate-box-with-credit-button .page-rate-widget-box .cancel {
    border-radius: 0;
}
.rate-box-with-credit-button .page-rate-widget-box .rate-points {
    border-left: 0;
}

#page-content .rate-box-with-credit-button .fa-info {
     border-color: #ffffff;
     color: #ffffff;
}
#page-content .rate-box-with-credit-button .fa-info:hover {
     color: #f7bb25;
}

.close-credits,
.credit-back {
    filter: hue-rotate(140deg);
}

#page-content .modalbox {
    box-shadow: 0 2px 6px rgba(0,30,0,.5);
}

/* BLOCKQUOTE */

blockquote,
div.blockquote {
    background-color: #e4e4e4;
}

/* IMAGE BLOCK */

.scp-image-block .scp-image-caption {
    background-color: #e1e1e1;
    border-top: solid 1 px #2F4F4F;
}

.scp-image-block .scp-image-caption > p {
    background-color: #e1e1e1;
    border: solid 1 px #2F4F4F;
}

/* TABLAS */

table.wiki-content-table th {
    border: 1px solid #888888;
    padding: 0.3em 0.7em;
    background-color: #000000;
    color: #ddbc05 !important;
}

/* TABS */

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #000000;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #000000;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background: #000000;
    color: white;
    border: 0px solid #000000;
    border-bottom: 2px solid #ddbc05;
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background: #484848;
    color: white;
    border-top: 0;
    border-left: 0;
    border-right: 0;
}

.yui-navset .yui-nav a:focus,
.yui-navset .yui-nav a:hover {
    background: #afafaf;
    border-bottom: 2px solid #d2c88f;
}

.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
    border: 0px;
}

/* JUSTIFICAR TABS */

 .yui-navset .yui-nav,
 .yui-navset .yui-navset-top .yui-nav{
     display: flex;
     flex-wrap: wrap;
     width: 100%;
     margin: 0 auto;
     box-shadow: none;
}
 .yui-navset .yui-nav a, /* ---- Link Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav a {
     border: unset;
     box-shadow: none;
}
 .yui-navset .yui-nav li, /* ---- Listitem Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav li {
     position: relative;
     display: flex;
     flex-grow: 2;
     max-width: 100%;
     margin: 0;
     padding: 0;
}
 .yui-navset .yui-nav li a,
 .yui-navset-top .yui-nav li a,
 .yui-navset-bottom .yui-nav li a{
     display: flex;
     align-items: center;
     justify-content: center;
     width: 100%;
}
 .yui-navset .yui-nav li em{
     border: unset;
}
 .yui-navset .yui-nav a:hover,
 .yui-navset .yui-nav a:focus{
}
 .yui-navset .yui-nav a em,
 .yui-navset .yui-navset-top .yui-nav a em{
     padding: .35em .75em;
     text-overflow: ellipsis;
     overflow: hidden;
     white-space: nowrap;
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
     display: flex;
     flex-grow: 2;
     max-width: 100%;
     align-items: center;
     justify-content: center;
}
.yui-navset .yui-nav .selected, .yui-navset .yui-navset-top .yui-nav .selected {
    margin: 0;
}

/* OWINDOW */

.owindow.owait .content {
    padding-bottom: 2rem;
    background-image: url("http://lafundacionscp.wdfiles.com/local--files/theme%3Ai-57/pig_loader.gif");
    background-repeat: no-repeat;
    background-position: bottom center;
    background-size: 57px;
    height: 40px;
}

.owindow.osuccess .content {
    padding-bottom: 2rem;
    background-image: url("http://lafundacionscp.wdfiles.com/local--files/theme%3Ai-57/pig_loaded.jpg");
    background-repeat: no-repeat;
    background-position: bottom center;
    background-size: 50px;
    height: 40px;
}

.owindow .title {
    color: #f3cf08;
    background-color: #000000;
    border: solid 1px #000000:
}

/* FOOTER */

#footer {
    background-color: #000;
}
[[/code]]
[[/collapsible]]
[[/iftags]]
