[[code type="CSS"]]
/*
  IAMEA BHL Theme
    Powered on 2021.12.1
    Created by Mishary
    https://scp-wiki.wikidot.com/theme:ijamea-bhl
    CC BY-SA 3.0
      Based on IJAMEA Theme Revised
        Powered on 2020/04/07 14:38:39
        Created by Nanigashi Sato
        Fixed by ukwhatn
        https://scp-wiki.wikidot.com/theme:revised-ijamea-theme
        CC BY-SA 3.0

  IJAMEA Logo
    Title: IJAMEA2.png
    Created by Nanimono Demonai
    Creation Year: 2019
    Source: https://scp-jp.wikidot.com/foundation-or-goi-logo
    Note: The background of this image was permeabilized by Nanigashi Sato.
    CC BY-SA 3.0
*/

/* Fonts */

@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@400;900&amp;family=Sawarabi+Mincho&amp;display=swap');

@import url('https://fonts.googleapis.com/css2?family=Abril+Fatface&amp;display=swap');

/* ROOT */

:root {
  --usukihada: 255,245,195;
  --kokibeni: 173,31,35;
  --kareno: 219,203,130;
  --shiro: 255,255,255;
  --asaginezu: 171,186,190;
  --kuro: 0,0,0;
  --bright-accent: var(--kokibeni);
  --medium-accent: var(--kokibeni);
  --dark-accent: var(--kareno);
  --logo-image: url(https://scp-wiki.wdfiles.com/local--files/theme%3Arevised-ijamea-theme/IJAMEA2.png);
  --header-title: "IJAMEA";
  --header-subtitle: "大日本帝国異常事例調査局";
  --header-h1-font-size: calc(var(--base-font-size)*3.5);
  --black-monochrome: var(--kuro);
  --white-monochrome: var(--shiro);
  --light-gray-monochrome: var(--kareno);
  --swatch-primary: var(--white-monochrome);
  --swatch-primary-darker: var(--black-monochrome);
  --swatch-primary-darkest: var(--black-monochrome);
  --swatch-menubg-light-color: var(--white-monochrome);
  --swatch-menubg-dark-color: var(--dark-accent);
  --swatch-text-secondary-color: var(--white-monochrome);
  --swatch-secondary-color: var(--white-monochrome);
  --ui-icon-color: var(--white-monochrome);
  --ui-button-hover-bg: var(--black-monochrome);
  --link-color: var(--swatch-text-general);
  --hover-link-color: var(--link-color);
  --newpage-color: var(--link-color);
  --header-gradient-color-bottom: var(--white-monochrome);
  --header-gradient-color-middle: var(--white-monochrome);
  --header-gradient-color-top: var(--white-monochrome);
  --body-font: Noto Serif SC, Inter, Verdana, Geneva, Helvetica Neue, Helvetica, Arial, sans-serif;
  --header-font: Abril Fatface,  Noto Serif sc, sans-serif;
  --title-font: var(--body-font);
  --UI-font: var(--body-font);
}

:root:lang(cn) {
  --header-title: "IJAMEA";
  --header-subtitle: "大日本帝国異常事例調査局";
  --body-font: Noto Serif SC, Inter, Verdana, Geneva, Helvetica Neue, Helvetica, Arial, sans-serif;
  --header-font: Abril Fatface,  Noto Serif sc, sans-serif;
  --title-font: var(--body-font);
  --UI-font: var(--body-font);
}

::-webkit-scrollbar-thumb {
    background: rgba(var(--swatch-tertiary-color),0.8);
}

div#container-wrap {
    background-image: linear-gradient(rgb(var(--shiro)) 15%, rgb(var(--kareno)) 15%, rgb(var(--kareno)) 30%, rgb(var(--kokibeni)) 30%, rgb(var(--kokibeni)) 43%, rgb(var(--shiro)) 43%, rgb(var(--shiro)) 55%, rgb(var(--kareno)) 55%, rgb(var(--kareno)) 80%, rgb(var(--shiro)) 80%);
}

#container-wrap-wrap {
    background-image:
      repeating-linear-gradient(180deg,
        hsla(0, 0%, 100%, 0),
        hsla(0, 0%, 100%, 0) 0.25vh,
        rgba(88, 88, 88, 0.1) 0.35vh,
        rgba(88, 88, 88, 0.2) 0.5vh),
       linear-gradient(to bottom, white 0, white 100%);
    background-repeat: no-repeat;
    background-size:
        100% var(--header-height-on-desktop),
        100% var(--final-header-height-on-desktop);
}

body {
    background: #FFF5C3;
}
::selection {
    background: rgb(var(--bright-accent));
    color: rgb(var(--swatch-text-secondary-color));
}

#header{
    background-position: 1rem 0;
    background-size: auto calc(var(--header-height-on-desktop));
    --swatch-headerh1-color: var(--swatch-text-general);
    --swatch-headerh2-color: var(--swatch-text-general);
    --login-line-divider-color: var(--swatch-text-general);
    --login-username-color: var(--swatch-text-general);
    --login-myaccount-color: var(--swatch-text-general);
    --login-arrow-color: var(--swatch-primary-darkest);
    --login-dropdown-text-color: var(--swatch-text-general);
    --login-dropdown-bg-hover-color: var(--black-monochrome);
    --search-icon-focus-bg-color: var(--swatch-primary-darkest);
}
#header h1 a {
    margin-top: calc(var(--header-height-on-desktop)/2 - 0.9em);
}

div#header h1 a:before {
    text-shadow: 1.75px 1.75px white;
}
#header h2 span {
    margin-top: calc(var(--header-height-on-desktop)/2 + var(--header-h1-font-size)/2 - var(--header-h2-font-size) + 0.4em);
    margin-left: var(--header-height-on-desktop);
}
#header h2 span:before {
    direction: rtl;
    unicode-bidi: bidi-override;
    font-weight: bold;
}

#top-bar{
    --topmenu-category-color: var(--swatch-text-general);
    --topmenu-category-hover-color: var(--swatch-text-general);
    --topmenu-hover-border-color: var(--bright-accent);
    --mobile-topmenu-sidebar-button-color: var(--bright-accent);
}

#header div[class*=top-bar]>ul li:active, #header div[class*=top-bar]>ul li:hover{
    background-color: rgba(var(--dark-accent), 0.5);
}

#header div[class*=top-bar]>ul>li>ul li:active>a, #header div[class*=top-bar]>ul>li>ul li:hover>a{
    background: rgb(var(--bright-accent));
    color: rgb(var(--swatch-text-secondary-color));
}

@media only screen and (max-width: 56.25rem){
  div.mobile-top-bar {
    background: rgb(var(--white-monochrome));
  }
}

#side-bar,
.side-block {
    --sidebar-bg-color: var(--usukihada);
    --sideblock-heading-border-color: var(--dark-accent);
    --sidebar-links-hover-text-color: var(--swatch-text-general);
    --sidebar-collapsible-fld-link-hover-bg: var(--dark-accent);
    --sidebar-collapsible-fld-link-hover-color: var(--swatch-text-general);
    --sidebar-subtest-color: var(--swatch-text-general);
}

#main-content{
    --tabs-selected-bg: var(--asaginezu);
    --tabs-selected-outline: var(--asaginezu);
    --tabs-bottom-border-color: rgb(var(--black-monochrome));
    --tabs-content-bg-color: var(--swatch-secondary-color), 1;
    --tables-header-txt: var(--swatch-secondary-color);
    --ui-icon-color: var(--swatch-text-secondary-color);
}

#page-content .printuser a {
    text-decoration: none;
}

#page-content div.creditRate a,
#page-content div.page-rate-widget-box a,
#u-credit-view a{
    text-decoration: none;
}

#page-content a {
    text-decoration: underline;
    text-decoration-color: rgb(var(--link-color));
}
#page-content a:hover {
    text-decoration-thickness: 2px;
}

#page-content .yui-nav a {
    text-decoration: none;
}

.yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav{
     border-bottom: solid 5px rgb(var(--swatch-primary-darker));
}
.yui-navset .yui-nav a:hover{
    color: rgb(var(--swatch-text-secondary-color));
}

input[type=radio]:checked:before{
    background: rgb(var(--swatch-primary-darker));
}

div[id*=page-options-bottom]>a:active:after, div[id*=page-options-bottom]>a:hover:after{
    background-color: rgb(var(--bright-accent));
}

#footer {
    --footer-bg-color: var(--dark-accent);
    --footer-link-hover-bg-color: var(--dark-accent);
}

#license-area {
    --license-bg-color: var(--dark-accent);
    --license-link-hover-bg-color: var(--dark-accent);
}

/* General's Note */

.generals-note{
  margin: -7.5px 0 1.5em 1em;
}

[[/code]]
[[/div]]
