[[module css]]

/* Immrama Theme              */
/* [2025 Wikidot Theme]       */
/* by Arthcymro               */

/*-----------------*/
/*---- IMPORTS ----*/
/*-----------------*/

@import url('https://fonts.googleapis.com/css2?family=Benne&display=swap');

:root {

    /*----------------------*/
    /*---- BEDROCK VARS ----*/
    /*----------------------*/

    --header-font-primary: 'Benne';
    --UI-font-primary: 'Benne';
    --body-font-primary: 'Benne';
    font-size: 110%;

    --logo: url(https://scp-wiki.wdfiles.com/local--files/theme%3Aimmrama/Immrama%20-%20Triskelion%20Header%20%28Blue%29.png);
    --title: "Immrama";
    --subtitle: "It's a long story";
    --title-size: 1.75rem;
    --subtitle-size: 1.25rem;

    --basalt-primary-color: 227, 226, 218;
    --basalt-secondary-color: 200,199,184;
    --basalt-tertiary-color: 173, 171, 149;
    --basalt-main-text-color: 20, 20, 20;
    --basalt-overtone: 50, 50, 50;
    --basalt-undertone: 28, 69, 45;
    --basalt-bright-element-color: 66, 101, 68;
    --basalt-dark-element-color: 28, 69, 45;
    --basalt-alternate-color: 166, 103, 54;
    --basalt-positive-color: 0, 100, 0;
    --basalt-negative-color: 109, 17, 17;
    --side-bar-link-background-hover: 28, 69, 45;
    --general-border-color: 20, 47, 33;
    --document-background-color: 243,223,193;
    --link-color: 36, 107, 206;
    --link-visited-color: 28, 84, 163;

/*-------------------------*/
/*---- IMMRAMA CLASSES ----*/
/*-------------------------*/

body {  
    background-image: linear-gradient(#e3e2da, #A6ABB1 100%);
}
.footnotes-footer .footnote-footer>a:first-child {
    font-family: var(--body-font);
}
div.papernote {
    display: block;
    margin-inline: auto;
    margin-block: 1.4rem;
    padding: 1rem 2rem;
    background-color: rgb(var(--basalt-secondary-color));
    color: rgb(var(--basalt-main-text-color));
}
div.papernote {
    display: block;
    margin-inline: auto;
    margin-block: 1.4rem;
    padding: 1rem 2rem;
    background-color: rgb(var(--document-background-color));
    color: rgb(var(--basalt-main-text-color));
}
div.document {
    background-color: rgb(var(--document-background-color));
    box-shadow: 0px 1px 1.9px -4px rgba(0, 0, 0, 0.1), 0px 6px 15px -4px rgba(0, 0, 0, 0.2);
    color: rgb(var(--basalt-main-text-color));
}
hr {
    border-top: none;
    position: relative;
    height: 0.35rem;
    background: rgba(0, 0, 0, 0);
    display: flex;
}
hr::before {
    content: " ";
    position: absolute;
    top: 0;
    left: 0;
    border-top: none;
    width: 100%;
    height: 0.125rem;
    background: #246bce;
    background-position: center center;
}
.imm-email {
    border: solid 2px #000000; 
    width: 88%; 
    padding: 1px 15px; 
    margin: 10px; 
    box-shadow: 0 1px 3px rgba(0,0,0,.5); 
    background-color: rgb(var(--basalt-primary-color))
}
.imm-email-example a.collapsible-block-link {
    font-weight: bold;
}
.imm-tofrom {
    margin-left: 10px; 
    margin-top: 5px; 
    padding: 1px 15px; 
    border-left: solid 3px #246bce
}
.yui-navset.yui-navset-top>ul.yui-nav {
    font-family: var(--header-font);
}
.yui-navset.yui-navset-top .yui-content {
    background-color: rgb(var(--basalt-primary-color));
}
:is(:where(#page-content) table:not(.form),table.wiki-content-table) td {
    border: solid .05rem rgb(var(--general-border-color));
    padding: .5em .625em;
    background-color: rgb(var(--basalt-primary-color));
} 
#header h1 a span {
    font-variant: small-caps;
}
.triskhr {
    min-height: 9em;
    background-image: url(https://scp-wiki.wdfiles.com/local--files/theme%3Aimmrama/Immrama%20-%20Line.png);
    background-size: 600px;
@media only screen and (max-width: 600px) {
    background-size: 360px;
}
    background-repeat: no-repeat;
    background-position: center;
}
.blood-triskhr {
    min-height: 9em;
    background-image: url(https://scp-wiki.wdfiles.com/local--files/theme%3Aimmrama/Immrama%20-%20Line%20%28Red%29.png);
    background-size: 600px;
@media only screen and (max-width: 600px) {
    background-size: 360px;
}
    background-repeat: no-repeat;
    background-position: center;
}
.smalltriskhr {
    min-height: 5.5em;
    background-image: url(https://scp-wiki.wdfiles.com/local--files/theme%3Aimmrama/Immrama%20-%20Line.png);
    background-size: 350px;
@media only screen and (max-width: 600px) {
    background-size: 210px;
}
    background-repeat: no-repeat;
    background-position: center;
}
.blood-smalltriskhr {
    min-height: 5.5em;
    background-image: url(https://scp-wiki.wdfiles.com/local--files/theme%3Aimmrama/Immrama%20-%20Line%20%28Red%29.png);
    background-size: 350px;
@media only screen and (max-width: 600px) {
    background-size: 210px;
}
    background-repeat: no-repeat;
    background-position: center;
}

[[/module]]

[[iftags +theme]]

[[=]]
[[module Rate]]
[[/=]]

