div#search-top-box form input[type="submit"] {
    display: none
}

#search-top-box-input {
    border: solid 1px var(--background);
    border-radius: 5px
}

#login-status {
    top: 2px
}

#login-status {
    display: flex;
    flex-direction: column;
    align-content: space-around;
    font-size: 0
}

#login-status .printuser {
    margin: 0;
    background-color: none;
    position: absolute;
    top: 13px;
    right: 0px;
    width: 28px;
    padding: 0;
}

#login-status .printuser img.small {
    background: none !important;
    margin: 0;
    width: 28px;
    height: 28px;
    padding: 0;
    border-radius: 100px;
    box-shadow: 0 0 3px #3f3f3f;
}

#login-status [id] {
    font-size: .72rem
}

#login-status>a[href="javascript:;"] {
    font-size: 0;
    font-weight: bold;
    margin: 0;
    text-align: center;
    text-decoration: none;
    background: none;
    width: 21.8px;
    position: absolute;
    top: 13px;
    height: 21px;
    right: 30px;
    border: none;
}

#login-status>a[href="javascript:;"]:before {
    display: inline-block;
    font-family: FontAwesome;
    font-size: 1.3rem;
    margin: 4px 3px;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    transform: translate(0, 0);
}

#login-status #account-topbutton:before {
    content: "\f013";
    color: var(--base-color)
}

#login-status .login-status-sign-in {
    right: 35px !important;
}

#login-status .login-status-sign-in:before {
    content: "\f13e";
    color: var(--base-color)
}

#login-status .login-status-create-account {
    right: 11px !important;
    margin: 0 0 .25rem;
}

div#login-status a.login-status-create-account {
    right: 11px;
}

#login-status .login-status-create-account:before {
    content: "\f067";
    color: var(--base-color)
}

#login-status #my-account {
    display: none
}

#login-status a strong {
    position: absolute;
    top: 0;
    right: 2.25rem;
    font-family: var(--bauhaus);
    font-size: 1rem;
    var(--lightColor)-space: nowrap
}

#login-status #account-topbutton:not(:focus):not(:focus-within):not(:hover):not(:active)+#account-options {
    display: none !important
}

#header #login-status #account-topbutton+#account-options:focus,
#header #login-status #account-topbutton+#account-options:focus-within,
#header #login-status #account-topbutton+#account-options:hover,
#header #login-status #account-topbutton+#account-options:active {
    display: block !important
}

#account-options ul {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-around
}

#account-options ul>li {
    flex-grow: 1;
    text-align: center;
    width: 40%
}

#account-options ul>li a {
    transition: all .25s ease-in-out
}

#account-options ul>li a:hover {
    background-color: var(--base-color);
    color: var(--background);
    text-decoration: none
}

#login-status ul a {
    color: #000
}

/* Sidebar组件 */
/* source: https://ah-sandbox.wikidot.com/component:collapsible-sidebar-x1 */

#top-bar .open-menu a {
        position: fixed;
        top: 0.5em;
        left: 0.5em;
        z-index: 5;
        font-family: 'Nanum Gothic', san-serif;
        font-size: 30px;
        font-weight: 700;
        width: 30px;
        height: 30px;
        line-height: 0.9em;
        text-align: center;
        border: transparent;
        background-color: transparent;
        border-radius: 0em;
        color: var(--lightColor);
        pointer-events: auto;
}

@media not all and (max-width: 767px) {

    #top-bar .mobile-top-bar {
        display: block;
        pointer-events: none;
    }

    #top-bar .mobile-top-bar li {
        display: none;
    }

    #main-content {
        max-width: 44.5rem;
        margin: 0 auto;
        padding: 0;
        transition: max-width 0.2s ease-in-out;
    }

    #side-bar {
        display: block;
        position: fixed;
        top: 0;
        left: -18rem;
        width: 15.25rem;
        height: 100%;
        margin: 0;
        overflow-x: hidden;
        overflow-y: auto;
        z-index: 10;
        padding: 1em 1em 0 1em;
        background-color: rgba(0,0,0,0.1);
        transition: left 0.4s ease-in-out;
    }

    #side-bar:target {
        top: 3em;
        left: 0;
    }
    #side-bar:focus-within {
        left: 0;
    }

    #side-bar:target .close-menu {
        display: block;
        position: fixed;
        width: 100%;
        height: 100%;
        top: 0;
        left: 0;
        margin-left: 17rem;
        opacity: 0;
        z-index: -1;
        visibility: visible;
    }
    #side-bar:not(:target) .close-menu { display: none; }

    #top-bar .open-menu a:hover {
        text-decoration: none;
    }
}

@media (max-width: 767px) {
    #side-bar {
        background-color: rgba(34, 32, 46, 0.75);
    }
}
