[[module css]]
@import url("https://bhl.scpwikicn.com/css/min/normalize.min.css");
@import url("https://bhl.scpwikicn.com/css/min/black-highlighter.min.css");
[[/module]]
[[include :scp-wiki:component:toggle-sidebar-bhl]]
[[include :scp-wiki:component:centered-header-bhl]]
[[include :scp-wiki:component:bhl-dark-sidebar]]
[[module css]]
:root {
    --logo-image: url("http://o5command-int.wdfiles.com/local--files/tech-team:graphic-templates/scp-logo-int-full.svg");
    --header-title: "SCP基金会";
    --header-subtitle: "国际翻译站";
    --swatch-int-blue: 52, 76, 105;
    --swatch-int-white: 201, 208, 216;
    --header-gradient-color-bottom: var(--swatch-int-blue);
    --rating-module-bg-color: var(--swatch-int-white);
    --ct_col_rate-primary : var(--swatch-int-white);
}

:root:lang(cn) {
    --header-title: "SCP基金会";
    --header-subtitle: "国际翻译站";
[[/module]]
