[[code type="css"]]
/*
	Isolated Terminal Theme CSS (ja-JP modification) by oplax-counterpoint (@merliborn)
*/
@import url('https://scp-jp-storage.wdfiles.com/local--files/file%3A9042763-2-mfzo/JFDotShinonomeMing16.css');
@import url('https://scp-jp-storage.wdfiles.com/local--files/file%3A9042763-3-ci8u/profontwindows.css');
@import url('https://scp-jp-storage.wdfiles.com/local--files/file%3A9042763-5-t5lr/terminus.css');
@import url('https://scp-jp-storage.wdfiles.com/local--files/file%3A9042763-6-aeo3/jiskan24.css');


/* Noto Sans JP */
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@100..900&display=swap');

:root {
	/* S-CSS-P integrations are removed because it's unavailable in scp-jp. */

	/* header measurements */
	--header-height-on-desktop: 12.4rem;
	--header-height-on-mobile: 12rem;
	--header-h1-font-size: calc(var(--base-font-size) * 3);

	/* Typefaces */
	--body-font: 'Terminus', 'Noto Sans JP', sans-serif;
	--header-font: 'JSDot Shinonome Ming 16', sans-serif;
	--title-font: 'JSDot Shinonome Ming 16', sans-serif;
	--mono-font: 'profontwindowsregular', monospace;
	--UI-font: 'profontwindowsregular', 'jiskan24h', monospace;
	--base-font-size: 1.1rem;

	/* null logo */
	--logo-image: none;

	/* change title */
	--header-title: "SCP FOUNDATION";

	/* Header Gradients */
	--diagonal-stripes: initial;


	/*----*/

	--accentColor: 246, 196, 3;
	--darkColor: 40, 26, 8;


	--bright-accent: var(--accentColor);
	--dark-accent: var(--accentColor);
	--pale-accent: var(--accentColor);
	--medium-accent: var(--accentColor);

	--black-monochrome: var(--accentColor);
	--dark-gray-monochrome: var(--darkColor);
	--gray-monochrome: var(--darkColor);
	--light-pale-gray-monochrome: var(--darkColor);
	--pale-gray-monochrome: var(--accentColor);
	--very-light-gray-monochrome: var(--darkColor);
	--white-monochrome: var(--darkColor);

	--rating-module-bg-color: var(--pale-gray-monochrome);
	--rating-module-button-color: var(--white-monochrome);
	--rating-module-button-credit-color: var(--white-monochrome);
	--rating-module-text-color: var(--white-monochrome);
	--rating-module-button-cancel-color: var(--gray-monochrome);
	--rating-module-text-hover-color: var(--pale-gray-monochrome);

	--swatch-headerh1-color: var(--black-monochrome);

	--swatch-menubg-medium-color: var(--bright-accent);
	--swatch-menubg-light-color: var(--gray-monochrome);
	--swatch-menutxt-light-color: var(--black-monochrome);
	--swatch-menubg-medium-dark-color: var(--black-monochrome);

	--swatch-primary-darker: var(--bright-accent);
	--swatch-primary-darkest: var(--pale-gray-monochrome);
	--swatch-text-secondary-color: var(--white-monochrome);
	--swatch-text-light: var(--bright-accent);
	--swatch-topmenu-bg-color: var(--bright-accent);

	--gradient-header: none;
	--gradient-background: rgb(var(--gray-monochrome));

	/* Desktop Sidebar button */
	--toggle-button-bg: rgb(var(--swatch-menubg-light-color));
	--toggle-border-width: 0;
	--toggle-icon-color: rgb(var(--swatch-primary-darkest));
	--toggle-roundness: 0;
}

/* mobile menu mark patch */
#header div[class*="top-bar"]>div.open-menu>p>a {
	font-family: 'Noto Sans JP', sans-serif;
}

/* credit view patch */
@media (max-width:479px) {
	div.modalbox {
	 font-size: 90%;
	}
	#u-credit-view div.credit {
	 margin: 2em 1ch;
	}
}
#u-credit-view div.creditBottomRate {
	padding-top: 0px;
}
#u-credit-view div.credit-license {
	height: unset;
	position: fixed;
}
#u-credit-view div.credit-license a {
	padding: 0px min(2px, 0.08em);
	font-size: unset;
}
#u-credit-view div.creditBottomRate > div:not(.credit-license) {
	position: relative;
	top: 0px;
}
#u-credit-otherwise .otherwise .credit-back {
	display: none;
}
#u-credit-otherwise div.credit.otherwise {
	padding-bottom: 1em;
}
#u-credit-otherwise .modalbox-title {
	margin-bottom: 14px;
}
/* ------------------------------- */

/* header title adjustments */
#header::before {
	opacity: 1;
}

/* change logo opacity */
#header {
	-webkit-filter: none;
	filter: none;
}

#header h1,
#header h1 a,
#header h1 a::before {
	text-shadow: none;
	letter-spacing: 0.1rem;
	align-self: flex-end;
}

#header h1 a::before {
	height: 0.5em;
	-webkit-text-stroke: 0;
}

#header h1 a::after,
#header h1 a span,
#header h2 {
	display: none;
}

/* svg logo mask */
