[[code type="css"]]
/*
    IT Pillar Of Industry CSS Theme
    [2022 Wikidot Theme]

    Created by c_bonefish
    Base Code by Denevola
    Logo by 035SL

    CC BY-SA 3.0
*/


/* Header */
div#container-wrap {
    background: url('https://scpko.wikidot.com/local--files/theme:it-pillar-of-industry/kwangheung-back.png') center top repeat-x;
    background-size: 14.5px;
}

#header {
    height: 200px;
    background-image: url('https://scpko.wikidot.com/local--files/theme:it-pillar-of-industry/kwangheung-logo3.png');
    position: relative;
    background-repeat: no-repeat;
    background-position: 1em 0em;
    background-size: 456px auto;
}

#header h1 a {
    position: absolute;
    font-size: 100%;
    text-align: center;
    text-shadow: none;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
}

#header h1 a::before {
    content: " ";
    color: #00000000;
    width: 100%;
    height: 100%;
}

#header h2,
#header h1 a span,
#header h2 span {
    display: none;
}

/* Login & Search & Top Bar */
#login-status {
    position: absolute;
    top: 3px;
    color: #fff;
    z-index: 2;
}

#login-status a {
    color: #fff;
    font-weight: bolder;
}

#account-topbutton {
    border-color: #fff;
}

#login-status ul a {
    color: #000000 !important;
}

#search-top-box {
    position: absolute;
    top: 30px;
    right: 0;
    width: 250px;
    text-align: right;
    z-index: 1;
}

#search-top-box-form {
    display: inline-flex;
}

#search-top-box-form > input {
    -webkit-appearance: none;
    -webkit-border-radius: 0;
    border-radius: 5px;
}

#search-top-box-input {
    background-color: #14306A;
    border: solid 1px #002060;
    color: #000;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    box-shadow: inset 1px 1px 5px rgb(0 0 0 / 50%);
    color: #000000 !important;
}

#search-top-box-form input[type=submit] {
    padding: 2px 5px;
    background: #002060;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
    border: solid 1px #14306A;
    color: #E8D9FF;
    font-size: 90%;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background: #002060;
}

#top-bar {
    display: flex;
    justify-content: center;
    top: 164px;
    width: 100%;
    z-index: 20;
}

#top-bar a {
    color: #F2F3F7;
    background: none;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #F2F3F7;
    border-color: transparent;
    color: #002060;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #F2F3F7;
}

#top-bar ul li ul,
#top-bar ul li ul li ul {
    border-color: rgba(0, 0, 0, .4);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-color: rgba(0, 0, 0, .1);
}

#top-bar ul li.sfhover ul li ul a,
#top-bar ul li:hover ul li ul a {
    border-top: none;
}

.top-bar {
    position: relative;
    margin: 0 auto;
}
.c_topbar_arrow {
    color: #BDBFC1;
}

/* Page Elements */
#page-title,
.meta-title {
    color: #1F2735;
    font-size: 2.1em;
    font-weight: bold;
}

#content-wrap {
    margin: 30px auto 0;
}

h1, h2, h3, h4, h5, h6 {
    color: #1F2735;
    font-weight: bold;
}

body {
    background-color: #E8EAEF;
    color: #000000;
}

a:link {
    color: #2C3749;
}

a:visited {
    color: #3F4F68;
}

.hovertip {
    background-color: #EFF1F4 !important;
    border: 2px solid #000 !important;
    color: #000 !important;
}

.scp-image-block {
    background: #EFF1F4;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
}

.scp-image-block .scp-image-caption {
    background: #EFF1F4;
    border-top: none;
    font-size: 85%;
}

.scp-image-block img {
    box-shadow: 0 0 1px black;
}

/* ---- SCROLLBAR ---- */
::-webkit-scrollbar {
    width: unset;
    height: unset;
}

::-webkit-scrollbar-track {
    background: #E8EAEF;
}

::-webkit-scrollbar-thumb {
    background: #002060;
    border: none;
}

::-webkit-scrollbar-thumb:hover {
    background: #2C416B;
}

textarea::-webkit-scrollbar-track {
    background: #E8EAEF;
}

/* Side Bar */
#side-bar {
    background: none;
}

#side-bar .side-block,
#side-bar .side-block.media,
#side-bar .side-block.resources,
#interwiki .side-block {
    background: #E8EAEF !important;
    border: 2px solid #001849;
    border-radius: 0 0;
}

#side-bar .heading,
#interwiki .heading,
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #001849;
    color: #2C3749;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link:hover,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #41516B;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #2C3749;
}

#side-bar .menu-item .image,
#interwiki .menu-item .image,
#side-bar .collapsible-block-folded,
#side-bar .collapsible-block-unfolded-link .collapsible-block-content .image {
    filter: grayscale(80%);
}

#side-bar .collapsible-block-folded a:link {
    filter: grayscale(80%);
}

#side-bar a:link,
#interwiki a:link {
    color: #2C3749;
}

#side-bar a:visited,
#interwiki a:visited {
    color: #7D7D7D;
}

#side-bar a:link:hover,
#side-bar a:visited:hover,
#interwiki a:link:hover,
#interwiki a:visited:hover {
    color: #3F4F68;
}

#top-bar .open-menu a {
    background: #E8EAEF;
    border-color: #002060;
    color: #002060;
}

/* Tab */
div.yui-navset ul.yui-nav a,
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:hover,
div.yui-navset ul.yui-nav .selected a:focus {
    background: none;
    border: none;
}

.yui-navset {
    clear: both;
    margin-bottom: 1em;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .5);
}

.yui-navset .yui-nav {
    background: #E8EAEF;
    border: 1px solid gainsboro;
    border-bottom: none;
}

.yui-navset .yui-nav li {
    margin-right: 10px !important;
}

.yui-navset .yui-nav li:first-child {
    margin-left: 1.5em;
}

.yui-navset .yui-nav li a em {
    padding: 10px .75em .25em;
    margin-bottom: -1px;
    background: none;
    border: none;
    border-bottom: 4px solid transparent;
    color: gray;
    transition: .2s;
}

.yui-navset .yui-nav li a em:hover {
    background: none;
}

.yui-navset .yui-nav .selected a em,
.yui-navset .yui-nav .selected a em:hover {
    padding: 10px .75em .25em;
    background: none;
    border: none;
    border-bottom: 4px solid #D3DAEA;
    color: #000;
    font-weight: bold;
}

div.yui-navset div.yui-content {
    background: #EFF1F3;
    border: 1px solid gainsboro;
}

/* Rate Module */
.page-rate-widget-box {
    margin-right: 0;
    background-color: #E8EAEF;
    border: 2px solid #002060;
    border-radius: 0px;
}

.page-rate-widget-box > span {
    border: none !important;
}

.page-rate-widget-box .rate-points {
    background: #E8EAEF !important;
    color: #000 !important;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background: #E8EAEF;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: #002060;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: #002060;
    color: #E8EAEF;
}

.page-rate-widget-box .cancel {
    background: #E8EAEF;
}

.page-rate-widget-box .cancel a {
    color: #000;
    font-weight: bold;
}

.page-rate-widget-box .cancel a:hover {
    background: #002060;
    border-radius: 0;
    color: #E8EAEF;
}

.creditRate {
    margin-right: 0 !important;
}

.rate-box-with-credit-button {
    background: #E8EAEF !important;
    border: 2px solid #002060 !important;
    border-radius: 0px !important;
}

.rate-box-with-credit-button .page-rate-widget-box {
    border: none;
}

.creditButton p a {
    border-left: 1px #000 !important;
    color: #000 !important;
}

#page-content .rate-box-with-credit-button .fa-info {
    padding: 3px 0 1px;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #121212;
    border-radius: 0 0 0 0;
    color: #E8EAEF !important;
}

/* ---- EDITING WINDOWS ---- */
.owindow {
    background-color: #E8EAEF;
    border-color: #8C8C8C;
}

.owindow .modal-header {
    background-color: #E8EAEF;
}

.owindow .modal-body img {
    background-color: #E8EAEF !important;
    color: #000000;
}

.owindow .title {
    background-color: #E8EAEF;
}

.owindow a,
.owindow a:visited {
    color: #CECECE;
}

.owindow .active {
    color: #000000;
}

.owindow .content.modal-body img {
    background-color: #727272 !important;
}

.owindow .button-bar a {
    background-color: #E8EAEF;
    color: #000000;
}

.owindow .button-bar a:hover,
.owindow .button-bar a:focus {
    background-color: #E8EAEF !important;
    border-color: #999999;
    color: #000000;
}

table.page-files .highlight {
    background-color: #FBFBFB;
}

input.text,
input.text:focus,
input.text:hover {
    background-color: #E8EAEF !important;
    border-color: #999999;
    color: #000000;
}

textarea {
    background-color: #FEFEFE;
    color: #000000;
}

.change-textarea-size a {
    background-color: #E8EAEF;
    color: #000000;
}

#lock-info {
    background-color: #E8EAEF;
}

/* Blockquote */
blockquote,
div.blockquote {
    background-color: #E8EAEF;
    border-style: dotted;
}

@media (min-width: 386px) and (max-width: 767px) {
    #header {
        background-image: url("https://scpko.wikidot.com/local--files/theme:it-pillar-of-industry/kwangheung-logo1.png");
        background-size: 130px auto;
        background-position: 1em 3em;
    }
    div#container{
        background-image: url("https://scpko.wikidot.com/local--files/theme:it-pillar-of-industry/kwangheung-logo2.png");
        background-repeat: no-repeat;
        background-size: 349px auto;
        background-position: 13em 0em;
    }
}

[[/code]]

[[/iftags]]
