    --swatch-text-general: var(--bright-accent);
    /* Primary Menu Colors */
    --swatch-menubg-color: var(--dark-accent);
    /* Colors for Secondary & Tertiary items like Blockquote and YUI Tabs */
    --swatch-secondary-color: var(--swatch-menubg-dark-color);
