.pager .current {
    color: rgb(var(--bright-accent));
}

.paged a:visited, .pager a {
    color: rgb(var(--bright-accent));
}

.printuser img.small {
    width: 1.25rem;
    height: 1.25rem;
    vertical-align: -0.4em;
}

#who-rated-page-area .printuser a {
