textarea,
input.text,
input.checkbox,
div.note,
#lock-info {
	border: 0.0625rem solid rgba(var(--swatch-menubg-light-color), 0.25);
	background-color: rgb(var(--swatch-menubg-extra-dark-color));
	color: rgb(var(--swatch-text-light));
