body {
    font-family: "Yuji Syuku","游明朝","Yu Mincho","ヒラギノ明朝 Pro W3","Hiragino Mincho Pro", 'Noto Serif JP', serif;
    background-image: url(http://scp-jp-storage.wikidot.com/local--files/file:3438721-245-n3of/washi_bg.jpg);
    font-weight: bold;
    background-color:rgba(245,222,179, 0.4);
    background-blend-mode:lighten;
}

/* COMMON */
#page-title {
    display: none;
}

h1, strong{
    -webkit-text-stroke-width: thin;
}

h1, h2, h3, h4, h5, h6 {
    color: #2f2725;
    font-family: "Yuji Syuku","游明朝","Yu Mincho","ヒラギノ明朝 Pro W3","Hiragino Mincho Pro", 'Noto Serif JP', serif;
}
#page-content h2 {
    height: auto;
    border-bottom: solid #2f2725 1px;
    padding: 4px 0;
    letter-spacing: 1px;
    font-size: 1.5em;
    font-weight: normal;
    -webkit-text-stroke-width: 0.8px;
}

.space {
     position: relative;
     height: 60px;
}

.footnote-footer {
     overflow: auto;
}

a {
    color: #BF1E33;
}
a:visited {
    color: #BD1E48;
}

#toc, blockquote{
    border-color: #9fa0a0;
    border-style: solid;
    border-width: 1px;
    margin: 1em 0;
    background-color: rgba(223, 224, 211,.5);
}

.scp-image-block .scp-image-caption{
    background-color: rgba(223, 224, 211,.5);
}

#page-content hr {
    background: #2f2725;
    margin: 1em 0;
    width: 100%;
}

/* GLOBAL WIDTH */
div#container-wrap {
    background: none;
}


