+ CSS 테마 코드

@@@@
[[/iftags]]

[[div [[iftags -테마]]style="display: none"[[/iftags]]]]
[[module CSS show="true"]]
/*
    King's Palace CSS Theme
    [2022 Wikidot Theme]

    Created by c_bonefish
    Base Code by Denevola
    Logo by SunnyClockwork

    Background Image1: https://pixabay.com/vectors/figure-five-pentagram-geometric-34438/
    Background Image2: https://pixabay.com/vectors/barren-dead-roots-silhouette-tree-2026228/
    Logo Font: Special Elite by Astigmatic

    CC BY-SA 3.0
*/

:root {
  --header-background-1: url('https://scpko.wikidot.com/local--files/theme:king-s-palace/header1.png') center top repeat-x;
  --header-background-2: url('https://scpko.wikidot.com/local--files/theme:king-s-palace/header2.png') center top repeat-x;

  --color-back-1: #fff;
  --color-back-2: #0a0a0a;

  --color-text-1: #000;
  --color-text-2: #fff;

  --color-z-1: #190806;
  --color-a-1: #6B251B;
  --color-b-1: #822D21;
  --color-c-1: #AF493B;
  --color-d-1: #F2E8E8;
  --color-e-1: #E1D2D2;
  --color-f-1: #FCF9F9;

  --color-z-2: #EDDCDC;
  --color-a-2: #CCAFAB;
  --color-b-2: #EAE2E1;
  --color-c-2: #722F27
  --color-d-2: #561818;
  --color-e-2: #3F0F0F;
  --color-f-2: #160000;
}

/* Header */
div#container-wrap {
    background: var(--header-background-{$custom});
    background-size: 420px;
}

#header {
    height: 200px;
    background-image: url('https://scpko.wikidot.com/local--files/theme:king-s-palace/logo.png');
    background-position: 50% 0%;
    background-repeat: no-repeat;
    background-size: 280px auto;
}

#header h1 a {
    position: absolute;
    font-size: 100%;
    text-align: center;
    text-shadow: none;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
}

#header h1 a::before {
    content: " ";
    color: #00000000;
    width: 100%;
    height: 100%;
}

#header h2,
#header h1 a span,
#header h2 span {
    display: none;
}

/* Login & Search & Top Bar */
#login-status {
    position: absolute;
    top: 3px;
    color: #FFCCC1;
    z-index: 2;
}

#login-status a {
    color: #FFCCC1;
    font-weight: bolder;
}

#account-topbutton {
    border-color: #FCFBF9;
}

#account-options {
    background-color: var(--color-back-{$custom});
}

#login-status ul a {
    color: var(--color-text-{$custom}) !important;
}

#search-top-box {
    position: absolute;
    top: 30px;
    right: 0;
    width: 250px;
    text-align: right;
    z-index: 1;
}

#search-top-box-form {
    display: inline-flex;
}

#search-top-box-form > input {
    -webkit-appearance: none;
    -webkit-border-radius: 0;
    border-radius: 5px;
}

#search-top-box-input {
    background-color: #F2E8E8;
    border: solid 1px #000;
    color: #000;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    box-shadow: inset 1px 1px 5px rgb(0 0 0 / 50%);
    color: #000000 !important;
}

#search-top-box-form input[type=submit] {
    padding: 2px 5px;
    background: #230902;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
    border: solid 1px #F2E8E8;
    color: #F2E8E8;
    font-size: 90%;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background: #230902;
}

#top-bar {
    display: flex;
    justify-content: center;
    top: 173px;
    width: 100%;
    z-index: 20;
}

#top-bar a {
    color: #F2CCCB;
    background: none;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #54190D;
    border-color: transparent;
    color: #FDF2F1;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #000;
}

#top-bar ul li ul,
#top-bar ul li ul li ul {
    border-color: rgba(0, 0, 0, .4);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-color: rgba(0, 0, 0, .1);
}

#top-bar ul li.sfhover ul li ul a,
#top-bar ul li:hover ul li ul a {
    border-top: none;
}

.top-bar {
    position: relative;
    margin: 0 auto;
}
.c_topbar_arrow {
    color: #D5D5D5;
}

/* Page Elements */
#page-title,
.meta-title {
    color: var(--color-z-{$custom});
    font-size: 2.1em;
    font-weight: bold;
}

#content-wrap {
    margin: 30px auto 0;
}

h1, h2, h3, h4, h5, h6 {
    color: var(--color-z-{$custom});
    font-weight: bold;
}

body {
    background-color: var(--color-back-{$custom});
    color: var(--color-text-{$custom});
}

a:link {
    color: var(--color-a-{$custom});
}

a:visited {
    color: var(--color-b-{$custom});
}

.hovertip {
    background-color: #F6F6F6 !important;
    border: 2px solid #666666 !important;
    color: #000000 !important;
}

.scp-image-block {
    background: var(--color-back-{$custom});
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
}

.scp-image-block .scp-image-caption {
    background: var(--color-d-{$custom});
    border-top: none;
    font-size: 85%;
}

.scp-image-block img {
    box-shadow: 0 0 1px black;
}

/* ---- SCROLLBAR ---- */
::-webkit-scrollbar {
    width: unset;
    height: unset;
}

::-webkit-scrollbar-track {
    background: #fff;
}

::-webkit-scrollbar-thumb {
    background: #54190D;
    border: none;
}

::-webkit-scrollbar-thumb:hover {
    background: #381008;
}

textarea::-webkit-scrollbar-track {
    background: #fff;
}

/* Side Bar */
#side-bar {
    background: none;
}

#side-bar .side-block,
#side-bar .side-block.media,
#side-bar .side-block.resources,
#interwiki .side-block {
    background-color: var(--color-back-{$custom}) !important;
    border: 2px solid #691908;
    border-radius: 10px 0;
}

#side-bar .heading,
#interwiki .heading,
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #691908;
    color: var(--color-a-{$custom});
}

#side-bar .collapsible-block-folded-link .collapsible-block-link:hover,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: var(--color-c-{$custom});
}

#side-bar .collapsible-block-folded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: var(--color-a-{$custom});
}

#side-bar .menu-item .image,
#interwiki .menu-item .image,
#side-bar .collapsible-block-folded,
#side-bar .collapsible-block-unfolded-link .collapsible-block-content .image {
    filter: grayscale(80%);
}

#side-bar a:link,
#interwiki a:link {
    color: var(--color-a-{$custom});
}

#side-bar a:visited,
#interwiki a:visited {
    color: var(--color-b-{$custom});
}

#side-bar a:link:hover,
#side-bar a:visited:hover,
#interwiki a:link:hover,
#interwiki a:visited:hover {
    color: var(--color-c-{$custom});
}

#top-bar .open-menu a {
    border-color: #691908;
    color: #691908;
}

/* Tab */
div.yui-navset ul.yui-nav a,
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:hover,
div.yui-navset ul.yui-nav .selected a:focus {
    background: none;
    border: none;
}

.yui-navset {
    background: var(--color-back-{$custom});
    clear: both;
    margin-bottom: 1em;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .5);
}

.yui-navset .yui-nav {
    background: var(--color-e-{$custom});
    border: 1px solid gainsboro;
    border-bottom: none;
}

.yui-navset .yui-nav li {
    margin-right: 10px !important;
}

.yui-navset .yui-nav li:first-child {
    margin-left: 1.5em;
}

.yui-navset .yui-nav li a em {
    padding: 10px .75em .25em;
    margin-bottom: -1px;
    background: none;
    border: none;
    border-bottom: 4px solid transparent;
    color: gray;
    transition: .2s;
}

.yui-navset .yui-nav li a em:hover {
    background: none;
}

.yui-navset .yui-nav .selected a em,
.yui-navset .yui-nav .selected a em:hover {
    padding: 10px .75em .25em;
    background: none;
    border: none;
    border-bottom: 4px solid #9C9C9C;
    color: var(--color-text-{$custom});
    font-weight: bold;
}

div.yui-navset div.yui-content {
    background: var(--color-back-{$custom});
    border: 1px solid gainsboro;
}

/* Rate Module */
.page-rate-widget-box {
    margin-right: 0;
    background-color: #F2E8E8;
    border: 2px solid #691908;
    border-radius: 0px;
    box-shadow: none;
}

.page-rate-widget-box > span {
    border: none !important;
}

div.page-rate-widget-box .rate-points {
    background: #F2E8E8;
    color: #000;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background: #F2E8E8;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: #000;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: #691908;
    color: #fff;
}

.page-rate-widget-box .cancel {
    background: #F2E8E8;
}

.page-rate-widget-box .cancel a {
    color: #000;
    font-weight: bold;
}

.page-rate-widget-box .cancel a:hover {
    background: #691908;
    border-radius: 0;
    color: #fff;
}

.creditRate {
    margin-right: 0 !important;
}

.rate-box-with-credit-button {
    background: #F2E8E8 !important;
    border: 2px solid #691908 !important;
    border-radius: 0px !important;
    box-shadow: none !important;
}

.rate-box-with-credit-button .page-rate-widget-box {
    border: none;
}

.creditButton p a {
    border-left: none !important;
    color: #000 !important;
}

#page-content .rate-box-with-credit-button .fa-info {
    padding: 3px 0 1px;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #691908;
    border-radius: 0 3px 3px 0;
    color: #fff !important;
}

/* ---- EDITING WINDOWS ---- */
.owindow {
    background-color: var(--color-back-{$custom});
    border-color: #8C8C8C;
}

.owindow .modal-header {
    background-color: var(--color-back-{$custom});
}

.owindow .modal-body img {
    background-color: var(--color-back-{$custom}) !important;
    color: var(--color-text-{$custom});
}

.owindow .title {
    background-color: var(--color-back-{$custom});
}

.owindow a,
.owindow a:visited {
    color: #CECECE;
}

.owindow .active {
    color: var(--color-text-{$custom});
}

.owindow .content.modal-body img {
    background-color: #727272 !important;
}

.owindow .button-bar a {
    background-color: var(--color-back-{$custom});
    color: var(--color-text-{$custom});
}

.owindow .button-bar a:hover,
.owindow .button-bar a:focus {
    background-color: var(--color-back-{$custom}) !important;
    border-color: #999999;
    color: var(--color-text-{$custom});
}

table.page-files .highlight {
    background-color: #FBFBFB;
}

input.text,
input.text:focus,
input.text:hover {
    background-color: var(--color-back-{$custom}) !important;
    border-color: #999999;
    color: var(--color-text-{$custom});
}

textarea {
    background-color: var(--color-back-{$custom}) !important;
    color: var(--color-b-{$custom});
}

.change-textarea-size a {
    background-color: var(--color-back-{$custom}) !important;
    color: var(--color-b-{$custom});
}

#lock-info {
    background-color: var(--color-back-{$custom});
}

/* Blockquote */
blockquote,
div.blockquote {
    background-color: var(--color-f-{$custom});
    border-style: dotted;
}

[[/module]]
[[/div]]

[[div style="display: none"]]
[[#ifexpr {$custom} == 1 | default | [!-- ]]

[[include :scpko:theme:king-s-palace
|custom=1
]]

[[#ifexpr {$custom} == 1 | default | --] ]]
[[/div]]
