[[code type="css"]]
/*
    Kiryu Labs Theme
    [2020 Wikidot Theme]
    Created by rounderhouse.
    Based on SCP Sigma 9 Theme created by Aelanna and Dr Devan, and Wilson's Wildlife Solutions theme by Stormbreath.
    Built for Zyn, #ARTEXCHANGE2019 - Merry (semi-late) Christmas!
*/

@import url('https://scp-wiki.wdfiles.com/local--code/theme%3Akiryu-labs-theme/1');
@import url('https://scpko.wdfiles.com/local--files/korean-font/SoulTitle.css');

#header h1 a {
    font-family:  'Bebas Neue', 'SoulTitle', cursive;
}

#header h1 a::before {
    content: "키류 연구실";
}

/* Info module fix */
.rate-box-with-credit-button {
    background-color: #a3c162;
}

.rate-box-with-credit-button .page-rate-widget-box * {
    border: none;
}

.rate-box-with-credit-button .page-rate-widget-box .cancel a:hover {
    border-radius: 0;
}
[[/code]]

