[[module CSS]]
@import url("https://fonts.googleapis.com/css2?family=Kaushan+Script&display=swap");
@import url('https://fonts.googleapis.com/css2?family=EB+Garamond:ital,wght@0,400..800;1,400..800&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@200..900&display=swap');

/* https://pixabay.com/photos/paris-france-city-cities-urban-195327/
https://pixabay.com/photos/hamburger-snack-french-fries-brown-3483290/ */

:root:lang(cn) {
    --title: "SCP厨房";
    --subtitle: "“it's Soul Charmin' Perfect”";
}

:root {
    --KRed: 228, 0, 43;
    --McYellow: 255, 188, 13;
    --BG: 244, 240, 240;
    --basalt-overtone: var(--McYellow);
    --basalt-undertone: var(--KRed);
    --basalt-bright-element-color: var(--KRed);
    --basalt-dark-element-color: 20, 20, 20;
    --basalt-secondary-color: 250, 247, 247;
    --header-gradient: linear-gradient(to bottom, rgba(var(--basalt-primary-color), 1), rgba(var(--basalt-primary-color), 0.9));
    --header-stripes: linear-gradient(to bottom, transparent 8.5rem, rgb(20, 20, 20) 8.5rem);
    --UI-title-background-color: var(--BG);

    --element-main-accent-color: var(--KRed);
    --element-side-accent-color: var(--McYellow);

    --search-icon-color: 20, 20, 20;
    --side-bar-button-color: 20, 20, 20;

    --bottom-area-text-color: 254, 254, 254;
    --bottom-area-background-color: var(--basalt-dark-element-color);
    --top-bar-link-color: var(--dr-hormress);
    --footer-link-color: var(--basalt-overtone);

    --title-size: min(8vw, 2.25rem);
    --subtitle-size: min(2.5vw, 1rem);
    --header-h1-height: 6rem;
    --top-bar-height: 2.5rem;
    --main-content-top-margin: 3rem;
    --main-content-width: 64rem;

    --header-font: "EB Garamond", "Noto Sans SC", serif;

    --header-border-width: 0rem;

    --logo-outside-mask: url("data:image/svg+xml,%3C%3Fxml version='1.0' encoding='utf-8'%3F%3E%3C!-- Generator: Adobe Illustrator 24.0.1, SVG Export Plug-In . SVG Version: 6.00 Build 0%29 --%3E%3Csvg version='1.1' id='SCP_Logo' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' x='0px' y='0px'%0AviewBox='0 0 2215 2080' style='enable-background:new 0 0 2215 2080;' xml:space='preserve'%3E%3Cpath id='Outer_Circle' d='M2215,1351.8l-125-139.7c5-40.5,7.5-81.3,7.5-122.1c0-410-249.3-761.8-604.6-912.2L1434.5,0h-654%0Al-58.4,177.9C366.8,328.2,117.5,680,117.5,1090c0,40.8,2.5,81.6,7.5,122.1L0,1351.8l327,566.4l183.5-38.4%0Ac166,125.7,372.8,200.2,597,200.2s431-74.5,597-200.2l183.5,38.4L2215,1351.8L2215,1351.8z M1683,1794.4%0Ac-156.7,128.5-357.1,205.6-575.5,205.6c-218.4,0-418.8-77.1-575.5-205.6l-164.2,34.4l-270-467.7l112.3-125.4%0Ac-7.6-47.5-11.6-96.1-11.6-145.8c0-389,243.9-721.1,586.9-851.2L837.5,80h540l52.1,158.8c343.1,130.2,586.9,462.2,586.9,851.2%0Ac0,49.6-4,98.3-11.6,145.8l112.3,125.4l-270,467.7L1683,1794.4z'/%3E%3C/svg%3E");
    --logo-inside-mask: url("data:image/svg+xml,%3C%3Fxml version='1.0' encoding='utf-8'%3F%3E%3C!-- Generator: Adobe Illustrator 24.0.1, SVG Export Plug-In . SVG Version: 6.00 Build 0%29 --%3E%3Csvg version='1.1' id='SCP_Logo' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' x='0px' y='0px'%0AviewBox='0 0 2215 2080' style='enable-background:new 0 0 2215 2080;' xml:space='preserve'%3E%3Cpath d='M1771.8,1427.4l-82.4-47.5c43.5-87.3,68-185.7,68-289.8c0-345.6-269.6-628.1-610-648.8V346h-80v95.2%0Ac-340.4,20.7-610,303.2-610,648.8c0,104.1,24.5,202.6,68,289.8l-82.4,47.5l40,69.3l82.4-47.6C682,1624.4,881.3,1740,1107.5,1740%0As425.5-115.6,541.9-290.9l82.4,47.6L1771.8,1427.4z M1107.5,1620c-181.8,0-342.3-91.6-437.7-231.1l206.6-119.3l42.5,73.6l154-233.3%0Al-279,16.7l42.5,73.6l-206.6,119.3c-33.5-69.5-52.2-147.4-52.2-229.7c0-279.3,216-508.1,490-528.5V800h-85l125,250l125-250h-85%0AV561.5c274,20.4,490,249.3,490,528.5c0,82.3-18.8,160.2-52.2,229.7l-206.6-119.3l42.5-73.6l-279-16.7l154,233.3l42.5-73.6%0Al206.6,119.3C1449.8,1528.4,1289.3,1620,1107.5,1620z'/%3E%3C/svg%3E");

    --burger-unit: calc(30vh - var(--header-final-height));
}

body {
    background-color: rgb(var(--BG));
}

div#content-wrap {
    overflow-x: hidden;
}

blockquote,
.blockquote,
div.blockquote,
table.wiki-content-table {
    --general-border-color: var(--element-main-accent-color);
    --blockquote-border-color: var(--element-main-accent-color);
    border-width: var(--base-border-width);
}

div.rate-box-with-credit-button,
div.page-rate-widget-box {
    box-shadow: 0 var(--base-border-width) 0 rgb(var(--element-side-accent-color));
}

div.rate-box-with-credit-button div.page-rate-widget-box {
    box-shadow: none;
}

table.wiki-content-table {
    border: solid var(--base-border-width) rgb(var(--general-border-color));
}

.yui-content {
    border: none;
}

.yui-navset {
    border: solid var(--base-border-width) rgb(var(--element-main-accent-color));
}

hr,
div#main-content :is(.blockquote, blockquote, .code) > hr {
    background-color: rgb(var(--element-main-accent-color));
}

/* ==== HEADER ==== */

div#header {
    background-color: transparent;
    backdrop-filter: blur(0.2rem);
    -webkit-backdrop-filter: blur(0.2rem);
    box-shadow: none;
}

div#header h1 a {
    display: inline-grid;
    grid-template-areas: 'a b';
    transform: scale(0.8);
    -webkit-transform: scale(0.8);
    transform-origin: left center;
    -webkit-transform-origin: left center;
}

div#header h1 a::before {
    margin-right: 0;
    background-image: none;
    background-color: rgb(var(--McYellow));
    mask-image: var(--logo-inside-mask);
    -webkit-mask-image: var(--logo-inside-mask);
    grid-area: a;
}

div#header h1 a::after {
    content: '';
    display: block;
    height: 100%;
    width: calc(var(--base-header-height) * .925);
    min-width: calc(var(--base-header-height) *.75);
    background-color: rgb(var(--KRed));
    mask-image: var(--logo-outside-mask);
    -webkit-mask-image: var(--logo-outside-mask);
    grid-area: a;
}

div#header h1 a::before,
div#header h1 a::after {
    mask-size: var(--header-logo-size);
    mask-position: left center;
    mask-repeat: no-repeat;
    -webkit-mask-size: var(--header-logo-size);
    -webkit-mask-position: left center;
    -webkit-mask-repeat: no-repeat;
}

div#header h1 a span::before {
    font-family: "EB Garamond", "Noto Serif SC", serif;
    margin-bottom: 0.25em;
    color: rgb(var(--basalt-overtone));
}

div#header h1 a span::after {
    color: rgb(var(--basalt-undertone));
    font-family: "Kaushan Script", cursive;
    font-weight: 400;
    letter-spacing: 0;
}

div#account-options {
    border-bottom: solid 0.3rem rgb(var(--KRed));
}

div#top-bar div[class*="top-bar"] > ul {
    justify-content: center;
}

@media (min-width: 1025px) {
div#top-bar div[class*="top-bar"] > ul > li > a {
    font-size: 1rem;
    padding-inline: min(5vw, 1.5rem);
}
}

div#header-extra-div-1 {
    position: absolute;
    bottom: -0.3rem;
    width: 100%;
    height: 0.3rem;
    background-image: repeating-linear-gradient(to right, rgb(var(--KRed)) 0rem, rgb(var(--McYellow)) 0rem, rgb(var(--McYellow)) 4rem, rgb(var(--KRed)) 4rem, rgb(var(--KRed)) 8rem);
    z-index: -1;
}

/* ==== SIDEBAR ==== */

div.owindow div.title,
div#side-bar div.side-block div.heading {
    position: relative;
    border-color: rgba(0, 0, 0, 0);
}

div.owindow div.title::after,
div#side-bar div.side-block div.heading::after {
    content: "";
    display: block;
    position: absolute;
    left: 0;
    bottom: calc(0rem - var(--base-border-width));
    width: 100%;
    height: var(--base-border-width);
    background-image: repeating-linear-gradient(-30deg, rgb(var(--KRed)) 0rem, rgb(var(--McYellow)) 0rem, rgb(var(--McYellow)) 1rem, rgb(var(--KRed)) 1rem, rgb(var(--KRed)) 2rem);
    background-position: 50% center;
    background-repeat: repeat-x;
}

div#side-bar div.collapsible-block-unfolded a.collapsible-block-link,
div#side-bar div.collapsible-block-folded a.collapsible-block-link {
    border-color: rgb(var(--McYellow));
}

/* ==== CONTENT ==== */

div#page-title,
div.meta-title {
    text-align: center;
    font-size: 200%;
}

div#main-content > .page-tags > span a {
    border-radius: 0rem;
}

div#footer {
    border-top: none;
}

blockquote,
.blockquote,
div.blockquote {
    border-left: solid 0.3rem rgb(var(--basalt-dark-element-color));
    border-block-width: 0px;
    border-right: none;
}

div.blockquote.overbox {
    border-left: solid 0.3rem rgb(var(--basalt-overtone));
}

div.blockquote[class*="burger"] {
    position: relative;
    margin-top: 3rem;
    padding: 1rem 2rem;
    border: none;
    border-left: solid 0.3rem rgb(var(--element-main-accent-color));
    box-shadow: none;
}

div.blockquote[class*="burger"].main-accent {
    border-left: solid 0.3rem rgb(var(--element-main-accent-color));
}

div.blockquote[class*="burger"].side-accent,
div.blockquote[class*="burger"]:nth-child(even) {
    border-left: solid 0.3rem rgb(var(--element-side-accent-color));
}

