    background-position: center;
    background-color: rgb(20, 20, 20);
}

div.banner-text-container {
    display: grid;
    grid-template-rows: 1fr;
    grid-auto-flow: column;
    height: 100%;
    background-image: linear-gradient(transparent, 40%, rgba(20, 20, 20, 0.8));
}

div.banner-text-container div {
    text-align: center;
    align-self: center;
}

div.banner-text-container h1 {
    font-size: 8rem;
    line-height: 1;
    margin: 0;
}

div.banner-text-container h2 {
    font-size: 2rem;
    line-height: 1;
    margin: 0;
}

@media (max-width: 1024px) {
:root {
    --burger-unit: 10vh;
}

div.banner-text-container {
    grid-template-rows: unset;
    grid-template-columns: 1fr;
    grid-auto-flow: row;
}

div.banner-text-container h1 {
    font-size: 6rem;
}

div.banner-text-container h2 {
    font-size: 1.5rem;
}
}

