.scp-image-block.block-right {
	margin: 1em 0 1em 2em;
}

.scp-image-block a {
	width: 100%;
	min-width: 100%;
}

.scp-image-block img {
	min-width: 100%;
	border: 0.5rem solid rgba(var(--pale-gray-monochrome));
	box-sizing: border-box;
}

.colors-container .subcolors .color:nth-of-type(9) {
	background: var(--pastel-rainbow) !important;
	-webkit-background-size: 100% 100%;
	        background-size: 100% 100%;
	-webkit-animation: rainbow 36s ease infinite;
	        animation: rainbow 36s ease infinite;
}
[[/module]]

