@import url('https://fonts.googleapis.com/css?family=Chicle|Overlock:400,900');

@supports(display:grid) {
	:root {
		/* S-CSS-P Integration */
		/* If you're making a new CSS theme, please include the following three variables at minimum. */
		--theme-base: "black-highlighte";
		/* must be either "nuscp" or "sigma9" */
		--theme-id: "laughter-and-knives-theme";
		/* set this to the URL of your theme's page - eg for "component:ar-theme", set it to "ar-theme" */
		--theme-name: "Laughter and Knives Theme";
		/* set this to your theme's full name */
	}
	::-webkit-scrollbar-thumb {
		background: linear-gradient(124deg,
				rgba(240, 194, 215, 1),
				rgba(215, 195, 218, 1),
				rgba(181, 197, 223, 1),
				rgba(184, 198, 223, 1),
				rgba(167, 203, 223, 1),
				rgba(196, 221, 189, 1),
				rgba(218, 230, 172, 1),
				rgba(230, 234, 171, 1),
				rgba(255, 242, 167, 1));
		background-size: 100% 100%;
	}

	::-webkit-scrollbar-track-piece {
		background-color: rgb(var(--bright-accent));
	}

	::-moz-selection {
		color: rgb(var(--black-monochrome));
		text-shadow: none;
	}

	:root *:nth-child(4n-3)::-moz-selection {
		background-color: rgba(216, 151, 154, 1);
	}

	:root *:nth-child(4n-2)::-moz-selection {
		background-color: rgba(198, 223, 158, 1);
	}

	:root *:nth-child(4n-1)::-moz-selection {
		background-color: rgba(145, 191, 222, 1);
	}

	:root *:nth-child(4n)::-moz-selection {
		background-color: rgba(246, 186, 212, 1);
	}

	::-moz-selection {
		color: rgb(var(--black-monochrome));
		text-shadow: none;
	}

	::selection {
		color: rgb(var(--black-monochrome));
		text-shadow: none;
	}

	:root *:nth-child(4n-3)::-moz-selection {
		background-color: rgba(216, 151, 154, 1);
	}

	:root *:nth-child(4n-3)::selection {
		background-color: rgba(216, 151, 154, 1);
	}

	:root *:nth-child(4n-2)::-moz-selection {
		background-color: rgba(198, 223, 158, 1);
	}

	:root *:nth-child(4n-2)::selection {
		background-color: rgba(198, 223, 158, 1);
	}

	:root *:nth-child(4n-1)::-moz-selection {
		background-color: rgba(145, 191, 222, 1);
	}

	:root *:nth-child(4n-1)::selection {
		background-color: rgba(145, 191, 222, 1);
	}

	:root *:nth-child(4n)::-moz-selection {
		background-color: rgba(246, 186, 212, 1);
	}

	:root *:nth-child(4n)::selection {
		background-color: rgba(246, 186, 212, 1);
	}

	:root {
		/* Typefaces */
		--body-font: 'Overlock', monospace;
		--header-font: 'Chicle', cursive;
		--title-font: 'Chicle', cursive;

		--logo-image: url("https://scp-wiki.wdfiles.com/local--files/theme:laughter-and-knives-theme/mzl_logo.svg");
		--header-title: "MAN ZOO LEAGUE";
		--header-subtitle: "Well, well, well... How the turn tables...";

		/* Standard Colors */

		--white-monochrome: 245, 233, 239;
		/* Prim */
		--pale-gray-monochrome: 220, 226, 243;
		/* Quartz */
		--light-gray-monochrome: 242, 243, 220;
		/* Loafer */
		--gray-monochrome: 185, 224, 173;
		/* Moss Green */
		--dark-gray-monochrome: 160, 240, 205;
		/* Magic Mint */
		--black-monochrome: 76, 0, 85;
		/* Ripe Plum */
		--bright-accent: 250, 140, 220;
		/* Lavender Rose */
		--medium-accent: 186, 237, 185;
		/* Tea Green */
		--dark-accent: 228, 123, 0;
		/* Mango Tango */
		--newpage-color: 221, 102, 17;
		/* Christine */

		--swatch-headerh1-color: var(--black-monochrome);
		--swatch-headerh2-color: var(--dark-accent);

		--swatch-tertiary-color: var(--swatch-menubg-dark-color);
		--swatch-text-secondary-color: var(--swatch-menutxt-dark-color);
		--swatch-text-tertiary-color: var(--swatch-menutxt-dark-color);
		--background-gradient-color: 215, 215, 215;
		--background-gradient-distance: 40rem;

		--pastel-rainbow: linear-gradient(to top right,
				rgba(216, 151, 154, 0.75),
				rgba(255, 240, 148, 0.75),
				rgba(198, 223, 158, 0.75),
				rgba(145, 191, 222, 0.75),
				rgba(246, 186, 212, 0.75));
	}

	hr {
		border-top: none;
		background:
			/* Bright Rainbow Gradient */
			linear-gradient(to top right,
				rgba(216, 151, 154, 0.75),
				rgba(255, 240, 148, 0.75),
				rgba(198, 223, 158, 0.75),
				rgba(145, 191, 222, 0.75),
				rgba(246, 186, 212, 0.75));
		-webkit-filter: blur(0.04rem);
		filter: blur(0.04rem);
	}

	#container-wrap-wrap {
		background-image:
			linear-gradient(to bottom,
				rgba(0, 0, 0, 0) 0rem,
				rgba(0, 0, 0, 0) 7.5rem,
				rgba(var(--black-monochrome), 1) 7.5rem,
				rgba(var(--gray-monochrome), 1) 7.625rem,
				rgba(var(--gray-monochrome), 1) calc(100% - 0.125rem),
				rgba(var(--gray-monochrome), 1) calc(100% - 0.125rem),
				rgba(var(--black-monochrome), 1) 100%),

			linear-gradient(to bottom,
				rgba(0, 0, 0, 0) 0rem,
				rgba(0, 0, 0, 0) 9.375rem,
				rgba(var(--white-monochrome), 0.5) 9.375rem,
				rgba(var(--pale-gray-monochrome), 0.5) 100%);
		background-size: 100% 9.375rem, 100% 100%;
		background-repeat: no-repeat;
	}

	#skrollr-body {
		background:
			/* Bright Rainbow Gradient */
			linear-gradient(to top right,
				rgba(216, 151, 154, 1),
				rgba(255, 240, 148, 1),
				rgba(198, 223, 158, 1),
				rgba(145, 191, 222, 1),
				rgba(246, 186, 212, 1));
		background-size: 100% 100%;
	}

	div#container-wrap {
		background-image: -webkit-repeating-linear-gradient(45deg, hsla(0, 0%, 100%, 0), hsla(0, 0%, 100%, 0) 0.25vh, rgba(88, 88, 88, 0.1) 0.35vh, rgba(88, 88, 88, 0.2) 0.5vh);
		background-image: repeating-linear-gradient(45deg, hsla(0, 0%, 100%, 0), hsla(0, 0%, 100%, 0) 0.25vh, rgba(88, 88, 88, 0.1) 0.35vh, rgba(88, 88, 88, 0.2) 0.5vh);
		background-size: 100% 7.5rem;
		background-repeat: no-repeat;
	}

	a {
		--wght: 900;
	}

	#header h1 a::before {
		letter-spacing: 0.1em;
	}

	#header h2 {
		font-size: calc(var(--base-font-size) * 0.9);
	}

	#header h2 span::before {
		--wght: 900;
		letter-spacing: 0.1em;
	}

	#login-status span.printuser,
	#login-status a#my-account {
		color: rgb(var(--swatch-menutxt-dark-color));
	}

	#top-bar * {
		--ui-wght: 900;
		--ui-hvr-wght: 1000;
		font-size: calc(var(--base-font-size));
		color: rgb(var(--light-gray-monochrome));
		text-shadow:
			0.0625rem 0.0625rem 0 rgb(var(--swatch-primary)),
			-0.0625rem 0.0625rem 0 rgb(var(--swatch-primary)),
			0.0625rem -0.0625rem 0 rgb(var(--swatch-primary)),
			-0.0625rem -0.0625rem 0 rgb(var(--swatch-primary)),
			0rem 0.0625rem 0 var(---swatch-primary),
			0rem -0.0625rem 0 rgb(var(--swatch-primary)),
			-0.0625rem 0rem 0 rgb(var(--swatch-primary)),
			0.0625rem 0rem 0 rgb(var(--swatch-primary-darker)),
			0.125rem 0.125rem 0 rgb(var(--swatch-primary-darker)),
			-0.125rem 0.125rem 0 rgb(var(--swatch-primary-darker)),
			0.125rem -0.125rem 0 rgb(var(--swatch-primary-darker)),
			-0.125rem -0.125rem 0 rgb(var(--swatch-primary-darker)),
			0rem 0.125rem 0 rgb(var(--swatch-primary-darker)),
			0rem -0.125rem 0 rgb(var(--swatch-primary-darker)),
			-0.125rem 0rem 0 rgb(var(--swatch-primary-darkest)),
			0.125rem 0rem 0 rgb(var(--swatch-primary-darkest)),
			0.0625rem 0.125rem 0 rgb(var(--swatch-primary-darkest)),
			-0.0625rem 0.125rem 0 rgb(var(--swatch-primary-darkest)),
			0.0625rem -0.125rem 0 rgb(var(--swatch-primary-darkest)),
			-0.0625rem -0.125rem 0 rgb(var(--swatch-primary-darkest)),
			0.125rem 0.0625rem 0 rgb(var(--swatch-primary-darkest)),
			-0.125rem 0.0625rem 0 rgb(var(--swatch-primary-darkest)),
			0.125rem -0.0625rem 0 rgb(var(--swatch-primary-darkest)),
			-0.125rem -0.0625rem 0 rgb(var(--swatch-primary-darkest));
	}

	#top-bar>div.top-bar>ul>li>a,
	#top-bar>div.top-bar>ul>li>ul>li>a,
	#interwiki>div>div>a
	#side-bar>div>div>a {
		position: relative !important;
		background: transparent !important;
		border: 0rem solid rgba(0, 0, 0, 0);
	}

	#top-bar div.mobile-top-bar>ul>li>a,
	#top-bar div.top-bar>ul>li>a {
		color: rgb(var(--swatch-menutxt-dark-color));
	}

	#top-bar div.top-bar>ul>li>ul>li>a::before,
	#top-bar div.top-bar>ul>li>ul>li>ul>li>a::before,
	#top-bar div.top-bar>ul>li>ul>li>a:hover::before,
	#interwiki div.menu-item a::before,
	#interwiki div.menu-item a:hover::before,
	#side-bar div.menu-item a::before,
	#side-bar div.menu-item a:hover::before {
		background-color: rgba(0, 0, 0, 0);
		background: var(--pastel-rainbow);
	}

	#top-bar div.mobile-top-bar>ul>li>ul>li>a,
	#top-bar div.top-bar>ul>li>ul>li>a {
		color: rgb(var(--swatch-menutxt-dark-color));
	}

	#search-top-box::before {
		transition: background-color 150ms linear;
	}

	#search-top-box:hover::before {
		background-color: rgb(var(--swatch-menutxt-dark-color))
	}

	#search-top-box form[id="search-top-box-form"] {
		border-radius: 0.5em;
	}

	#search-top-box-form input[type="submit"]:hover,
	#search-top-box-form input[type="submit"]:focus {
		background:
			/* Bright Rainbow Gradient */
			linear-gradient(to top right,
				rgba(216, 151, 154, 0.75),
				rgba(255, 240, 148, 0.75),
				rgba(198, 223, 158, 0.75),
				rgba(145, 191, 222, 0.75),
				rgba(246, 186, 212, 0.75));
		background-size: 100% 100%;
	}

	#font-size {
		--ui-wght: 900;
	}

	#interwiki {
		background-color: transparent;
	}

	#side-bar,
	#side-bar:hover {
		font-variant: unset;
		background:
			/* Bright Rainbow Gradient */
			linear-gradient(to top right,
				rgba(216, 151, 154, 0.75),
				rgba(255, 240, 148, 0.75),
				rgba(198, 223, 158, 0.75),
				rgba(145, 191, 222, 0.75),
				rgba(246, 186, 212, 0.75));
		background-size: 200% 200%;
	}

	#interwiki .heading,
	#side-bar .heading {
		background-color: rgb(var(--gray-monochrome));
	}

	#interwiki .heading p,
	#side-bar .heading p,
	#side-bar .side-block>.collapsible-block .collapsible-block-link {
		text-shadow: inherit;
		text-transform: none;
		font-family: var(--header-font);
		font-size: 125%;
		color: rgb(var(--swatch-menutxt-dark-color));
		letter-spacing: 0.1em;
	}

	#interwiki div.menu-item a,
	#side-bar div.menu-item a,
	#side-bar div.menu-item .text {
		font-size: calc(var(--base-font-size));
		text-shadow:
			0.0625rem 0.0625rem 0 rgb(var(--swatch-primary)),
			-0.0625rem 0.0625rem 0 rgb(var(--swatch-primary)),
			0.0625rem -0.0625rem 0 rgb(var(--swatch-primary)),
			-0.0625rem -0.0625rem 0 rgb(var(--swatch-primary)),
			0rem 0.0625rem 0 rgb(var(--swatch-primary)),
			0rem -0.0625rem 0 rgb(var(--swatch-primary)),
			-0.0625rem 0rem 0 rgb(var(--swatch-primary-darker)),
			0.0625rem 0rem 0 rgb(var(--swatch-primary-darker)),
			0.125rem 0.125rem 0 rgb(var(--swatch-primary-darker)),
			-0.125rem 0.125rem 0 rgb(var(--swatch-primary-darker)),
			0.125rem -0.125rem 0 rgb(var(--swatch-primary-darker)),
			-0.125rem -0.125rem 0 rgb(var(--swatch-primary-darker)),
			0rem 0.125rem 0 rgb(var(--swatch-primary-darker)),
			0rem -0.125rem 0 rgb(var(--swatch-primary-darker)),
			-0.125rem 0rem 0 rgb(var(--swatch-primary-darkest)),
			0.125rem 0rem 0 rgb(var(--swatch-primary-darkest)),
			0.0625rem 0.125rem 0 rgb(var(--swatch-primary-darkest)),
			-0.0625rem 0.125rem 0 rgb(var(--swatch-primary-darkest)),
			0.0625rem -0.125rem 0 rgb(var(--swatch-primary-darkest)),
			-0.0625rem -0.125rem 0 rgb(var(--swatch-primary-darkest)),
			0.125rem 0.0625rem 0 rgb(var(--swatch-primary-darkest)),
			-0.125rem 0.0625rem 0 rgb(var(--swatch-primary-darkest)),
			0.125rem -0.0625rem 0 rgb(var(--swatch-primary-darkest)),
			-0.125rem -0.0625rem 0 rgb(var(--swatch-primary-darkest));
	}

	.page-rate-widget-box {
		--box-shadow: rgba(var(--gray-monochrome), 0.5);
		background:
			/* Bright Rainbow Gradient */
			linear-gradient(to top right,
				rgba(216, 151, 154, 0.75),
				rgba(255, 240, 148, 0.75),
				rgba(198, 223, 158, 0.75),
				rgba(145, 191, 222, 0.75),
				rgba(246, 186, 212, 0.75)) !important;
		background-size: 50% 50%;
		box-shadow: 0.0625rem 0.0625rem 0.0625rem var(--box-shadow);
		border: dashed 0.0625rem rgb(var(--black-monochrome));
		border-radius: 1rem;
	}

	.page-rate-widget-box>span.rate-points,
	.page-rate-widget-box>span.rateup,
	.page-rate-widget-box>span.ratedown,
	.page-rate-widget-box>span.cancel,
	.page-rate-widget-box>span.rate-points>a,
	.page-rate-widget-box>span.rateup>a,
	.page-rate-widget-box>span.ratedown>a,
	.page-rate-widget-box>span.cancel>a {
		color: rgb(var(--bright-accent));
		mix-blend-mode: unset;
		transition: all 0.2s ease-in-out;
	}

	.page-rate-widget-box>span.rate-points:hover,
	.page-rate-widget-box>span.rate-points:active {
		color: rgb(var(--bright-accent)) !important;
		mix-blend-mode: unset !important;
	}

	.page-rate-widget-box>span.rateup:hover,
	.page-rate-widget-box>span.rateup:active,
	.page-rate-widget-box>span.ratedown:hover,
	.page-rate-widget-box>span.ratedown:active,
	.page-rate-widget-box>span.cancel:hover,
	.page-rate-widget-box>span.cancel:active {
		color: rgb(var(--black-monochrome));
		background: linear-gradient(124deg,
				rgba(240, 194, 215, 1),
				rgba(215, 195, 218, 1),
				rgba(181, 197, 223, 1),
				rgba(184, 198, 223, 1),
				rgba(167, 203, 223, 1),
				rgba(196, 221, 189, 1),
				rgba(218, 230, 172, 1),
				rgba(230, 234, 171, 1),
				rgba(255, 242, 167, 1));
		background-size: 50% 50%;
		mix-blend-mode: unset !important;
	}

	#page-title {
		--swatch-primary: rgb(var(--black-monochrome));
		color: rgb(var(--bright-accent));
		letter-spacing: 0.15em;
		text-align: center !important;
		text-shadow: .063rem .063rem .063rem rgb(var(--swatch-primary));
	}

	#page-content {
		font-size: calc(var(--base-font-size) * 1.1);
	}

	#page-content div.warning {
		--wght: 900;
		position: relative;
		height: 15rem;
		display: flex;
		flex-direction: column;
		justify-content: center;
		background-color: rgba(var(--dark-accent),0.75);
	}

	#page-content div.top,
	#page-content div.bottom {
		position: absolute;
		width: 100%;
	}

	#page-content div.top {
		z-index: 4;
	}

	#page-content div.bottom {
		z-index: 3;
		text-shadow: 0.0625rem 0.0625rem 0 rgb(var(--swatch-primary)),
			-0.0625rem 0.0625rem 0 var(--bright-accent),
			0.0625rem -0.0625rem 0 var(--bright-accent),
			-0.0625rem -0.0625rem 0 var(--bright-accent),
			0rem 0.0625rem 0 rgb(var(--swatch-primary)),
			0rem -0.0625rem 0 rgb(var(--swatch-primary)),
			-0.0625rem 0rem 0 rgb(var(--swatch-primary)),
			0.0625rem 0rem 0 rgb(var(--swatch-primary)),
			0.125rem 0.125rem 0 rgb(var(--swatch-primary)),
			-0.125rem 0.125rem 0 rgb(var(--swatch-primary)),
			0.125rem -0.125rem 0 rgb(var(--swatch-primary)),
			-0.125rem -0.125rem 0 rgb(var(--swatch-primary)),
			0rem 0.125rem 0 rgb(var(--swatch-primary)),
			0rem -0.125rem 0 rgb(var(--swatch-primary)),
			-0.125rem 0rem 0 rgb(var(--swatch-primary)),
			0.125rem 0rem 0 rgb(var(--swatch-primary)),
			0.0625rem 0.125rem 0 rgb(var(--swatch-primary)),
			-0.0625rem 0.125rem 0 rgb(var(--swatch-primary)),
			0.0625rem -0.125rem 0 rgb(var(--swatch-primary)),
			-0.0625rem -0.125rem 0 rgb(var(--swatch-primary)),
			0.125rem 0.0625rem 0 rgb(var(--swatch-primary)),
			-0.125rem 0.0625rem 0 rgb(var(--swatch-primary)),
			0.125rem -0.0625rem 0 rgb(var(--swatch-primary)),
			-0.125rem -0.0625rem 0 rgb(var(--swatch-primary));
	}

	#page-content div.top>span:nth-child(1),
	#page-content div.top>span:nth-child(3),
	#page-content div.bottom>span:nth-child(1),
	#page-content div.bottom>span:nth-child(3) {
		position: relative;
		z-index: 5;
		background:
			/* Bright Rainbow Gradient */
			linear-gradient(to top right,
				rgba(216, 151, 154, 1),
				rgba(255, 240, 148, 1),
				rgba(198, 223, 158, 1),
				rgba(145, 191, 222, 1),
				rgba(246, 186, 212, 1));
		background-size: 300% 300%;
		background-repeat: repeat;
		-webkit-background-clip: text;
		-webkit-text-fill-color: transparent;
	}

	#page-content div.top>span:nth-child(1),
	#page-content div.bottom>span:nth-child(1) {
		font-size: calc(var(--base-font-size) * 2.5);
	}

	#page-content div.top>span:nth-child(3),
	#page-content div.bottom>span:nth-child(3) {
		font-size: calc(var(--base-font-size) * 1.25);
		width: 75%;
		display: block;
		margin: 0 auto;
	}

	#page-content div.bobble-bg {
		position: absolute;
		background: url(http://kontainer.djkakt.us/local--files/theme:laughter-and-knives-theme/bobble.png);
		background-size: contain;
		background-repeat: no-repeat;
		background-position: center center;
		top: 0;
		width: 100%;
		height: 15rem;
		-webkit-animation: spin 10s linear infinite;
		animation: spin 10s linear infinite;
		-webkit-animation-play-state: paused;
		animation-play-state: paused;
	}

	#page-content div.warning:hover>div.bobble-bg {
		-webkit-animation-play-state: running;
		animation-play-state: running;
	}

	div.content-log {
		display: inline-block;
		border: dashed 0.0625rem rgb(var(--black-monochrome));
		border-radius: 1rem;
		background: linear-gradient(124deg,
				rgba(240, 194, 215, 1),
				rgba(215, 195, 218, 1),
				rgba(181, 197, 223, 1),
				rgba(184, 198, 223, 1),
				rgba(167, 203, 223, 1),
				rgba(196, 221, 189, 1),
				rgba(218, 230, 172, 1),
				rgba(230, 234, 171, 1),
				rgba(255, 242, 167, 1));
		background-size: 300% 300%;
		background-repeat: repeat;
		margin: auto;
		width: auto;
		padding: 1rem;
		background-blend-mode: multiply;
	}

	div.content-log.inset {
		background: rgba(255, 255, 255, 0.35);
	}

	div.content-log.bobble * {
		--mono-wght: 900;
		font-family: var(--mono-font), monospace;
		font-variation-settings:
			"wght"var(--mono-wght),
			"slnt"var(--slnt),
			"CASL"var(--CASL),
			"CRSV"var(--CRSV),
			"MONO"var(--MONO);
	}

	div.content-log.bobbleinset {
		--mono-wght: 900;
		background: rgba(255, 255, 255, 0.35);
		font-family: var(--mono-font), monospace;
	}

	div.content-log.bobbleinset,
	div.content-log.bobbleinset * {
		font-variation-settings:
			"wght"var(--mono-wght),
			"slnt"var(--slnt),
			"CASL"var(--CASL),
			"CRSV"var(--CRSV),
			"MONO"var(--MONO);
	}

	.yui-navset .yui-nav .selected a {
		color: rgb(var(--black-monochrome)) !important;
	}

	table.wiki-content-table th {
		background: var(--pastel-rainbow);
		color: rgb(var(--black-monochrome));
	}

	.yui-navset .yui-nav .selected,
	.yui-navset .yui-navset-top .yui-nav .selected,
	.yui-navset .yui-nav a:hover,
	.yui-navset .yui-nav a:focus,
	.yui-navset .yui-nav .selected a:hover {
		color: rgb(var(--black-monochrome));
		background-color: rgba(0, 0, 0, 0);
	}

	.yui-navset .yui-nav .selected {
		box-shadow: none;
	}

	.yui-navset .yui-nav li::after {
		content: "";
		position: absolute;
		top: 0;
		left: 0;
		width: 100%;
		height: 100%;
		z-index: 9;
		pointer-events: none;
	}

	.yui-navset .yui-nav .selected::after {
		transition: box-shadow 150ms cubic-bezier(0.4, 0, 0.2, 1);
		box-shadow:
			inset 0.25rem 0 0 0 rgb(var(--swatch-primary-darker)),
			inset -0.25rem 0 0 0 rgb(var(--swatch-primary-darkest)),
			inset 0 0.25rem 0 0 rgb(var(--gray-monochrome)),
			0 0.25rem 0 0 rgb(var(--swatch-primary));
	}

	.yui-navset .yui-nav a,
	.yui-navset .yui-nav .selected a {
		position: relative;
		overflow: hidden;
	}

	.yui-navset .yui-nav a::before {
		opacity: 0;
		transition: opacity 250ms cubic-bezier(0.4, 0, 0.2, 1)
	}

	.yui-navset .yui-nav .selected a::before {
		background-image: var(--pastel-rainbow);
		opacity: 1;
	}

	@media only screen and (max-width: 56.25rem) {
		#header {
			background-position: left calc(4vw + 5.5rem - 10.4rem) top calc(4% - var(--offset-from-page-top) / 2), top center;
			background-size: calc(8.75rem + var(--offset-from-page-top)), 100% 7.5rem;
			background-repeat: no-repeat, repeat;
		}

		#header h2 {
			font-size: calc(var(--base-font-size) * 0.7);
		}
	}

	@-webkit-keyframes rainbow {
		0% {
			background-position: 0% 82%
		}

		50% {
			background-position: 100% 19%
		}

		100% {
			background-position: 0% 82%
		}
	}

	@keyframes rainbow {
		0% {
			background-position: 0% 82%
		}

		50% {
			background-position: 100% 19%
		}

		100% {
			background-position: 0% 82%
		}
	}

	@-webkit-keyframes spin {
		100% {
			transform: rotate(1turn);
		}
	}

	@keyframes spin {
		100% {
			transform: rotate(1turn);
		}
	}
}

